import { Component, OnInit } from '@angular/core';
import { HttpResponse } from '@angular/common/http';
import { UntypedFormBuilder } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { Observable } from 'rxjs';
import { finalize, map } from 'rxjs/operators';

import { IDeliveryType, DeliveryType } from '../delivery-type.model';
import { DeliveryTypeService } from '../service/delivery-type.service';
import { IProduct } from 'app/entities/product/product.model';
import { ProductService } from 'app/entities/product/service/product.service';
import { DeliveryTypes } from 'app/entities/enumerations/delivery-types.model';

@Component({
  selector: 'jhi-delivery-type-update',
  templateUrl: './delivery-type-update.component.html',
})
export class DeliveryTypeUpdateComponent implements OnInit {
  isSaving = false;
  deliveryTypesValues = Object.keys(DeliveryTypes);

  productsSharedCollection: IProduct[] = [];

  editForm = this.fb.group({
    id: [],
    deliveryType: [],
    minimumOrderQuantity: [],
    price: [],
    pricePerKilometre: [],
    product: [],
  });

  constructor(
    protected deliveryTypeService: DeliveryTypeService,
    protected productService: ProductService,
    protected activatedRoute: ActivatedRoute,
    protected fb: UntypedFormBuilder
  ) {}

  ngOnInit(): void {
    this.activatedRoute.data.subscribe(({ deliveryType }) => {
      this.updateForm(deliveryType);

      this.loadRelationshipsOptions();
    });
  }

  previousState(): void {
    window.history.back();
  }

  save(): void {
    this.isSaving = true;
    const deliveryType = this.createFromForm();
    if (deliveryType.id !== undefined) {
      this.subscribeToSaveResponse(this.deliveryTypeService.update(deliveryType));
    } else {
      this.subscribeToSaveResponse(this.deliveryTypeService.create(deliveryType));
    }
  }

  trackProductById(index: number, item: IProduct): number {
    return item.id!;
  }

  protected subscribeToSaveResponse(result: Observable<HttpResponse<IDeliveryType>>): void {
    result.pipe(finalize(() => this.onSaveFinalize())).subscribe(
      () => this.onSaveSuccess(),
      () => this.onSaveError()
    );
  }

  protected onSaveSuccess(): void {
    this.previousState();
  }

  protected onSaveError(): void {
    // Api for inheritance.
  }

  protected onSaveFinalize(): void {
    this.isSaving = false;
  }

  protected updateForm(deliveryType: IDeliveryType): void {
    this.editForm.patchValue({
      id: deliveryType.id,
      deliveryType: deliveryType.deliveryType,
      minimumOrderQuantity: deliveryType.minimumOrderQuantity,
      price: deliveryType.price,
      pricePerKilometre: deliveryType.pricePerKilometre,
      product: deliveryType.product,
    });

    this.productsSharedCollection = this.productService.addProductToCollectionIfMissing(
      this.productsSharedCollection,
      deliveryType.product
    );
  }

  protected loadRelationshipsOptions(): void {
    this.productService
      .query()
      .pipe(map((res: HttpResponse<IProduct[]>) => res.body ?? []))
      .pipe(
        map((products: IProduct[]) => this.productService.addProductToCollectionIfMissing(products, this.editForm.get('product')!.value))
      )
      .subscribe((products: IProduct[]) => (this.productsSharedCollection = products));
  }

  protected createFromForm(): IDeliveryType {
    return {
      ...new DeliveryType(),
      id: this.editForm.get(['id'])!.value,
      deliveryType: this.editForm.get(['deliveryType'])!.value,
      minimumOrderQuantity: this.editForm.get(['minimumOrderQuantity'])!.value,
      price: this.editForm.get(['price'])!.value,
      pricePerKilometre: this.editForm.get(['pricePerKilometre'])!.value,
      product: this.editForm.get(['product'])!.value,
    };
  }
}
<div class="row justify-content-center">
  <div class="col-8">
    <form name="editForm" role="form" novalidate (ngSubmit)="save()" [formGroup]="editForm">
      <h2
        id="jhi-delivery-type-heading"
        data-cy="DeliveryTypeCreateUpdateHeading"
        jhiTranslate="createyoureventApp.deliveryType.home.createOrEditLabel"
      >
        Create or edit a Delivery Type
      </h2>

      <div>
        <jhi-alert-error></jhi-alert-error>

        <div class="form-group" [hidden]="editForm.get('id')!.value == null">
          <label class="form-control-label" jhiTranslate="global.field.id" for="field_id">ID</label>
          <input type="number" class="form-control" name="id" id="field_id" data-cy="id" formControlName="id" [readonly]="true" />
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.deliveryType.deliveryType" for="field_deliveryType"
            >Delivery Type</label
          >
          <select class="form-control" name="deliveryType" formControlName="deliveryType" id="field_deliveryType" data-cy="deliveryType">
            <option [ngValue]="null">{{ 'createyoureventApp.DeliveryTypes.null' | translate }}</option>
            <option *ngFor="let deliveryTypes of deliveryTypesValues" [value]="deliveryTypes">
              {{ 'createyoureventApp.DeliveryTypes.' + deliveryTypes | translate }}
            </option>
          </select>
        </div>

        <div class="form-group">
          <label
            class="form-control-label"
            jhiTranslate="createyoureventApp.deliveryType.minimumOrderQuantity"
            for="field_minimumOrderQuantity"
            >Minimum Order Quantity</label
          >
          <input
            type="number"
            class="form-control"
            name="minimumOrderQuantity"
            id="field_minimumOrderQuantity"
            data-cy="minimumOrderQuantity"
            formControlName="minimumOrderQuantity"
          />
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.deliveryType.price" for="field_price">Price</label>
          <input type="number" class="form-control" name="price" id="field_price" data-cy="price" formControlName="price" />
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.deliveryType.pricePerKilometre" for="field_pricePerKilometre"
            >Price Per Kilometre</label
          >
          <input
            type="number"
            class="form-control"
            name="pricePerKilometre"
            id="field_pricePerKilometre"
            data-cy="pricePerKilometre"
            formControlName="pricePerKilometre"
          />
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.deliveryType.product" for="field_product">Product</label>
          <select class="form-control" id="field_product" data-cy="product" name="product" formControlName="product">
            <option [ngValue]="null"></option>
            <option
              [ngValue]="productOption.id === editForm.get('product')!.value?.id ? editForm.get('product')!.value : productOption"
              *ngFor="let productOption of productsSharedCollection; trackBy: trackProductById"
            >
              {{ productOption.id }}
            </option>
          </select>
        </div>
      </div>

      <div>
        <button type="button" id="cancel-save" data-cy="entityCreateCancelButton" class="btn btn-secondary" (click)="previousState()">
          <fa-icon icon="ban"></fa-icon>&nbsp;<span jhiTranslate="entity.action.cancel">Cancel</span>
        </button>

        <button
          type="submit"
          id="save-entity"
          data-cy="entityCreateSaveButton"
          [disabled]="editForm.invalid || isSaving"
          class="btn btn-primary"
        >
          <fa-icon icon="save"></fa-icon>&nbsp;<span jhiTranslate="entity.action.save">Save</span>
        </button>
      </div>
    </form>
  </div>
</div>
