          </select>
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.organizationComment.user" for="field_user">User</label>
          <select class="form-control" id="field_user" data-cy="user" name="user" formControlName="user">
            <option [ngValue]="null"></option>
            <option
              [ngValue]="userOption.id === editForm.get('user')!.value?.id ? editForm.get('user')!.value : userOption"
              *ngFor="let userOption of usersSharedCollection; trackBy: trackUserById"
            >
              {{ userOption.id }}
            </option>
          </select>
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.organizationComment.event" for="field_event">Event</label>
          <select class="form-control" id="field_event" data-cy="event" name="event" formControlName="event">
            <option [ngValue]="null"></option>
            <option
              [ngValue]="organizationOption.id === editForm.get('event')!.value?.id ? editForm.get('event')!.value : organizationOption"
              *ngFor="let organizationOption of organizationsSharedCollection; trackBy: trackOrganizationById"
            >
              {{ organizationOption.id }}
            </option>
          </select>
        </div>

        <div class="form-group">
          <label
            class="form-control-label"
            jhiTranslate="createyoureventApp.organizationComment.organizationComment"
            for="field_organizationComment"
            >Organization Comment</label
          >
          <select
            class="form-control"
            id="field_organizationComment"
            data-cy="organizationComment"
            name="organizationComment"
            formControlName="organizationComment"
          >
            <option [ngValue]="null"></option>
            <option
              [ngValue]="
                organizationCommentOption.id === editForm.get('organizationComment')!.value?.id
                  ? editForm.get('organizationComment')!.value
                  : organizationCommentOption
              "
              *ngFor="let organizationCommentOption of organizationCommentsSharedCollection; trackBy: trackOrganizationCommentById"
            >
              {{ organizationCommentOption.id }}
            </option>
          </select>
        </div>
      </div>

      <div>
        <button type="button" id="cancel-save" data-cy="entityCreateCancelButton" class="btn btn-secondary" (click)="previousState()">
          <fa-icon icon="ban"></fa-icon>&nbsp;<span jhiTranslate="entity.action.cancel">Cancel</span>
        </button>

        <button
          type="submit"
          id="save-entity"
          data-cy="entityCreateSaveButton"
          [disabled]="editForm.invalid || isSaving"
          class="btn btn-primary"
        >
          <fa-icon icon="save"></fa-icon>&nbsp;<span jhiTranslate="entity.action.save">Save</span>
        </button>
      </div>
