import { Component, OnInit, ChangeDetectionStrategy, ViewEncapsulation, OnDestroy } from '@angular/core';
import { CalendarEvent, CalendarView } from 'angular-calendar';
import { CalendarService } from './calendar.service';
import { colors } from './colors';
import { isSameDay, isSameMonth } from 'date-fns';
import { Subject, Subscription } from 'rxjs';
import { Router } from '@angular/router';
import { JhiLanguageService } from 'ng-jhipster';
import { SharedLanguageChangeService } from '../layouts/navbar/SharedLanguageChangeService.service';
import { IEvent } from 'app/entities/event/event.model';

interface ExtendetCalendarEvent extends CalendarEvent {
  id: number;
}

@Component({
  selector: 'jhi-calendar',
  changeDetection: ChangeDetectionStrategy.OnPush,
  encapsulation: ViewEncapsulation.None,
  templateUrl: './calendar.component.html',
  styleUrls: ['./calendar.component.scss']
})
export class CalendarComponent implements OnInit, OnDestroy {
  view: CalendarView = CalendarView.Month;

  CalendarView = CalendarView;

  viewDate: Date = new Date();

  calendarEvents: ExtendetCalendarEvent[] = [];
  events!: IEvent[];

  activeDayIsOpen = false;

  locale = 'de';

  refresh: Subject<any> = new Subject();

  changeEventsubscription: Subscription;

  constructor(
    protected calendarService: CalendarService,
    private router: Router,
    private languageService: JhiLanguageService,
    private sharedLanguageChangeService: SharedLanguageChangeService
  ) {
    this.changeEventsubscription = this.sharedLanguageChangeService.getChangeEvent().subscribe(() => {
      this.changeLanguage();
    });
  }
  ngOnDestroy(): void {
    this.changeEventsubscription.unsubscribe();
  }

  changeLanguage(): void {
    this.locale = this.languageService.getCurrentLanguage();
    this.refresh.next();
  }

  ngOnInit(): void {
    this.locale = this.languageService.getCurrentLanguage();
    this.calendarService.queryIsPublicAndActive().subscribe(events => {
      this.events = events.body!;
      this.events.forEach(event => {
        const start = new Date(event.dateStart!.toString());
        const end = new Date(event.dateEnd!.toString());
        this.calendarEvents.push({ id: event.id!, title: event.name!, start: start, end: end, color: colors.red });
      });
      this.refresh.next();
    });
  }

  dayClicked({ date, events }: { date: Date; events: CalendarEvent[] }): void {
    if (isSameMonth(date, this.viewDate)) {
      if ((isSameDay(this.viewDate, date) && this.activeDayIsOpen === true) || events.length === 0) {
        this.activeDayIsOpen = false;
      } else {
        this.activeDayIsOpen = true;
      }
      this.viewDate = date;
    }
  }

  setView(view: CalendarView): void {
    this.view = view;
  }

  closeOpenMonthViewDay(): void {
    this.activeDayIsOpen = false;
  }

  eventClicked({ event }: { event: CalendarEvent }): void {
    const id = event.id;
    this.router.navigate(['/events', id, 'view']);
  }
}
<div class="row text-center">
    <div class="col-md-4">
      <div class="btn-group">
        <div
          class="btn btn-primary"
          mwlCalendarPreviousView
          [view]="view"
          [(viewDate)]="viewDate"
          (viewDateChange)="closeOpenMonthViewDay()"
        >
          <span jhiTranslate="calendar.previous">Previous</span>
        </div>
        <div
          class="btn btn-outline-secondary"
          mwlCalendarToday
          [(viewDate)]="viewDate"
        >
        <span jhiTranslate="calendar.today">Today</span>
        </div>
        <div
          class="btn btn-primary"
          mwlCalendarNextView
          [view]="view"
          [(viewDate)]="viewDate"
          (viewDateChange)="closeOpenMonthViewDay()"
        >
        <span jhiTranslate="calendar.next">Next</span>
        </div>
      </div>
    </div>
    <div class="col-md-4">
      <ng-container *ngIf="locale === 'en'">
        <h3>{{ viewDate | calendarDate:(view + 'ViewTitle'):'en' }}</h3>
      </ng-container>
      <ng-container *ngIf="locale === 'de'">
        <h3>{{ viewDate | calendarDate:(view + 'ViewTitle'):'de' }}</h3>
      </ng-container>
    </div>
    <div class="col-md-4">
      <div class="btn-group">
        <div
          class="btn btn-primary"
          (click)="setView(CalendarView.Month)"
          [class.active]="view === CalendarView.Month"
        >
          <span jhiTranslate="calendar.month">Month</span>
        </div>
        <div
          class="btn btn-primary"
          (click)="setView(CalendarView.Week)"
          [class.active]="view === CalendarView.Week"
        >
        <span jhiTranslate="calendar.week">Week</span>
        </div>
        <div
          class="btn btn-primary"
          (click)="setView(CalendarView.Day)"
          [class.active]="view === CalendarView.Day"
        >
        <span jhiTranslate="calendar.day">Day</span>
        </div>
      </div>
    </div>
  </div>
  <br />
  <div [ngSwitch]="view">
    <mwl-calendar-month-view
      *ngSwitchCase="CalendarView.Month"
      [viewDate]="viewDate"
      [events]="calendarEvents"
      [refresh]="refresh"
      [activeDayIsOpen]="activeDayIsOpen"
      (dayClicked)="dayClicked($event.day)"
      (eventClicked)="eventClicked($event)"
      [locale]="locale"
    >
    </mwl-calendar-month-view>
    <mwl-calendar-week-view
      *ngSwitchCase="CalendarView.Week"
      [viewDate]="viewDate"
      [events]="calendarEvents"
      [refresh]="refresh"
      (eventClicked)="eventClicked($event)"
      [locale]="locale"
    >
    </mwl-calendar-week-view>
    <mwl-calendar-day-view
      *ngSwitchCase="CalendarView.Day"
      [viewDate]="viewDate"
      [events]="calendarEvents"
      [refresh]="refresh"
      (eventClicked)="eventClicked($event)"
      [locale]="locale"
    >
    </mwl-calendar-day-view>
  </div>
