import { Component, OnInit } from '@angular/core';
import { UntypedFormControl, Validators } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { SharedChatService } from 'app/chat.service';
import {
  IEventProductRatingComment,
  EventProductRatingComment,
} from 'app/entities/event-product-rating-comment/event-product-rating-comment.model';
import { EventProductRatingCommentService } from 'app/entities/event-product-rating-comment/service/event-product-rating-comment.service';
import { EventProductRating, IEventProductRating } from 'app/entities/event-product-rating/event-product-rating.model';
import { EventProductRatingService } from 'app/entities/event-product-rating/service/event-product-rating.service';
import { IEvent } from 'app/entities/event/event.model';
import { EventService } from 'app/entities/event/service/event.service';
import { IProduct } from 'app/entities/product/product.model';
import { ProductService } from 'app/entities/product/service/product.service';
import { IUser } from 'app/entities/user/user.model';
import { GeneralService } from 'app/general.service';
import { Subject } from 'rxjs';
import { EventService as EventUserService } from '../event.service';
import * as dayjs from 'dayjs';

@Component({
  selector: 'jhi-event-product-rating',
  templateUrl: './evaluate-event-product.component.html',
  styleUrls: ['./evaluate-event-product.component.scss'],
})
export class EventToProductRatingComponent implements OnInit {
  event!: IEvent;
  product!: IProduct;
  user!: IUser;
  userLoggedIn!: IUser;

  userRated = false;

  likes = 0;
  dislikes = 0;

  commentText = new UntypedFormControl(null, Validators.required);
  rating!: EventProductRating;
  comments: IEventProductRatingComment[] = [];
  isSaving = false;

  refresh: Subject<any> = new Subject();

  constructor(
    private route: ActivatedRoute,
    private eventUserService: EventUserService,
    private eventService: EventService,
    private productService: ProductService,
    private eventProductRatingService: EventProductRatingService,
    private eventProductRatingCommentService: EventProductRatingCommentService,
    private generalService: GeneralService,
    private sharedChatService: SharedChatService
  ) {}

  ngOnInit(): void {
    let eventId: number, productId: number;
    this.route.params.subscribe(params => {
      eventId = params['eventId'];
      productId = params['productId'];

      this.eventService.find(eventId).subscribe(event => {
        this.event = event.body!;
        this.productService.find(productId).subscribe(product => {
          this.product = product.body!;
          this.generalService.findWidthAuthorities().subscribe(user => {
            this.user = user.body!;
            this.userLoggedIn = user.body!;

            this.calculateLikes();
            this.calculateDislikes();

            this.eventUserService
              .findEventProductRatingByEventAndProductAndUser(this.event.id!, this.product.id!, this.user.id)
              .subscribe(res => {
                this.rating = res.body!;
              });
            this.eventUserService.findEventProductRatingCommentByEventAndProduct(this.event.id!, this.product.id!).subscribe(res => {
              this.comments = res.body!;
            });
          });
        });
      });
    });
  }

  previousState(): void {
    window.history.back();
  }

  saveComment(): void {
    const eventProductRatingComment: IEventProductRatingComment = new EventProductRatingComment();
    eventProductRatingComment.comment = this.commentText.value;
    eventProductRatingComment.event = this.event;
    eventProductRatingComment.product = this.product;
    eventProductRatingComment.user = this.user;
    eventProductRatingComment.date = dayjs();
    this.eventProductRatingCommentService.create(eventProductRatingComment).subscribe(() => {
      this.comments.push(eventProductRatingComment);
      this.commentText.reset();
      this.refresh.next();
    });
  }

  clickUserName(user: IUser): void {
    this.sharedChatService.callClickName(user);
  }

  like(): void {
    const rating = new EventProductRating();
    rating.like = 1;
    rating.dislike = 0;
    rating.event = this.event;
    rating.product = this.product;
    rating.user = this.user;
    this.eventProductRatingService.create(rating).subscribe(() => {
      this.likes++;
      this.rating = rating;
      this.refresh.next();
    });
  }

  dislike(): void {
    const rating = new EventProductRating();
    rating.like = 0;
    rating.dislike = 1;
    rating.event = this.event;
    rating.product = this.product;
    rating.user = this.user;
    this.eventProductRatingService.create(rating).subscribe(() => {
      this.dislikes++;
      this.rating = rating;
      this.refresh.next();
    });
  }

  calculateLikes(): void {
    let l: IEventProductRating[];
    this.generalService.findEventProductRatingsByEventIdAndProductId(this.event.id!, this.product.id!).subscribe(likes => {
      l = likes.body!;
      l.forEach(element => {
        this.likes += element.like!;
        this.refresh.next();
      });
    });
  }

  calculateDislikes(): void {
    let l: IEventProductRating[];
    this.generalService.findEventProductRatingsByEventIdAndProductId(this.event.id!, this.product.id!).subscribe(dislikes => {
      l = dislikes.body!;
      l.forEach(element => {
        this.dislikes += element.dislike!;
        this.refresh.next();
      });
    });
  }
}
<div>
    <h2 id="page-heading">
        <span jhiTranslate="evaluate-event-product.title">Evaluate product from event</span>
    </h2>

    <jhi-alert-error></jhi-alert-error>

    <jhi-alert></jhi-alert>

    <div class="container-fluid">
        <div class="row">
            <div class="col-md-12">
                <h5><span jhiTranslate="evaluate-event-product.eval-prod">Evaluate product:</span>{{ product.title }}<span jhiTranslate="evaluate-event-product.for-event">for event:</span>{{ event.name }}</h5>
            </div>
        </div>
        <div class="row">
            <div class="col-md-6 cards">
                <mat-card class="product-card">
                    <mat-card-header>
                    <mat-card-title>{{product.title}}</mat-card-title>
                    </mat-card-header>
                    <img mat-card-image [src]="'data:' + product.photoContentType + ';base64,' + product.photo"  alt="Product photo">
                    <mat-card-content>
                    <p>
                        {{product.price | currency}}

                    </p>
                    <p>
                        <ng-container *ngIf="product.priceType === 'SELL'">
                            <div class="marker green">
                                <p>{{ product.priceType }}</p>
                            </div>
                        </ng-container>
                        <ng-container *ngIf="product.priceType === 'RENT'">
                            <div class="marker blue">
                                <p>{{ product.priceType }}:{{ product.rentType }}</p>
                            </div>
                        </ng-container>
                    </p>
                    </mat-card-content>
                    <mat-card-actions>
                    </mat-card-actions>
                </mat-card>
            </div>
            <div class="col-md-6">
                <mat-card class="product-card">
                    <mat-card-header>
                    <mat-card-title>{{event.name}}</mat-card-title>
                    </mat-card-header>
                    <ng-container *ngIf="event.flyer !== null">
                        <img mat-card-image [src]="'data:' + event.flyerContentType + ';base64,' + event.flyer"  alt="Event photo">
                    </ng-container>
                    <mat-card-content>
                    <ng-container *ngIf="event.flyer === null">
                            <p>
                                {{ event.description }}
                            </p>
                    </ng-container>
                      <dl class="row-md jh-entity-details">
                        <dt><span jhiTranslate="event-detail.placenumber">Placenumber</span></dt>
                        <dd>
                            <span>{{ event.placenumber }}</span>
                        </dd>
                        <dt><span jhiTranslate="event-detail.price">Price</span></dt>
                        <dd>
                            <span>{{ event.price | currency }}</span>
                        </dd>
                      </dl>
                    </mat-card-content>
                    <mat-card-actions>
                    </mat-card-actions>
                </mat-card>
            </div>
        </div>
        <div class="row">
            <div class="col-md-12">
                <hr/>
                <div>
                    <button mat-icon-button (click)="like()" [disabled]="(rating != null)">
                        <mat-icon aria-label="Like">thumb_up</mat-icon>
                     </button>
                     {{ likes }}
                     <button mat-icon-button class="primary" (click)="dislike()" [disabled]="(rating != null)">
                        <mat-icon aria-label="Dislike">thumb_down</mat-icon>
                     </button>
                     {{ dislikes }}
                </div>
                <hr/>

                <h3><span jhiTranslate="evaluate-event-product.comment">Comments and feedbacks</span></h3>
                    <textarea class="form-control" rows="3" [formControl]="commentText"></textarea>
                    <hr/>
                    <button  class="btn btn-info" (click)="saveComment()" [disabled]="(!commentText.valid)">Save comment</button>
                    <button type="button" id="cancel-save" class="btn btn-secondary" (click)="previousState()">
                        <fa-icon icon="ban"></fa-icon>&nbsp;<span jhiTranslate="entity.action.cancel">Cancel</span>
                    </button>
                    <hr/>
                    <ng-container *ngFor="let comment of comments">
                        <div class="row">
                        <div class="panel panel-default">
                            <div class="panel-heading">
                                <ng-container *ngIf="comment.user.id !== userLoggedIn.id">
                                    <a (click)="clickUserName(comment.user)"><span class="bold">{{ comment.user.firstName }} {{ comment.user.lastName }}</span></a> <span class="text-muted">{{ comment.date | date:'mediumDate' }}</span>
                                </ng-container>
                                <ng-container *ngIf="comment.user.id === userLoggedIn.id">
                                    <span class="bold">{{ comment.user.firstName }} {{ comment.user.lastName }}</span> <span class="text-muted">{{ comment.date | date:'mediumDate' }}</span>
                                </ng-container>
                            </div>
                            <div class="panel-body">
                                {{ comment.comment }}
                            </div>
                        </div>
                        </div>
                    </ng-container>
            </div>
            </div>
        </div>
    </div>
