        </div>
    </div>
</div>


<ng-template #eventMovie>
    <p-carousel [value]="allEvents"  [orientation]="orientation" [autoplayInterval]="autoplayInterval" [verticalViewPortHeight]="verticalViewPortHeight" [circular]="circular" [numVisible]="numVisible" [numScroll]="numScroll">
        <p-header>
        </p-header>
        <ng-template let-event pTemplate="item">
            <a (click)="goToEvent(event.id)" jhiText="tv.gotoEvent">
            <div class="container_box_slider">
                <div class="box_{{ 4 | randomNumber }}">
                    <span class="black"></span>
                    <div class="row">
                        <div class="col-md-4">
                            <div class="card content">
                                <img class="card-img-top card-img-event-slider" [src]="'data:' + event.flyerContentType + ';base64,' + event.flyer" alt="{{ event.name }}">
                                <div class="card-body">
                                  <h6 class="card-title bold"><span class="font-title-event">{{ event.name }}</span></h6>
                                  <h6 class="card-subtitle">{{event.motto}}</h6>
                                  <div class="card-text">
                                    <ng-template #t let-fill="fill">
                                      <span class="heart" [class.full]="fill === 100">
                                        <span class="half" [style.width.%]="fill">&hearts;</span>&hearts;
                                      </span>
                                    </ng-template>
                                      <ngb-rating [rate]="getAverage(event.id)" [starTemplate]="t" [readonly]="true" [max]="5"></ngb-rating>
                                      <br/>
                                      <span jhiTranslate="event-detail.total-ratings">Total ratings:</span> {{ getRatingsTotal(event.id) }}
