                                      <dd>
                                          <span>{{ event.reservations.length }}/{{ event.placenumber }}</span>
                                      </dd>
                                      <dt><span jhiTranslate="event-detail.price">Price</span></dt>
                                      <dd>
                                          <span>{{ event.price | currency }}</span>
                                      </dd>
                                  </div>
                                </div>
                              </div>
                        </div>
                        <div class="col-md-5">
                            <div class="card content">
                                <div class="card-body">
                                  <div class="card-text">
                                        <ul>
                                            <ng-container *ngFor="let t of event.tags">
                                                <li>{{t.tag}}</li>
                                            </ng-container>
                                        </ul>
                                    </div>
                                </div>
                            </div>
                          </div>
                        </div>
                    </div>
                  </div>
            </a>
