import { HttpResponse } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { Validators, UntypedFormBuilder } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { IRideCosts, RideCosts } from 'app/entities/ride-costs/ride-costs.model';
import { RideCostsService } from 'app/entities/ride-costs/service/ride-costs.service';
import { ServiceMap } from 'app/entities/service-map/service-map.model';
import { ServiceMapService } from 'app/entities/service-map/service/service-map.service';
import { IUser } from 'app/entities/user/user.model';
import { GeneralService } from 'app/general.service';
import { Observable } from 'rxjs';

@Component({
  selector: 'jhi-add-ride-costs',
  templateUrl: './add-ride-costs.component.html',
})
export class AddRideCostsComponent implements OnInit {
  isSaving = false;
  serviceMapId!: number;
  user!: IUser;
  serviceMap!: ServiceMap;
  serviceId!: number;

  editForm = this.fb.group({
    id: [],
    pricePerKilometre: [null, [Validators.required]],
  });

  constructor(
    protected rideCostsService: RideCostsService,
    protected activatedRoute: ActivatedRoute,
    private fb: UntypedFormBuilder,
    private route: ActivatedRoute,
    private generalService: GeneralService,
    private serviceMapService: ServiceMapService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.serviceMapId = Number(this.route.snapshot.paramMap.get('serviceMapId'));
    this.serviceId = Number(this.route.snapshot.paramMap.get('serviceId'));
    this.generalService.findWidthAuthorities().subscribe(u => {
      this.user = u.body!;

      this.serviceMapService.find(this.serviceMapId).subscribe(res => {
        this.serviceMap = res.body!;
        this.updateForm(this.serviceMap.rideCost!);
      });
    });
  }

  updateForm(rideCosts: IRideCosts): void {
    this.editForm.patchValue({
      id: rideCosts.id,
      pricePerKilometre: rideCosts.pricePerKilometre,
    });
  }

  previousState(): void {
    window.history.back();
  }

  save(): void {
    this.isSaving = true;
    const rideCosts = this.createFromForm();
    if (rideCosts.id !== null) {
      this.subscribeToSaveResponse(this.rideCostsService.update(rideCosts));
    } else {
      this.rideCostsService.create(rideCosts).subscribe(res => {
        this.serviceMap.rideCost = res.body;
        this.serviceMapService.update(this.serviceMap).subscribe(() => {
          this.router.navigate(['/service/service-maps/service-offers/' + this.serviceId + '/' + this.serviceMapId]);
          this.isSaving = false;
        });
      });
    }
  }

  private createFromForm(): IRideCosts {
    return {
      ...new RideCosts(),
      id: this.editForm.get(['id'])!.value,
      pricePerKilometre: this.editForm.get(['pricePerKilometre'])!.value,
    };
  }

  protected subscribeToSaveResponse(result: Observable<HttpResponse<IRideCosts>>): void {
    result.subscribe(() => {
      this.router.navigate(['/service/service-maps/service-offers/' + this.serviceId + '/' + this.serviceMapId]);
    });
  }

  protected onSaveError(): void {
    this.isSaving = false;
  }
}
<div class="row justify-content-center">
    <div class="col-8">
        <form name="editForm" role="form" (ngSubmit)="save()" novalidate (ngSubmit)="save()" [formGroup]="editForm">
            <h2 id="jhi-ride-costs-heading" jhiTranslate="add-ride-costs.createLabel">Create or edit a Ride Costs</h2>

            <div>
                <jhi-alert-error></jhi-alert-error>

                <div jhiTranslate="add-ride-costs.createRideCost.info">
                    Here you can enter a price, which you calculate as the kilometer price for this service map if you use a car for these tasks.
                    The costs which are then calculated are from the address that you have entered in your service location up to the event for which
                    the customer then books you.
                </div>

                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="add-ride-costs.pricePerKilometre" for="field_pricePerKilometre">Price Per Kilometre</label>
                    <input type="number" class="form-control" name="pricePerKilometre" id="field_pricePerKilometre"
                           formControlName="pricePerKilometre"/>
                    <div *ngIf="editForm.get('pricePerKilometre')!.invalid && (editForm.get('pricePerKilometre')!.dirty || editForm.get('pricePerKilometre')!.touched)">
                        <small class="form-text text-danger"
                               *ngIf="editForm.get('pricePerKilometre')?.errors?.required" jhiTranslate="entity.validation.required">
                        This field is required.
                        </small>
                        <small class="form-text text-danger"
                            [hidden]="!editForm.get('pricePerKilometre')?.errors?.number" jhiTranslate="entity.validation.number">
                            This field should be a number.
                        </small>
                    </div>
                </div>
            </div>

            <div>
                <button type="button" id="cancel-save" class="btn btn-secondary" (click)="previousState()">
                    <fa-icon icon="ban"></fa-icon>&nbsp;<span jhiTranslate="entity.action.cancel">Cancel</span>
                </button>

                <button type="submit" id="save-entity" [disabled]="editForm.invalid || isSaving" class="btn btn-primary">
                    <fa-icon icon="save"></fa-icon>&nbsp;<span jhiTranslate="entity.action.save">Save</span>
                </button>
            </div>
        </form>
    </div>
</div>
