import { Component, OnInit } from "@angular/core";
import { ActivatedRoute } from "@angular/router";
import { SharedChatService } from "app/chat.service";
import { EventLikeDislikeService } from "app/entities/event-like-dislike/service/event-like-dislike.service";
import { IEvent } from "app/entities/event/event.model";
import { EventService } from "app/entities/event/service/event.service";
import { IUser } from "app/entities/user/user.model";
import { GeneralService } from "app/general.service";


@Component({
  selector: 'jhi-ratings',
  templateUrl: './ratings.component.html',
  styleUrls: ['./ratings.component.scss']
})
export class RatingsComponent implements OnInit {

  eventId!: number;
  jhiEvent!: IEvent;
  user!: IUser;
  likes: any[] = [];
  dislikes: any[] = [];

  constructor(
    private eventService: EventService,
    private route: ActivatedRoute,
    private generalService: GeneralService,
    private eventLikeDislikeService: EventLikeDislikeService
  ) {}


  ngOnInit(): void {
    this.route.parent!.params.subscribe(params => {
      this.eventId = params['eventId'];

      this.eventService.find(this.eventId).subscribe(ev => {
        this.jhiEvent = ev.body!;

        this.generalService.findWidthAuthorities().subscribe(user => {
          this.user = user.body!;

          this.generalService.findEventLikeDislikeByEventId(this.jhiEvent.id!).subscribe(res => {
            res.body!.forEach(element => {
              if (element.like === 1) {
                this.likes.push(element);
              }
              if (element.dislike === 1) {
                this.dislikes.push(element);
              }
            });
          });
        });
      });
    });
  }

  previousState(): void {
    window.history.back();
  }

  onDeleteDislikes(id: number): void {
    this.eventLikeDislikeService.delete(id).subscribe();
  }

  onDeleteLikes(id: number): void {
    this.eventLikeDislikeService.delete(id).subscribe();
  }
}
<div class="alert alert-info" role="alert">
    <h2 jhiTranslate="dashboard-event.ratings-title">All user rating's</h2>
    <p jhiTranslate="dashboard-event.ratings-info">
        Below you can see all likes / dislikes that have been given for this product of each individual user. You will also find the email address of the user who has liked there. You can also contact them if you have any questions about the decision.
    </p>
</div>
<hr/>
<jhi-admin-like-dislike [likes]="likes" [dislikes]="dislikes" (deletedLikes)="onDeleteLikes($event)" (deletedDislikes)="onDeleteDislikes($event)"></jhi-admin-like-dislike>

<div class="row">
    <div class="col-md-12">
        <hr/>
        <div class="btn-cancel">
            <button type="button" id="cancel-save" class="btn btn-secondary btn-cancel" (click)="previousState()">
              <fa-icon icon="ban"></fa-icon>&nbsp;<span jhiTranslate="entity.action.cancel">Cancel</span>
          </button>
        </div>
    </div>
</div>
