import { Component, OnInit } from "@angular/core";
import { Router } from "@angular/router";
import { TranslateService } from "@ngx-translate/core";
import { GiftShoppingCart } from "app/entities/gift-shopping-cart/gift-shopping-cart.model";
import { GiftShoppingCartService } from "app/entities/gift-shopping-cart/service/gift-shopping-cart.service";
import { IGift } from "app/entities/gift/gift.model";
import { GiftService } from "app/entities/gift/service/gift.service";
import { IUser } from "app/entities/user/user.model";
import { GeneralService } from "app/general.service";
import * as dayjs from "dayjs";
import { MessageService } from "primeng/api";
import { PointsDataService } from "../points-display/points-display.service";


@Component({
  selector: 'jhi-exchange',
  templateUrl: './exchange.component.html',
  styleUrls: ['./exchange.component.scss'],
  providers: [MessageService]
})
export class ExchangeComponent implements OnInit {
  gifts!: IGift[];
  user!: IUser;

  constructor(
    private generalService: GeneralService,
    private giftShoppingCartService: GiftShoppingCartService,
    private messageService: MessageService,
    private translate: TranslateService,
    private giftService: GiftService,
    private pointsData: PointsDataService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.generalService.findWidthAuthorities().subscribe(u => {
      this.user = u.body!;
    });

    this.generalService.findGiftsByActiveTrue().subscribe(res => {
      if (!this.gifts) {
        this.gifts = [];
      }
      res.body!.forEach(element => {
        if (element.stock! > 0) {
          this.gifts.push(element);
        }
      });
    });
  }

  exchange(gift: IGift): void {
    if (gift.points! > this.user.points!) {
      this.messageService.add({
        key: 'myKey1',
        severity: 'error',
        summary: this.translate.instant('select-products.error'),
        detail: this.translate.instant('exchange.too-few-points')
      });
      return;
    }
    const cart = new GiftShoppingCart();
    cart.user = this.user;
    cart.gift = gift;
    cart.amount = 1;
    cart.date = dayjs();
    this.giftShoppingCartService.create(cart).subscribe(() => {
      this.user!.points! -= gift.points!;
      this.pointsData.changePoint(this.user!.points!);
      this.generalService.updateUserLoggedInAndPoints(this.user.id, this.user!.loggedIn!, this.user!.points!).subscribe();
      gift.stock! -= 1;
      this.giftService.update(gift).subscribe();
      this.router.navigate(['/my-gifts']);
    });
  }
}
<p-toast key="myKey1"></p-toast>

<h1 class="monoton" jhiTranslate="exchange.title">Temple of gifts</h1>

<div class="alert alert-primary" role="alert">
    <span jhiTranslate="exchange.info">
        On this page you can see all the gifts that you can exchange for your points. Most of the gifts are donations and we would like to thank all donors once again.
    </span>
</div>

<hr/>
<div class="box_yellow">
<h2 jhiTranslate="exchange.all-gifts" class="monoton">All gifts</h2>
<hr/>
<p-carousel [value]="gifts" [numVisible]="5" [numScroll]="1">
    <ng-template let-gift pTemplate="item">
        <p-card class="card">
            <ng-template pTemplate="header">
                <img [src]="'data:' + gift.photoContentType + ';base64,' + gift.photo" style="max-width: 250px;" alt="gift image"/>
            </ng-template>
            <dl class="row-md jh-entity-details">
                <h3><span>{{ gift.title }}</span></h3>

                <dd>
                    <span>{{ gift.description }}</span>
                </dd>
                <hr/>
                <dt><span jhiTranslate="exchange.points">Points</span></dt>
                <dd>
                    <span>{{ gift.points }}</span>
                </dd>
                <dt><span jhiTranslate="exchange.stock">Stock</span></dt>
                <dd>
                    <span>{{ gift.stock }}</span>
                </dd>
            </dl>
            <ng-template pTemplate="footer">
                <ng-container *ngIf="user">
                    <ng-container *jhiHasAnyAuthority="'ROLE_USER'">
                        <button pButton (click)="exchange(gift)" type="button" icon="pi pi-heart" iconPos="left" label="{{ 'exchange.exchange' | translate }}"></button>
                    </ng-container>
                </ng-container>
            </ng-template>
        </p-card>
    </ng-template>
</p-carousel>
</div>
