import { Component } from '@angular/core';
import { FieldType } from '@ngx-formly/core';

@Component({
  selector: 'jhi-formly-field-address',
  templateUrl: './formly-field-address.component.html'
})
export class FormlyFieldAddressComponent extends FieldType {
  formattedaddress = '';

  public addressChange(address: any): void {
    this.formattedaddress = address.formatted_address;
    this.field.formControl!.setValue(address.formatted_address);
  }
}
<label for="{{id}}">
<span>{{to.label}}</span>
    {{to.required ? '*' : ''}}
</label>
<input  ngx-google-places-autocomplete id="{{id}}" [placeholder]="to.placeholder" [formControl]="formControl" class="form-control" [formlyAttributes]="field" [class.is-invalid]="showError" (onAddressChange)="addressChange($event)"/>
<small class="form-text text-muted">{{ to.description }}</small>
