            <span jhiTranslate="createyoureventApp.eventServiceMapOrder.serviceMap">Service Map</span> <fa-icon icon="sort"></fa-icon>
          </th>
          <th scope="col" jhiSortBy="cart.id">
            <span jhiTranslate="createyoureventApp.eventServiceMapOrder.cart">Cart</span> <fa-icon icon="sort"></fa-icon>
          </th>
          <th scope="col"></th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let eventServiceMapOrder of eventServiceMapOrders; trackBy: trackId" data-cy="entityTable">
