import { Component, Input, OnInit } from '@angular/core';

@Component({
  selector: 'jhi-please-wait-dialog',
  templateUrl: './please-wait-dialog.component.html',
  styleUrls: ['./please-wait-dialog.component.scss']
})
export class PleaseWaitDialogComponent implements OnInit {

  @Input() display = false;
  constructor() { }

  ngOnInit() {
  }

}
<p-dialog header="{{ 'please-wait.please-wait' | translate }}" [(visible)]="display"  [modal]="true" [style]="{width: '50vw'}"
[draggable]="false" [resizable]="false">

  <div class="alert alert-info" role="alert" jhiTranslate="add-product.please-wait">
    {{ 'please-wait.please-wait' | translate }}
  </div>
  <p-progressBar mode="indeterminate"></p-progressBar>
</p-dialog>
