export class AddProductToEventComponent {
  constructor(generalService, route, productService, router, eventOrderProductService, jhiEventService, messageService, translate, shopService, eventService, sharedEventService, organisatorService, eventUserService, eventProductOrderService, googleGeocoderService, deliveryTypeService, currencyPipe, dialogService) {
    this.generalService = generalService;
