                    <th style="width:8rem"></th>
                </tr>
            </ng-template>
            <ng-template pTemplate="body" let-serviceMap let-editing="editing" let-ri="rowIndex">
                <tr [pEditableRow]="serviceMap">
                    <td pEditableColumn>
                        <p-cellEditor>
                            <ng-template pTemplate="input">
                                <input pInputText type="text" [(ngModel)]="serviceMap.title" required>
                            </ng-template>
                            <ng-template pTemplate="output">
                                {{serviceMap.title}}
                            </ng-template>
                        </p-cellEditor>
                    </td>
                    <td style="text-align:center">
                        <button *ngIf="!editing" pButton pRipple type="button" pInitEditableRow icon="pi pi-pencil" (click)="onRowEditInit(serviceMap)" class="p-button-rounded p-button-text"></button>
                        <button *ngIf="editing" pButton pRipple type="button" pSaveEditableRow icon="pi pi-check" (click)="onRowEditSave(serviceMap)" class="p-button-rounded p-button-text p-button-success p-mr-2"></button>
                        <button *ngIf="editing" pButton pRipple type="button" pCancelEditableRow icon="pi pi-times" (click)="onRowEditCancel(serviceMap, ri)" class="p-button-rounded p-button-text p-button-danger"></button>
                    </td>
                    <td>
                        <button (click)="gotoServiceOffers(serviceMap.id)"
                        class="btn btn-info btn-sm">
                            <fa-icon icon="clipboard-check"></fa-icon>
                            <span class="d-none d-md-inline" jhiTranslate="dashboardService.gotoServiceOffers">Service-Offers</span>
                        </button>
                    </td>
                    <td>
                        <button type="submit" (click)="delete(serviceMap)"
                            class="btn btn-danger btn-sm">
                        <fa-icon icon="times"></fa-icon>
                        <span class="d-none d-md-inline" jhiTranslate="entity.action.delete">Delete</span>
                        </button>
                    </td>
                </tr>
            </ng-template>
        </p-table>
    </div>




        <hr/>

