import { Component, OnInit } from '@angular/core';
import { HttpResponse } from '@angular/common/http';
import { UntypedFormBuilder } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { Observable } from 'rxjs';
import { finalize } from 'rxjs/operators';

import { IPointsExchange, PointsExchange } from '../points-exchange.model';
import { PointsExchangeService } from '../service/points-exchange.service';

@Component({
  selector: 'jhi-points-exchange-update',
  templateUrl: './points-exchange-update.component.html',
})
export class PointsExchangeUpdateComponent implements OnInit {
  isSaving = false;

  editForm = this.fb.group({
    id: [],
    pointsTotal: [],
    bondPointsTotal: [],
  });

  constructor(
    protected pointsExchangeService: PointsExchangeService,
    protected activatedRoute: ActivatedRoute,
    protected fb: UntypedFormBuilder
  ) {}

  ngOnInit(): void {
    this.activatedRoute.data.subscribe(({ pointsExchange }) => {
      this.updateForm(pointsExchange);
    });
  }

  previousState(): void {
    window.history.back();
  }

  save(): void {
    this.isSaving = true;
    const pointsExchange = this.createFromForm();
    if (pointsExchange.id !== undefined) {
      this.subscribeToSaveResponse(this.pointsExchangeService.update(pointsExchange));
    } else {
      this.subscribeToSaveResponse(this.pointsExchangeService.create(pointsExchange));
    }
  }

  protected subscribeToSaveResponse(result: Observable<HttpResponse<IPointsExchange>>): void {
    result.pipe(finalize(() => this.onSaveFinalize())).subscribe(
      () => this.onSaveSuccess(),
      () => this.onSaveError()
    );
  }

  protected onSaveSuccess(): void {
    this.previousState();
  }

  protected onSaveError(): void {
    // Api for inheritance.
  }

  protected onSaveFinalize(): void {
    this.isSaving = false;
  }

  protected updateForm(pointsExchange: IPointsExchange): void {
    this.editForm.patchValue({
      id: pointsExchange.id,
      pointsTotal: pointsExchange.pointsTotal,
      bondPointsTotal: pointsExchange.bondPointsTotal,
    });
  }

  protected createFromForm(): IPointsExchange {
    return {
      ...new PointsExchange(),
      id: this.editForm.get(['id'])!.value,
      pointsTotal: this.editForm.get(['pointsTotal'])!.value,
      bondPointsTotal: this.editForm.get(['bondPointsTotal'])!.value,
    };
  }
}
<div class="row justify-content-center">
  <div class="col-8">
    <form name="editForm" role="form" novalidate (ngSubmit)="save()" [formGroup]="editForm">
      <h2
        id="jhi-points-exchange-heading"
        data-cy="PointsExchangeCreateUpdateHeading"
        jhiTranslate="createyoureventApp.pointsExchange.home.createOrEditLabel"
      >
        Create or edit a Points Exchange
      </h2>

      <div>
        <jhi-alert-error></jhi-alert-error>

        <div class="form-group" [hidden]="editForm.get('id')!.value == null">
          <label class="form-control-label" jhiTranslate="global.field.id" for="field_id">ID</label>
          <input type="number" class="form-control" name="id" id="field_id" data-cy="id" formControlName="id" [readonly]="true" />
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.pointsExchange.pointsTotal" for="field_pointsTotal"
            >Points Total</label
          >
          <input
            type="number"
            class="form-control"
            name="pointsTotal"
            id="field_pointsTotal"
            data-cy="pointsTotal"
            formControlName="pointsTotal"
          />
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.pointsExchange.bondPointsTotal" for="field_bondPointsTotal"
            >Bond Points Total</label
          >
          <input
            type="number"
            class="form-control"
            name="bondPointsTotal"
            id="field_bondPointsTotal"
            data-cy="bondPointsTotal"
            formControlName="bondPointsTotal"
          />
        </div>
      </div>

      <div>
        <button type="button" id="cancel-save" data-cy="entityCreateCancelButton" class="btn btn-secondary" (click)="previousState()">
          <fa-icon icon="ban"></fa-icon>&nbsp;<span jhiTranslate="entity.action.cancel">Cancel</span>
        </button>

        <button
          type="submit"
          id="save-entity"
          data-cy="entityCreateSaveButton"
          [disabled]="editForm.invalid || isSaving"
          class="btn btn-primary"
        >
          <fa-icon icon="save"></fa-icon>&nbsp;<span jhiTranslate="entity.action.save">Save</span>
        </button>
      </div>
    </form>
  </div>
</div>
