import { Component, OnChanges, OnInit, Input, Output, ViewChild, SimpleChanges, EventEmitter } from '@angular/core';
import { UntypedFormControl, Validators } from '@angular/forms';
import { SharedChatService } from 'app/chat.service';
import { UserPointAssociationService } from 'app/entities/user-point-association/service/user-point-association.service';
import { UserPointAssociation } from 'app/entities/user-point-association/user-point-association.model';
import { IUser } from 'app/entities/user/user.model';
import { GeneralService } from 'app/general.service';
import { PointsDataService } from 'app/points/points-display/points-display.service';
import { IChatController } from 'ng-chat';
import { Subscription } from 'rxjs';
import * as dayjs from 'dayjs';
import { OverlayPanel } from 'primeng/overlaypanel';

@Component({
  selector: 'jhi-comment-box',
  templateUrl: './comment-box.component.html',
  styleUrls: ['comment-box.component.scss'],
})
export class CommentBoxComponent implements OnChanges, OnInit {
  @Input() comments!: any[];
  @Output() comment: EventEmitter<string> = new EventEmitter();
  @Output() answer: EventEmitter<{ commentId: number; answer: string }> = new EventEmitter();

  @ViewChild('op')
  private _op!: OverlayPanel;

  @ViewChild('ngChatInstance')
  protected ngChatInstance!: IChatController;

  userLoggedIn!: IUser;

  points: number = 0;
  subscription: Subscription = new Subscription();

  textareaInput = new UntypedFormControl(null, Validators.required);
  answerInput = new UntypedFormControl(null, Validators.required);

  constructor(
    private sharedChatService: SharedChatService,
    private generalService: GeneralService,
    private pointsDataService: PointsDataService,
    private userPointAssociationService: UserPointAssociationService
  ) {}

  ngOnInit(): void {
    this.subscription = this.pointsDataService.currentPoint.subscribe(points => (this.points = points));
  }

  ngOnChanges(changes: SimpleChanges): void {
    if (changes['comments'] !== undefined) {
      this.comments = changes['comments'].currentValue;
      this.generalService.findWidthAuthorities().subscribe(u => {
        this.userLoggedIn = u.body!;
      });
    }
  }

  clickUserName(user: IUser): void {
    this.sharedChatService.callClickName(user);
  }

  clickAnswer(commentId: number): void {
    this.answer.emit({ commentId: commentId, answer: this.answerInput.value });
    this.answerInput.reset();
    this._op.hide();
  }

  saveComment(): void {
    this.generalService.findPointsByKey('add_comment').subscribe(p => {
      const points = p.body!;
      this.generalService.findUserPointAssociationByUsersIdAndPointkey(this.userLoggedIn.id!, points.key!).subscribe(s => {
        const upa = s.body!;
        const day = dayjs();
        let i = 0;
        upa.forEach(element => {
          if (element.date === day) {
            i++;
          }
        });
        if (i < points.countPerDay!) {
          const iupa = new UserPointAssociation();
          iupa.users = this.userLoggedIn;
          iupa.points = points;
          iupa.date = dayjs();
          this.userPointAssociationService.create(iupa).subscribe();
          this.userLoggedIn.points! += points.points!;
          this.userLoggedIn.loggedIn = true;
          this.generalService
            .updateUserLoggedInAndPoints(this.userLoggedIn.id!, this.userLoggedIn.loggedIn, this.userLoggedIn.points!)
            .subscribe(t => {
              this.generalService.findWidthAuthorities().subscribe(k => {
                this.pointsDataService.changePoint(k.body!.points!);
              });
            });
        }
      });
    });
    this.comment.emit(this.textareaInput.value);
    this.textareaInput.reset();
  }
}
<div class="box">
    <h4 jhiTranslate="comment-box.feedbacks-and-comments">Feedbacks and comments</h4>
    <hr/>
        <textarea [rows]="5" [cols]="100" pInputTextarea [autoResize]="true" [formControl]="textareaInput"></textarea>
        <div *ngIf="textareaInput.invalid && (textareaInput.dirty || textareaInput.touched)" class="alert alert-danger" role="alert">
            <div jhiTranslate="comment-box.comment-invalid">Please give a feedback</div>
        </div>
    <button  class="btn btn-info" (click)="saveComment()" [disabled]="(!textareaInput.valid)">
        <fa-icon icon="save"></fa-icon>&nbsp;<span jhiTranslate="comment-box.save">Save</span>
    </button>
    <hr/>
    <!--
    <p>
        <div class="feedback">
            <ul class="user_comment">
                <li *ngFor="let comment of comments">
                    <div class="comment">
                        {{ comment.comment }}
                    </div>
                </li>
            </ul>
        </div>
    </p>
    -->
    <ng-container *ngFor="let comment of comments">
        <div class="row">
          <!--
            <div class="col-sm-1">
                <div class="thumbnail">
                    <img class="img-responsive user-photo" src="{{ comment.user.imageUrl }}">
                </div>
            </div>
            -->
            <div class="col-sm-8">
                <div class="panel panel-default" *ngIf="comment.shopComment === null || comment.productComment === null || comment.eventComment === null || comment.serviceComment === null || comment.organizationComment === null">
                    <div class="panel-heading">
                        <ng-container *ngIf="comment.user.id !== userLoggedIn.id">
                            <a (click)="clickUserName(comment.user)"><span class="bold">{{ comment.user.firstName }} {{ comment.user.lastName }}</span></a> <span class="text-muted">{{ comment.date | date:'mediumDate' }}</span>
                        </ng-container>
                        <ng-container *ngIf="comment.user.id === userLoggedIn.id">
                            <span class="bold">{{ comment.user.firstName }} {{ comment.user.lastName }}</span> <span class="text-muted">{{ comment.date | date:'mediumDate' }}</span>
                        </ng-container>
                    </div>
                    <div class="panel-body">
                        {{ comment.comment }}
                    </div>
                    <div div class="panel-footer">
                        <p-overlayPanel #op [dismissable]="true" [showCloseIcon]="true"  appendTo="body">
                          <ng-template pTemplate>
                            <textarea [rows]="5" [cols]="100" pInputTextarea [autoResize]="true" [formControl]="answerInput"></textarea>
                            <div *ngIf="answerInput.invalid && (answerInput.dirty || answerInput.touched)" class="alert alert-danger" role="alert">
                                <div jhiTranslate="comment-box.answer-invalid">Please give a answer</div>
                            </div>
                          <button  class="btn btn-info" (click)="clickAnswer(comment.id)" [disabled]="(!answerInput.valid)">
                              <fa-icon icon="save"></fa-icon>&nbsp;<span jhiTranslate="comment-box.save">Save</span>
                          </button>
                          </ng-template>
                      </p-overlayPanel>
                      <button pButton (click)="op.toggle($event)"><span class="bold" jhiTranslate="comment-box.answer">Answer</span></button>
                    </div>
                </div>
                <ng-container *ngIf="comment.organizationComments">
                  <ng-container *ngFor="let answer of comment.organizationComments">
                    <div class="panel-answer panel-default">
                      <div class="panel-answer-heading">
                          <ng-container *ngIf="answer.user.id !== userLoggedIn.id">
                              <a (click)="clickUserName(answer.user)"><span class="bold">{{ answer.user.firstName }} {{ answer.user.lastName }}</span></a> <span class="text-muted">{{ answer.date | date:'mediumDate' }}</span>
                          </ng-container>
                          <ng-container *ngIf="answer.user.id === userLoggedIn.id">
                              <span class="bold">{{ answer.user.firstName }} {{ answer.user.lastName }}</span> <span class="text-muted">{{ answer.date | date:'mediumDate' }}</span>
                          </ng-container>
                      </div>
                      <div class="panel-body">
                          {{ answer.comment }}
                      </div>
                  </div>
                </ng-container>
              </ng-container>
                <ng-container *ngIf="comment.shopComments">
                  <ng-container *ngFor="let answer of comment.shopComments">
                    <div class="panel-answer panel-default">
                      <div class="panel-answer-heading">
                          <ng-container *ngIf="answer.user.id !== userLoggedIn.id">
                              <a (click)="clickUserName(answer.user)"><span class="bold">{{ answer.user.firstName }} {{ answer.user.lastName }}</span></a> <span class="text-muted">{{ answer.date | date:'mediumDate' }}</span>
                          </ng-container>
                          <ng-container *ngIf="answer.user.id === userLoggedIn.id">
                              <span class="bold">{{ answer.user.firstName }} {{ answer.user.lastName }}</span> <span class="text-muted">{{ answer.date | date:'mediumDate' }}</span>
                          </ng-container>
                      </div>
                      <div class="panel-body">
                          {{ answer.comment }}
                      </div>
                  </div>
                </ng-container>
              </ng-container>
              <ng-container *ngIf="comment.productComments">
                <ng-container *ngFor="let answer of comment.productComments">
                  <div class="panel-answer panel-default">
                    <div class="panel-answer-heading">
                        <ng-container *ngIf="answer.user.id !== userLoggedIn.id">
                            <a (click)="clickUserName(answer.user)"><span class="bold">{{ answer.user.firstName }} {{ answer.user.lastName }}</span></a> <span class="text-muted">{{ answer.date | date:'mediumDate' }}</span>
                        </ng-container>
                        <ng-container *ngIf="answer.user.id === userLoggedIn.id">
                            <span class="bold">{{ answer.user.firstName }} {{ answer.user.lastName }}</span> <span class="text-muted">{{ answer.date | date:'mediumDate' }}</span>
                        </ng-container>
                    </div>
                    <div class="panel-body">
                        {{ answer.comment }}
                    </div>
                </div>
              </ng-container>
            </ng-container>
            <ng-container *ngIf="comment.eventComments">
              <ng-container *ngFor="let answer of comment.eventComments">
                <div class="panel-answer panel-default">
                  <div class="panel-answer-heading">
                      <ng-container *ngIf="answer.user.id !== userLoggedIn.id">
                          <a (click)="clickUserName(answer.user)"><span class="bold">{{ answer.user.firstName }} {{ answer.user.lastName }}</span></a> <span class="text-muted">{{ answer.date | date:'mediumDate' }}</span>
                      </ng-container>
                      <ng-container *ngIf="answer.user.id === userLoggedIn.id">
                          <span class="bold">{{ answer.user.firstName }} {{ answer.user.lastName }}</span> <span class="text-muted">{{ answer.date | date:'mediumDate' }}</span>
                      </ng-container>
                  </div>
                  <div class="panel-body">
                      {{ answer.comment }}
                  </div>
              </div>
            </ng-container>
          </ng-container>
          <ng-container *ngIf="comment.serviceComments">
            <ng-container *ngFor="let answer of comment.serviceComments">
              <div class="panel-answer panel-default">
                <div class="panel-answer-heading">
                    <ng-container *ngIf="answer.user.id !== userLoggedIn.id">
                        <a (click)="clickUserName(answer.user)"><span class="bold">{{ answer.user.firstName }} {{ answer.user.lastName }}</span></a> <span class="text-muted">{{ answer.date | date:'mediumDate' }}</span>
                    </ng-container>
                    <ng-container *ngIf="answer.user.id === userLoggedIn.id">
                        <span class="bold">{{ answer.user.firstName }} {{ answer.user.lastName }}</span> <span class="text-muted">{{ answer.date | date:'mediumDate' }}</span>
                    </ng-container>
                </div>
                <div class="panel-body">
                    {{ answer.comment }}
                </div>
            </div>
          </ng-container>
        </ng-container>
            </div>
        </div>
    </ng-container>
</div>
