import { HttpResponse, HttpHeaders } from "@angular/common/http";
import { Component, OnInit, OnDestroy } from "@angular/core";
import { ActivatedRoute, Router } from "@angular/router";
import { NgbModal } from "@ng-bootstrap/ng-bootstrap";
import { ITEMS_PER_PAGE } from "app/config/pagination.constants";
import { IChipsAdmin } from "app/entities/chips-admin/chips-admin.model";
import { ChipsAdminService } from "app/entities/chips-admin/service/chips-admin.service";
import { ChipsCollectionService } from "app/entities/chips-collection/service/chips-collection.service";
import { IChips } from "app/entities/chips/chips.model";
import { ChipsService } from "app/entities/chips/service/chips.service";
import { PropertyService } from "app/entities/property/service/property.service";
import { IUser } from "app/entities/user/user.model";
import { GeneralService } from "app/general.service";
import { JhiDataUtils, JhiEventManager } from "ng-jhipster";
import { ConfirmationService, MessageService } from "primeng/api";
import { Subscription } from "rxjs";
import { SwitchOffOnEmitterService } from "../switch-off-on-emitter.service";
import { ChipsDeleteDialogComponent } from "./chips-delete-dialog.component";


@Component({
  selector: 'jhi-admin-chips',
  templateUrl: './chips.component.html',
  providers: [ConfirmationService,MessageService]
})
export class ChipsComponent implements OnInit, OnDestroy {

  switched = false;
  chips?: IChips[];
  eventSubscriber?: Subscription;
  totalItems = 0;
  itemsPerPage = ITEMS_PER_PAGE;
  page!: number;
  predicate!: string;
  ascending!: boolean;
  ngbPaginationPage = 1;

  stateOptions: any[];
  value1 = "off";

  color: string = '';

  usersWithAllChips: IUser[] = [];


  constructor(
    protected chipsService: ChipsService,
    protected activatedRoute: ActivatedRoute,
    protected dataUtils: JhiDataUtils,
    protected router: Router,
    protected eventManager: JhiEventManager,
    protected modalService: NgbModal,
    private switchOffOnEmitterService: SwitchOffOnEmitterService,
    private chipsAdminService: ChipsAdminService,
    private generalService: GeneralService,
    private chipsCollectionService: ChipsCollectionService,
    private propertyService: PropertyService
  ) {
    this.stateOptions = [{label: 'Off', value: 'off'}, {label: 'On', value: 'on'}];
  }

  loadPage(page?: number): void {
    const pageToLoad: number = page || this.page;

    this.chipsService
      .query({
        page: pageToLoad - 1,
        size: this.itemsPerPage,
        sort: this.sort()
      })
      .subscribe(
        (res: HttpResponse<IChips[]>) => this.onSuccess(res.body, res.headers, pageToLoad),
        () => this.onError()
      );
  }

  changeColor(e: any): void {
    this.generalService.findPropertyByKey('color_chips').subscribe(cp => {
      const property = cp.body!;
      property.value  = e.value;
      this.propertyService.update(property).subscribe();
    });

  }


  ngOnInit(): void {
    this.generalService.findPropertyByKey('color_chips').subscribe(cp => {
      this.color = cp.body!.value!;
    });

    this.chipsAdminService.find(1).subscribe(active => {
      const activeGame = active.body!.gameActive;
      if(activeGame) {
        this.value1 = "on";
      }
    });

    this.activatedRoute.data.subscribe(data => {
      this.page = data.pagingParams.page;
      this.ascending = data.pagingParams.ascending;
      this.predicate = data.pagingParams.predicate;
      this.ngbPaginationPage = data.pagingParams.page;
      this.loadPage();
      this.getAllUsersWithAllChips();
    });
    this.registerChangeInChips();
  }

  getAllUsersWithAllChips(): void {
    if(this.value1 === "off") {
      this.usersWithAllChips = [];
    } else {
      const countChips = this.chips!.length;


      this.chipsCollectionService.query().subscribe(cc => {
        const chipCollections = cc.body!;

        chipCollections.forEach(col => {
          this.generalService.findAllChipsCollectionChipsByChipsCollectionId(col.id!).subscribe(c => {
            if(c.body!.length === this.chips!.length) {
              this.usersWithAllChips.push(col.user!);
            }
          });
        });
      });
    }
  }

  switchOnOff(e: any): void {
    this.chipsAdminService.find(1).subscribe(a => {
      const activeGame: IChipsAdmin = a.body!;
      if(e.value === 'off') {
        activeGame.gameActive = false;
        this.deleteAllChipsRelations();
        this.getAllUsersWithAllChips();
      } else {
        activeGame.gameActive = true;
      }
      this.chipsAdminService.update(activeGame).subscribe(ua => {
        this.switchOffOnEmitterService.onSwitchOffOnButtonClick(e.value);
      });
    });

  }

  deleteAllChipsRelations(): void {
    this.generalService.deleteAllChipsCollectionChips().subscribe();
  }

  ngOnDestroy(): void {
    if (this.eventSubscriber) {
      this.eventManager.destroy(this.eventSubscriber);
    }
  }

  trackId(index: number, item: IChips): number {
    // eslint-disable-next-line @typescript-eslint/no-unnecessary-type-assertion
    return item.id!;
  }

  byteSize(base64String: string): string {
    return this.dataUtils.byteSize(base64String);
  }

  openFile(contentType: string, base64String: string): void {
    return this.dataUtils.openFile(contentType, base64String);
  }

  registerChangeInChips(): void {
    this.eventSubscriber = this.eventManager.subscribe('chipsListModification', () => this.loadPage());
  }

  delete(chips: IChips): void {
    const modalRef = this.modalService.open(ChipsDeleteDialogComponent, { size: 'lg', backdrop: 'static' });
    modalRef.componentInstance.chips = chips;
  }

  sort(): string[] {
    const result = [this.predicate + ',' + (this.ascending ? 'asc' : 'desc')];
    if (this.predicate !== 'id') {
      result.push('id');
    }
    return result;
  }

  protected onSuccess(data: IChips[] | null, headers: HttpHeaders, page: number): void {
    this.totalItems = Number(headers.get('X-Total-Count'));
    this.page = page;
    this.router.navigate(['/adminChips'], {
      queryParams: {
        page: this.page,
        size: this.itemsPerPage,
        sort: this.predicate + ',' + (this.ascending ? 'asc' : 'desc')
      }
    });
    this.chips = data || [];
      this.getAllUsersWithAllChips();
  }

  protected onError(): void {
    this.ngbPaginationPage = this.page;
  }
}
<div>
    <h2 id="page-heading">
        <span jhiTranslate="createyoureventApp.chips.home.title">Chips</span>
        <ng-container *ngIf="value1 === 'off'">
            <button id="jh-create-entity" class="btn btn-primary float-right jh-create-entity create-chips" [routerLink]="['/adminChips/new']">
                <fa-icon icon="plus"></fa-icon>
                <span  jhiTranslate="createyoureventApp.chips.home.createLabel">
                Create a new Chips
                </span>
            </button>
        </ng-container>
    </h2>

    <jhi-alert-error></jhi-alert-error>

    <jhi-alert></jhi-alert>

    <div class="alert alert-warning" id="no-result" *ngIf="chips?.length === 0">
        <span jhiTranslate="createyoureventApp.chips.home.notFound">No chips found</span>
    </div>

    <div class="table-responsive" id="entities" *ngIf="chips?.length > 0">
        <table class="table table-striped" aria-describedby="page-heading">
            <thead>
                <tr jhiSort [(predicate)]="predicate" [(ascending)]="ascending" [callback]="loadPage.bind(this)">
                    <th scope="col"  jhiSortBy="id"><span jhiTranslate="global.field.id">ID</span> <fa-icon icon="sort"></fa-icon></th>
                    <th scope="col"  jhiSortBy="points"><span jhiTranslate="createyoureventApp.chips.points">Points</span> <fa-icon icon="sort"></fa-icon></th>
                    <th scope="col"  jhiSortBy="website"><span jhiTranslate="createyoureventApp.chips.website">Website</span> <fa-icon icon="sort"></fa-icon></th>
                    <th scope="col"  jhiSortBy="x"><span jhiTranslate="createyoureventApp.chips.x">X</span> <fa-icon icon="sort"></fa-icon></th>
                    <th scope="col"  jhiSortBy="y"><span jhiTranslate="createyoureventApp.chips.y">Y</span> <fa-icon icon="sort"></fa-icon></th>
                    <th scope="col"  jhiSortBy="y"><span jhiTranslate="createyoureventApp.chips.color">Color</span> <fa-icon icon="sort"></fa-icon></th>
                    <th scope="col"  jhiSortBy="image"><span jhiTranslate="createyoureventApp.chips.image">Image</span> <fa-icon icon="sort"></fa-icon></th>
                    <th scope="col"></th>
                </tr>
            </thead>
            <tbody>
                <tr *ngFor="let chips of chips ;trackBy: trackId">
                    <td><a [routerLink]="['/adminChips', chips.id, 'view']">{{ chips.id }}</a></td>
                    <td>{{ chips.points }}</td>
                    <td>{{ chips.website }}</td>
                    <td>{{ chips.x }}</td>
                    <td>{{ chips.y }}</td>
                    <td>
                        <div [style]="'width: 15px; height: 15px; border: 1px solid black; background-color: ' + chips.color + ';'"></div>
                    </td>
                    <td>
                        <a *ngIf="chips.image" (click)="openFile(chips.imageContentType, chips.image)">
                            <img [src]="'data:' + chips.imageContentType + ';base64,' + chips.image" style="max-height: 30px;" alt="chips image"/>
                        </a>
                        <span *ngIf="chips.image">{{ chips.imageContentType }}, {{ byteSize(chips.image) }}</span>
                    </td>
                    <td class="text-right">
                        <div class="btn-group">
                            <ng-container *ngIf="value1 === 'off'">
                                <button type="submit"
                                        [routerLink]="['/adminChips', chips.id, 'view']"
                                        class="btn btn-info btn-sm">
                                    <fa-icon icon="eye"></fa-icon>
                                    <span class="d-none d-md-inline" jhiTranslate="entity.action.view">View</span>
                                </button>

                                <button type="submit"
                                        [routerLink]="['/adminChips', chips.id, 'edit']"
                                        class="btn btn-primary btn-sm">
                                    <fa-icon icon="pencil-alt"></fa-icon>
                                    <span class="d-none d-md-inline" jhiTranslate="entity.action.edit">Edit</span>
                                </button>

                                <button type="submit" (click)="delete(chips)"
                                        class="btn btn-danger btn-sm">
                                    <fa-icon icon="times"></fa-icon>
                                    <span class="d-none d-md-inline" jhiTranslate="entity.action.delete">Delete</span>
                                </button>
                            </ng-container>
                        </div>
                    </td>
                </tr>
            </tbody>
        </table>
    </div>

    <div>
        <p-selectButton [options]="stateOptions" [(ngModel)]="value1" optionLabel="label" optionValue="value" (onChange)="switchOnOff($event)"></p-selectButton>
    </div>

    <div *ngIf="chips?.length > 0">
        <div class="row justify-content-center">
            <jhi-item-count [params]="{ page: page, totalItems: totalItems, itemsPerPage: itemsPerPage }"></jhi-item-count>
        </div>

        <div class="row justify-content-center">
            <ngb-pagination [collectionSize]="totalItems" [(page)]="ngbPaginationPage" [pageSize]="itemsPerPage" [maxSize]="5" [rotate]="true" [boundaryLinks]="true" (pageChange)="loadPage($event)"></ngb-pagination>
        </div>
    </div>
</div>
<hr>

<p-table [value]="usersWithAllChips">
    <ng-template pTemplate="header">
        <tr>
            <th>Firstname</th>
            <th>Lastname</th>
            <th>Address</th>
            <th>Phone</th>
            <th>Email</th>
        </tr>
    </ng-template>
    <ng-template pTemplate="body" let-user>
        <tr>
            <td>{{user.firstName}}</td>
            <td>{{user.lastName}}</td>
            <td>{{user.address}}</td>
            <td>{{user.phone}}</td>
            <td>{{user.email}}</td>
        </tr>
    </ng-template>
</p-table>
