import { GeneralService } from 'app/general.service';
import { IBond, Bond } from './../../entities/bond/bond.model';
import { BondService } from './../../entities/bond/service/bond.service';
import { Component, OnInit } from '@angular/core';
import * as dayjs from 'dayjs';
import { Router } from '@angular/router';

@Component({
  selector: 'jhi-admin-bonds',
  templateUrl: './admin-bonds.component.html',
  styleUrls: ['./admin-bonds.component.scss'],
})
export class AdminBondsComponent implements OnInit {
  // Add bond vars
  name: string;
  description: string;
  points: number;
  code: string;

  valide = true;

  generated: string[] = [];
  possible: string = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';

  //Overview bonds vars
  bonds: IBond[];

  constructor(private bondService: BondService, protected router: Router, private generalService: GeneralService) {}

  ngOnInit(): void {
    this.bondService.query().subscribe(bonds => {
      this.bonds = bonds.body;
    });
  }

  generateBond(): void {
    if (!this.name || !this.description || !this.points) {
      this.valide = false;
      return;
    } else {
      this.valide = true;
    }

    this.generated = [];
    this.generateCodes(1, 15);
    this.generalService.findBondsByCode(this.generated.toString()).subscribe(b => {
      const bs = b.body;
      if (bs.length >= 1) {
        this.generateBond();
      }
    });

    const bond = new Bond();
    bond.name = this.name;
    bond.description = this.description;
    bond.creationDate = dayjs();
    bond.code = this.generated.toString();
    bond.points = this.points;
    this.bondService.create(bond).subscribe(() => {
      this.bondService.query().subscribe(bonds => {
        this.bonds = bonds.body;
      });
    });
  }

  generateCodes(number: number, length: number): void {
    for (var i = 0; i < number; i++) {
      this.generateCode(length);
    }
  }

  generateCode(length?: number): void {
    var text = '';

    for (var i = 0; i < length; i++) {
      text += this.possible.charAt(Math.floor(Math.random() * this.possible.length));
    }

    if (this.generated.indexOf(text) === -1) {
      this.generated.push(text);
    } else {
      this.generateCode();
    }
  }

  gotoBond(id: number): void {
    this.router.navigate(['admin/' + id + '/print-bond']);
  }

  onRowEditInit(bond: any): void {
    console.log(bond);
  }

  onRowEditComplete(bond: any): void {
    console.log(bond);
    this.bondService.update(bond.data).subscribe();
  }

  onRowEditCancel(bond: any) {
    console.log(bond);
  }

  deleteBond(bond: IBond): void {
    this.bondService.delete(bond.id).subscribe(() => {
      this.bondService.query().subscribe(bonds => {
        this.bonds = bonds.body;
      });
    });
  }
}
<p-fieldset legend="{{ 'admin-bonds.add-bond' | translate }}" [toggleable]="true">

  <ng-container *ngIf="valide === false">
    <div class="alert alert-danger" role="alert">
      <span jhiTranslate="admin-bonds.fill-out-all-fields">Please fill out all fields</span>
    </div>
  </ng-container>


  <div class="row">
		<div class="col-md-4">
      <div class="p-field p-col-12 p-md-4">
        <span class="p-float-label">
            <input type="text" id="title" pInputText [(ngModel)]="name">
            <label for="title" jhiTranslate="admin-bonds.name">Title</label>
        </span>
      </div>
		</div>
		<div class="col-md-4">
      <div class="p-field p-col-12 p-md-4">
        <span class="p-float-label">
          <textarea inputId="description" [rows]="5" [cols]="30" pInputTextarea [(ngModel)]="description" autoResize="autoResize"></textarea>
            <label for="description" jhiTranslate="admin-bonds.description">Description</label>
        </span>
    </div>
		</div>
		<div class="col-md-4">
      <div class="p-field p-col-12 p-md-4">
        <span class="p-float-label">
            <p-inputNumber inputId="points" [(ngModel)]="points"></p-inputNumber>
            <label for="points" jhiTranslate="admin-bonds.points">Points</label>
        </span>
    </div>
		</div>
	</div>
  <button pButton type="button" label="{{ 'admin-bonds.generate-bond' | translate }}" (click)="generateBond()" ></button>
</p-fieldset>


<p-fieldset legend="{{ 'admin-bonds.overview-bonds' | translate }}" [toggleable]="false">
  <p-table #dt [value]="bonds" [paginator]="true" [rows]="10" [showCurrentPageReport]="true"
  currentPageReportTemplate="Showing {first} to {last} of {totalRecords} entries" [rowsPerPageOptions]="[10,25,50]"
  (onEditInit)="onRowEditInit($event)" (onEditComplete)="onRowEditComplete($event)" (onEditCancel)="onRowEditCancel($event)" editMode="cell">
    <ng-template pTemplate="header">
        <tr>
            <th jhiTranslate="admin-bonds.name">Title</th>
            <th jhiTranslate="admin-bonds.description">Description</th>
            <th jhiTranslate="admin-bonds.points">Points</th>
            <th jhiTranslate="admin-bonds.creation-date">Creation date</th>
            <th jhiTranslate="admin-bonds.redemption-date">Redemption date</th>
            <th jhiTranslate="admin-bonds.code">Code</th>
            <th></th>
        </tr>
    </ng-template>
    <ng-template pTemplate="body" let-bond>
        <tr>
            <td [pEditableColumn]="bond" [pEditableColumnField]="'name'" [pEditableColumnRowIndex]="index">
                <p-cellEditor>
                    <ng-template pTemplate="input">
                        <input pInputText type="text" [(ngModel)]="bond.name">
                    </ng-template>
                    <ng-template pTemplate="output">
                        {{ bond.name }}
                    </ng-template>
                </p-cellEditor>
            </td>
            <td [pEditableColumn]="bond" [pEditableColumnField]="'description'" [pEditableColumnRowIndex]="index">
              <p-cellEditor>
                  <ng-template pTemplate="input">
                    <textarea inputId="description" [rows]="5" [cols]="25" pInputTextarea [(ngModel)]="bond.description" autoResize="autoResize"></textarea>
                  </ng-template>
                  <ng-template pTemplate="output">
                      {{ bond.description }}
                  </ng-template>
              </p-cellEditor>
            </td>
            <td [pEditableColumn]="bond" [pEditableColumnField]="'points'" [pEditableColumnRowIndex]="index">
              <p-cellEditor>
                  <ng-template pTemplate="input">
                    <p-inputNumber inputId="points" [(ngModel)]="bond.points"></p-inputNumber>
                  </ng-template>
                  <ng-template pTemplate="output">
                      {{ bond.points }}
                  </ng-template>
              </p-cellEditor>
            </td>
            <td>{{ bond.creationDate }}</td>
            <td>{{ bond.redemptionDate }}</td>
            <td>{{ bond.code }}</td>
            <td>
              <button pButton type="button" label="{{ 'admin-bonds.print' | translate }}" (click)="gotoBond(bond.id)" ></button>
              <button pButton type="button" label="{{ 'admin-bonds.delete' | translate }}" (click)="deleteBond(bond)" ></button>
            </td>
        </tr>
    </ng-template>
    <ng-template pTemplate="paginatorleft">
        <p-button type="button" icon="pi pi-plus" styleClass="p-button-text"></p-button>
    </ng-template>
    <ng-template pTemplate="paginatorright">
        <p-button type="button" icon="pi pi-cloud" styleClass="p-button-text"></p-button>
    </ng-template>
  </p-table>
</p-fieldset>
