import { HttpResponse } from "@angular/common/http";
import { Component, OnInit } from "@angular/core";
import { NgbModal, ModalDismissReasons } from "@ng-bootstrap/ng-bootstrap";
import { IShop } from "app/entities/shop/shop.model";
import { SupplierDashboardService } from "../dashboard/dashboard.service";


@Component({
  selector: 'jhi-register-product',
  templateUrl: './register-product.component.html',
  styleUrls: ['./register-product.component.scss']
})
export class RegisterProductComponent implements OnInit {

  shops: IShop[] = [];
  closeResult = '';


  constructor(protected supplierDashboardService: SupplierDashboardService, private modalService: NgbModal) { }

  ngOnInit(): void {
    this.supplierDashboardService
    .queryByUserAndActive()
    .subscribe(
      (res: HttpResponse<IShop[]>) => this.shops = res.body!,
      () => console.error('Load shops error')
    );
  }

  private getDismissReason(reason: any): string {
    if (reason === ModalDismissReasons.ESC) {
      return 'by pressing ESC';
    } else if (reason === ModalDismissReasons.BACKDROP_CLICK) {
      return 'by clicking on a backdrop';
    } else {
      return `with: ${reason}`;
    }
  }


  open(content: any): void {
    this.modalService.open(content, {ariaLabelledBy: 'modal-basic-title'}).result.then((result) => {
      this.closeResult = `Closed with: ${result}`;
    }, (reason) => {
      this.closeResult = `Dismissed ${this.getDismissReason(reason)}`;
    });
  }
}
<ng-container *ngFor="let shop of shops">
    <div class="card text-center shop-card">
        <div class="card-header">
            <img class="card-img-top" [src]="'data:' + shop.logoContentType + ';base64,' + shop.logo"  style="max-height: 259px;" alt="shop image"/>
        </div>
        <div class="card-body">
          <h5 class="card-title">{{shop.name}}</h5>
          <p class="card-text">{{shop.motto}}</p>
        </div>
        <div class="card-footer text-muted">
            <button id="overview-product" class="btn btn-primary" [routerLink]="[shop.id, 'overviewProduct']">
                <fa-icon icon="plus"></fa-icon>
                <span  jhiTranslate="register-product.overviewProduct">
                Overview Products
                </span>
            </button>
            <button id="jh-add-product" class="btn btn-primary" [routerLink]="[shop.id, 'addProduct']">
                <fa-icon icon="plus"></fa-icon>
                <span  jhiTranslate="register-product.addProduct">
                Add Product
                </span>
            </button>
            <button id="jh-orders" class="btn btn-primary" [routerLink]="['/supplier/orders/', shop.id]">
                <fa-icon icon="plus"></fa-icon>
                <span  jhiTranslate="register-product.orders">
                Orders
                </span>
            </button>
        </div>
      </div>
</ng-container>
