import { Component, OnInit } from '@angular/core';
import { HttpResponse } from '@angular/common/http';
import { UntypedFormBuilder } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { Observable } from 'rxjs';
import { finalize, map } from 'rxjs/operators';

import * as dayjs from 'dayjs';
import { DATE_TIME_FORMAT } from 'app/config/input.constants';

import { IReservation, Reservation } from '../reservation.model';
import { ReservationService } from '../service/reservation.service';
import { IReservationTransactionId } from 'app/entities/reservation-transaction-id/reservation-transaction-id.model';
import { ReservationTransactionIdService } from 'app/entities/reservation-transaction-id/service/reservation-transaction-id.service';
import { ITicket } from 'app/entities/ticket/ticket.model';
import { TicketService } from 'app/entities/ticket/service/ticket.service';
import { IUser } from 'app/entities/user/user.model';
import { UserService } from 'app/entities/user/user.service';
import { IEvent } from 'app/entities/event/event.model';
import { EventService } from 'app/entities/event/service/event.service';

@Component({
  selector: 'jhi-reservation-update',
  templateUrl: './reservation-update.component.html',
})
export class ReservationUpdateComponent implements OnInit {
  isSaving = false;

  transactionIdsCollection: IReservationTransactionId[] = [];
  ticketsCollection: ITicket[] = [];
  usersSharedCollection: IUser[] = [];
  eventsSharedCollection: IEvent[] = [];

  editForm = this.fb.group({
    id: [],
    date: [],
    billed: [],
    accessEvent: [],
    accessDate: [],
    tdTxId: [],
    transactionId: [],
    ticket: [],
    user: [],
    event: [],
  });

  constructor(
    protected reservationService: ReservationService,
    protected reservationTransactionIdService: ReservationTransactionIdService,
    protected ticketService: TicketService,
    protected userService: UserService,
    protected eventService: EventService,
    protected activatedRoute: ActivatedRoute,
    protected fb: UntypedFormBuilder
  ) {}

  ngOnInit(): void {
    this.activatedRoute.data.subscribe(({ reservation }) => {
      if (reservation.id === undefined) {
        const today = dayjs().startOf('day');
        reservation.date = today;
        reservation.accessDate = today;
      }

      this.updateForm(reservation);

      this.loadRelationshipsOptions();
    });
  }

  previousState(): void {
    window.history.back();
  }

  save(): void {
    this.isSaving = true;
    const reservation = this.createFromForm();
    if (reservation.id !== undefined) {
      this.subscribeToSaveResponse(this.reservationService.update(reservation));
    } else {
      this.subscribeToSaveResponse(this.reservationService.create(reservation));
    }
  }

  trackReservationTransactionIdById(index: number, item: IReservationTransactionId): number {
    return item.id!;
  }

  trackTicketById(index: number, item: ITicket): number {
    return item.id!;
  }

  trackUserById(index: number, item: IUser): string {
    return item.id!;
  }

  trackEventById(index: number, item: IEvent): number {
    return item.id!;
  }

  protected subscribeToSaveResponse(result: Observable<HttpResponse<IReservation>>): void {
    result.pipe(finalize(() => this.onSaveFinalize())).subscribe(
      () => this.onSaveSuccess(),
      () => this.onSaveError()
    );
  }

  protected onSaveSuccess(): void {
    this.previousState();
  }

  protected onSaveError(): void {
    // Api for inheritance.
  }

  protected onSaveFinalize(): void {
    this.isSaving = false;
  }

  protected updateForm(reservation: IReservation): void {
    this.editForm.patchValue({
      id: reservation.id,
      date: reservation.date ? reservation.date.format(DATE_TIME_FORMAT) : null,
      billed: reservation.billed,
      accessEvent: reservation.accessEvent,
      accessDate: reservation.accessDate ? reservation.accessDate.format(DATE_TIME_FORMAT) : null,
      tdTxId: reservation.tdTxId,
      transactionId: reservation.transactionId,
      ticket: reservation.ticket,
      user: reservation.user,
      event: reservation.event,
    });

    this.transactionIdsCollection = this.reservationTransactionIdService.addReservationTransactionIdToCollectionIfMissing(
      this.transactionIdsCollection,
      reservation.transactionId
    );
    this.ticketsCollection = this.ticketService.addTicketToCollectionIfMissing(this.ticketsCollection, reservation.ticket);
    this.usersSharedCollection = this.userService.addUserToCollectionIfMissing(this.usersSharedCollection, reservation.user);
    this.eventsSharedCollection = this.eventService.addEventToCollectionIfMissing(this.eventsSharedCollection, reservation.event);
  }

  protected loadRelationshipsOptions(): void {
    this.reservationTransactionIdService
      .query({ filter: 'reservation-is-null' })
      .pipe(map((res: HttpResponse<IReservationTransactionId[]>) => res.body ?? []))
      .pipe(
        map((reservationTransactionIds: IReservationTransactionId[]) =>
          this.reservationTransactionIdService.addReservationTransactionIdToCollectionIfMissing(
            reservationTransactionIds,
            this.editForm.get('transactionId')!.value
          )
        )
      )
      .subscribe((reservationTransactionIds: IReservationTransactionId[]) => (this.transactionIdsCollection = reservationTransactionIds));

    this.ticketService
      .query({ filter: 'reservation-is-null' })
      .pipe(map((res: HttpResponse<ITicket[]>) => res.body ?? []))
      .pipe(map((tickets: ITicket[]) => this.ticketService.addTicketToCollectionIfMissing(tickets, this.editForm.get('ticket')!.value)))
      .subscribe((tickets: ITicket[]) => (this.ticketsCollection = tickets));

    this.userService
      .query()
      .pipe(map((res: HttpResponse<IUser[]>) => res.body ?? []))
      .pipe(map((users: IUser[]) => this.userService.addUserToCollectionIfMissing(users, this.editForm.get('user')!.value)))
      .subscribe((users: IUser[]) => (this.usersSharedCollection = users));

    this.eventService
      .query()
      .pipe(map((res: HttpResponse<IEvent[]>) => res.body ?? []))
      .pipe(map((events: IEvent[]) => this.eventService.addEventToCollectionIfMissing(events, this.editForm.get('event')!.value)))
      .subscribe((events: IEvent[]) => (this.eventsSharedCollection = events));
  }

  protected createFromForm(): IReservation {
    return {
      ...new Reservation(),
      id: this.editForm.get(['id'])!.value,
      date: this.editForm.get(['date'])!.value ? dayjs(this.editForm.get(['date'])!.value, DATE_TIME_FORMAT) : undefined,
      billed: this.editForm.get(['billed'])!.value,
      accessEvent: this.editForm.get(['accessEvent'])!.value,
      accessDate: this.editForm.get(['accessDate'])!.value ? dayjs(this.editForm.get(['accessDate'])!.value, DATE_TIME_FORMAT) : undefined,
      tdTxId: this.editForm.get(['tdTxId'])!.value,
      transactionId: this.editForm.get(['transactionId'])!.value,
      ticket: this.editForm.get(['ticket'])!.value,
      user: this.editForm.get(['user'])!.value,
      event: this.editForm.get(['event'])!.value,
    };
  }
}
<div class="row justify-content-center">
  <div class="col-8">
    <form name="editForm" role="form" novalidate (ngSubmit)="save()" [formGroup]="editForm">
      <h2
        id="jhi-reservation-heading"
        data-cy="ReservationCreateUpdateHeading"
        jhiTranslate="createyoureventApp.reservation.home.createOrEditLabel"
      >
        Create or edit a Reservation
      </h2>

      <div>
        <jhi-alert-error></jhi-alert-error>

        <div class="form-group" [hidden]="editForm.get('id')!.value == null">
          <label class="form-control-label" jhiTranslate="global.field.id" for="field_id">ID</label>
          <input type="number" class="form-control" name="id" id="field_id" data-cy="id" formControlName="id" [readonly]="true" />
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.reservation.date" for="field_date">Date</label>
          <div class="d-flex">
            <input
              id="field_date"
              data-cy="date"
              type="datetime-local"
              class="form-control"
              name="date"
              formControlName="date"
              placeholder="YYYY-MM-DD HH:mm"
            />
          </div>
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.reservation.billed" for="field_billed">Billed</label>
          <input type="checkbox" class="form-check" name="billed" id="field_billed" data-cy="billed" formControlName="billed" />
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.reservation.accessEvent" for="field_accessEvent"
            >Access Event</label
          >
          <input
            type="checkbox"
            class="form-check"
            name="accessEvent"
            id="field_accessEvent"
            data-cy="accessEvent"
            formControlName="accessEvent"
          />
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.reservation.accessDate" for="field_accessDate"
            >Access Date</label
          >
          <div class="d-flex">
            <input
              id="field_accessDate"
              data-cy="accessDate"
              type="datetime-local"
              class="form-control"
              name="accessDate"
              formControlName="accessDate"
              placeholder="YYYY-MM-DD HH:mm"
            />
          </div>
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.reservation.tdTxId" for="field_tdTxId">Td Tx Id</label>
          <input type="text" class="form-control" name="tdTxId" id="field_tdTxId" data-cy="tdTxId" formControlName="tdTxId" />
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.reservation.transactionId" for="field_transactionId"
            >Transaction Id</label
          >
          <select
            class="form-control"
            id="field_transactionId"
            data-cy="transactionId"
            name="transactionId"
            formControlName="transactionId"
          >
            <option [ngValue]="null"></option>
            <option
              [ngValue]="
                reservationTransactionIdOption.id === editForm.get('transactionId')!.value?.id
                  ? editForm.get('transactionId')!.value
                  : reservationTransactionIdOption
              "
              *ngFor="let reservationTransactionIdOption of transactionIdsCollection; trackBy: trackReservationTransactionIdById"
            >
              {{ reservationTransactionIdOption.id }}
            </option>
          </select>
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.reservation.ticket" for="field_ticket">Ticket</label>
          <select class="form-control" id="field_ticket" data-cy="ticket" name="ticket" formControlName="ticket">
            <option [ngValue]="null"></option>
            <option
              [ngValue]="ticketOption.id === editForm.get('ticket')!.value?.id ? editForm.get('ticket')!.value : ticketOption"
              *ngFor="let ticketOption of ticketsCollection; trackBy: trackTicketById"
            >
              {{ ticketOption.id }}
            </option>
          </select>
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.reservation.user" for="field_user">User</label>
          <select class="form-control" id="field_user" data-cy="user" name="user" formControlName="user">
            <option [ngValue]="null"></option>
            <option
              [ngValue]="userOption.id === editForm.get('user')!.value?.id ? editForm.get('user')!.value : userOption"
              *ngFor="let userOption of usersSharedCollection; trackBy: trackUserById"
            >
              {{ userOption.id }}
            </option>
          </select>
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.reservation.event" for="field_event">Event</label>
          <select class="form-control" id="field_event" data-cy="event" name="event" formControlName="event">
            <option [ngValue]="null"></option>
            <option
              [ngValue]="eventOption.id === editForm.get('event')!.value?.id ? editForm.get('event')!.value : eventOption"
              *ngFor="let eventOption of eventsSharedCollection; trackBy: trackEventById"
            >
              {{ eventOption.id }}
            </option>
          </select>
        </div>
      </div>

      <div>
        <button type="button" id="cancel-save" data-cy="entityCreateCancelButton" class="btn btn-secondary" (click)="previousState()">
          <fa-icon icon="ban"></fa-icon>&nbsp;<span jhiTranslate="entity.action.cancel">Cancel</span>
        </button>

        <button
          type="submit"
          id="save-entity"
          data-cy="entityCreateSaveButton"
          [disabled]="editForm.invalid || isSaving"
          class="btn btn-primary"
        >
          <fa-icon icon="save"></fa-icon>&nbsp;<span jhiTranslate="entity.action.save">Save</span>
        </button>
      </div>
    </form>
  </div>
</div>
