import { HttpResponse, HttpHeaders } from "@angular/common/http";
import { Component, OnInit, OnDestroy } from "@angular/core";
import { ActivatedRoute, Router } from "@angular/router";
import { NgbModal } from "@ng-bootstrap/ng-bootstrap";
import { ITEMS_PER_PAGE } from "app/config/pagination.constants";
import { IEvent } from "app/entities/event/event.model";
import { GeneralService } from "app/general.service";
import { EventDeleteDialogComponent } from "app/views/event/event-delete-dialog.component";
import { EventService } from "app/views/event/event.service";
import { JhiEventManager } from "ng-jhipster";
import { Subscription } from "rxjs";
import { OrganisatorDashboardService } from "./dashboard_organisator.service";


@Component({
  selector: 'jhi-dashboard-organisator',
  templateUrl: './dashboard_organisator.component.html',
  styleUrls: ['./dashboard_organisator.component.scss']
})
export class DashboardOrganisatorComponent implements OnInit, OnDestroy {
  events!: IEvent[];
  eventSubscriber?: Subscription;
  totalItems = 0;
  itemsPerPage = ITEMS_PER_PAGE;
  page!: number;
  predicate!: string;
  ascending!: boolean;
  ngbPaginationPage = 1;
  status = false;

  constructor(
    private organisatorDashboardService: OrganisatorDashboardService,
    protected eventService: EventService,
    protected activatedRoute: ActivatedRoute,
    protected router: Router,
    protected eventManager: JhiEventManager,
    protected modalService: NgbModal,
    private generalService: GeneralService
  ) {
    this.events = [];
  }

  loadPage(page?: number): void {
    const pageToLoad: number = page || this.page;
    this.generalService.findWidthAuthorities().subscribe(u => {
      const user = u.body!;
      this.generalService.getEventsFromUserAndActive(user.id).subscribe(e => {
        this.events = e.body!;
      });
    });

    this.organisatorDashboardService
      .queryByUserAndActive({
        page: pageToLoad - 1,
        size: this.itemsPerPage,
        sort: this.sort()
      })
      .subscribe(
        (res: HttpResponse<IEvent[]>) => this.onSuccess(res.body, res.headers, pageToLoad),
        () => this.onError()
      );
  }

  ngOnInit(): void {
    this.activatedRoute.data.subscribe(data => {
      this.page = data.pagingParams.page;
      this.ascending = data.pagingParams.ascending;
      this.predicate = data.pagingParams.predicate;
      this.ngbPaginationPage = data.pagingParams.page;
      this.loadPage();
    });
    this.registerChangeInEvents();
  }

  ngOnDestroy(): void {
    if (this.eventSubscriber) {
      this.eventManager.destroy(this.eventSubscriber);
    }
  }

  trackId(index: number, item: IEvent): number {
    // eslint-disable-next-line @typescript-eslint/no-unnecessary-type-assertion
    return item.id!;
  }

  registerChangeInEvents(): void {
    this.eventSubscriber = this.eventManager.subscribe('eventListModification', () => this.loadPage());
  }

  delete(event: IEvent): void {
    const modalRef = this.modalService.open(EventDeleteDialogComponent, { size: 'lg', backdrop: 'static' });
    modalRef.componentInstance.event = event;
  }

  sort(): string[] {
    const result = [this.predicate + ',' + (this.ascending ? 'asc' : 'desc')];
    if (this.predicate !== 'id') {
      result.push('id');
    }
    return result;
  }

  protected onSuccess(data: IEvent[] | null, headers: HttpHeaders, page: number): void {
    this.totalItems = Number(headers.get('X-Total-Count'));
    this.page = page;
    this.router.navigate(['/organisator/dashboard'], {
      queryParams: {
        page: this.page,
        size: this.itemsPerPage,
        sort: this.predicate + ',' + (this.ascending ? 'asc' : 'desc')
      }
    });
    this.events = data || [];
  }

  protected onError(): void {
    this.ngbPaginationPage = this.page;
  }
}


<jhi-alert-error></jhi-alert-error>

<jhi-alert></jhi-alert>

<ng-container *ngIf="events.length === 0">
    <div class="alert alert-warning" role="alert">
        <span jhiTranslate="dashboard-event.create-an-event">First, create an event.</span>
      </div>
</ng-container>

<ng-container *ngIf="events.length !== 0">
<div class="container-fluid">
	<div class="row">
		<div class="col-md-12">
			<div class="row">
				<div class="col-md-12">
                    <mat-toolbar color="primary">
                        <span>{{'organisator.dashboard.events' | translate}}</span>
                    </mat-toolbar>
				</div>
			</div>
			<div class="row">
				<div class="col-md-12">
                  <div class="table-responsive" id="entities" *ngIf="events?.length > 0">
                    <table class="table table-striped" aria-describedby="page-heading">
                        <thead>
                            <tr jhiSort [(predicate)]="predicate" [(ascending)]="ascending" [callback]="loadPage.bind(this)">
                                <th scope="col"  jhiSortBy="id"><span jhiTranslate="global.field.id">ID</span> <fa-icon icon="sort"></fa-icon></th>
                                <th scope="col"  jhiSortBy="name"><span jhiTranslate="createyoureventApp.event.name">Name</span> <fa-icon icon="sort"></fa-icon></th>
                                <th scope="col"  jhiSortBy="dateStart"><span jhiTranslate="createyoureventApp.event.dateStart">Date Start</span> <fa-icon icon="sort"></fa-icon></th>
                                <th scope="col"  jhiSortBy="dateEnd"><span jhiTranslate="createyoureventApp.event.dateEnd">Date End</span> <fa-icon icon="sort"></fa-icon></th>
                                <th scope="col"  jhiSortBy="price"><span jhiTranslate="createyoureventApp.event.price">Price</span> <fa-icon icon="sort"></fa-icon></th>
                                <th scope="col"></th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr *ngFor="let event of events ;trackBy: trackId">
                                <td><a [routerLink]="['/events', event.id, 'view']">{{ event.id }}</a></td>
                                <td>{{ event.name }}</td>
                                <td>{{ event.dateStart | date:'mediumDate' }}</td>
                                <td>{{ event.dateEnd | date:'mediumDate' }}</td>
                                <td>{{ event.price | currency }}</td>
                                <td class="text-right">
                                    <div class="btn-group">
                                        <button type="submit"
                                                [routerLink]="['/events', event.id, 'view']"
                                                class="btn btn-info btn-sm">
                                            <fa-icon icon="eye"></fa-icon>
                                            <span class="d-none d-md-inline" jhiTranslate="entity.action.view">View</span>
                                        </button>
                                        <button type="submit"
                                                [routerLink]="['/events', event.id, 'edit']"
                                                class="btn btn-primary btn-sm">
                                            <fa-icon icon="pencil-alt"></fa-icon>
                                            <span class="d-none d-md-inline" jhiTranslate="entity.action.edit">Edit</span>
                                        </button>
                                       <!--
                                        <button type="submit" (click)="delete(event)"
                                                class="btn btn-danger btn-sm">
                                            <fa-icon icon="times"></fa-icon>
                                            <span class="d-none d-md-inline" jhiTranslate="entity.action.delete">Delete</span>
                                        </button>

                                        <button [routerLink]="['/events', event.id, 'product-wishlist']"
                                                class="btn btn-primary btn-sm">
                                            <fa-icon icon="pencil-alt"></fa-icon>
                                            <span class="d-none d-md-inline" jhiTranslate="dashboard-organisator.wishlist">Wishlist</span>
                                        </button>
                                        -->
                                        <button [routerLink]="['/events', event.id, 'dashboard-event']"
                                            class="btn btn-primary btn-sm">
                                            <fa-icon icon="list" fixedWidth="true"></fa-icon>
                                            <span class="d-none d-md-inline" jhiTranslate="dashboard-event.dashboard">Dashboard</span>
                                        </button>

                                        <button [routerLink]="['/organisator/guest-reservation', event.id, 'guest-reservation']" class="btn btn-success btn-sm">
                                            <fa-icon icon="pencil-alt"></fa-icon>
                                            <span class="d-none d-md-inline" jhiTranslate="dashboard-organisator.reservation">Reservation's</span>
                                        </button>
                                        <!--
                                        <ng-container *ngIf="!event.billedOrganisator">
                                            <button [routerLink]="['/organisator/cashbox', event.id, 'cashbox']" class="btn btn-success btn-sm">
                                                <fa-icon icon="cash-register"></fa-icon>
                                                <span class="d-none d-md-inline" jhiTranslate="dashboard-organisator.cashbox">Cashbox</span>
                                            </button>
                                        </ng-container>

                                        <button [routerLink]="['/organisator/offers', event.id, 'offer']"
                                            class="btn btn-primary btn-sm">
                                            <fa-icon icon="money-bill"></fa-icon>
                                            <span class="d-none d-md-inline" jhiTranslate="dashboard-organisator.offer">Offer's</span>
                                        </button>
                                        -->
                                    </div>
                                </td>
                            </tr>
                        </tbody>
                    </table>
                </div>

                <div *ngIf="events?.length > 0">
                    <div class="row justify-content-center">
                        <jhi-item-count [params]="{ page: page, totalItems: totalItems, itemsPerPage: itemsPerPage }"></jhi-item-count>
                    </div>

                    <div class="row justify-content-center">
                        <ngb-pagination [collectionSize]="totalItems" [(page)]="ngbPaginationPage" [pageSize]="itemsPerPage" [maxSize]="5" [rotate]="true" [boundaryLinks]="true" (pageChange)="loadPage($event)"></ngb-pagination>
                    </div>
                </div>
            </div>
				</div>
			</div>
			<div class="row">
				<div class="col-md-8">
				</div>
				<div class="col-md-4">
				</div>
			</div>
		</div>
	</div>
<router-outlet></router-outlet>
</ng-container>
