import { Component, OnInit } from '@angular/core';
import { HttpResponse } from '@angular/common/http';
import { UntypedFormBuilder } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { Observable } from 'rxjs';
import { finalize, map } from 'rxjs/operators';

import * as dayjs from 'dayjs';
import { DATE_TIME_FORMAT } from 'app/config/input.constants';

import { IEventServiceMapOrder, EventServiceMapOrder } from '../event-service-map-order.model';
import { EventServiceMapOrderService } from '../service/event-service-map-order.service';
import { IEvent } from 'app/entities/event/event.model';
import { EventService } from 'app/entities/event/service/event.service';
import { IServiceMap } from 'app/entities/service-map/service-map.model';
import { ServiceMapService } from 'app/entities/service-map/service/service-map.service';
import { ICart } from 'app/entities/cart/cart.model';
import { CartService } from 'app/entities/cart/service/cart.service';

@Component({
  selector: 'jhi-event-service-map-order-update',
  templateUrl: './event-service-map-order-update.component.html',
})
export class EventServiceMapOrderUpdateComponent implements OnInit {
  isSaving = false;

  eventsSharedCollection: IEvent[] = [];
  serviceMapsSharedCollection: IServiceMap[] = [];
  cartsSharedCollection: ICart[] = [];

  editForm = this.fb.group({
    id: [],
    date: [],
    dateFrom: [],
    dateUntil: [],
    costHour: [],
    rideCosts: [],
    total: [],
    totalHours: [],
    kilometre: [],
    billed: [],
    seen: [],
    approved: [],
    event: [],
    serviceMap: [],
    cart: [],
  });

  constructor(
    protected eventServiceMapOrderService: EventServiceMapOrderService,
    protected eventService: EventService,
    protected serviceMapService: ServiceMapService,
    protected cartService: CartService,
    protected activatedRoute: ActivatedRoute,
    protected fb: UntypedFormBuilder
  ) {}

  ngOnInit(): void {
    this.activatedRoute.data.subscribe(({ eventServiceMapOrder }) => {
      if (eventServiceMapOrder.id === undefined) {
        const today = dayjs().startOf('day');
        eventServiceMapOrder.date = today;
        eventServiceMapOrder.dateFrom = today;
        eventServiceMapOrder.dateUntil = today;
      }

      this.updateForm(eventServiceMapOrder);

      this.loadRelationshipsOptions();
    });
  }

  previousState(): void {
    window.history.back();
  }

  save(): void {
    this.isSaving = true;
    const eventServiceMapOrder = this.createFromForm();
    if (eventServiceMapOrder.id !== undefined) {
      this.subscribeToSaveResponse(this.eventServiceMapOrderService.update(eventServiceMapOrder));
    } else {
      this.subscribeToSaveResponse(this.eventServiceMapOrderService.create(eventServiceMapOrder));
    }
  }

  trackEventById(index: number, item: IEvent): number {
    return item.id!;
  }

  trackServiceMapById(index: number, item: IServiceMap): number {
    return item.id!;
  }

  trackCartById(index: number, item: ICart): number {
    return item.id!;
  }

  protected subscribeToSaveResponse(result: Observable<HttpResponse<IEventServiceMapOrder>>): void {
    result.pipe(finalize(() => this.onSaveFinalize())).subscribe(
      () => this.onSaveSuccess(),
      () => this.onSaveError()
    );
  }

  protected onSaveSuccess(): void {
    this.previousState();
  }

  protected onSaveError(): void {
    // Api for inheritance.
  }

  protected onSaveFinalize(): void {
    this.isSaving = false;
  }

  protected updateForm(eventServiceMapOrder: IEventServiceMapOrder): void {
    this.editForm.patchValue({
      id: eventServiceMapOrder.id,
      date: eventServiceMapOrder.date ? eventServiceMapOrder.date.format(DATE_TIME_FORMAT) : null,
      dateFrom: eventServiceMapOrder.dateFrom ? eventServiceMapOrder.dateFrom.format(DATE_TIME_FORMAT) : null,
      dateUntil: eventServiceMapOrder.dateUntil ? eventServiceMapOrder.dateUntil.format(DATE_TIME_FORMAT) : null,
      costHour: eventServiceMapOrder.costHour,
      rideCosts: eventServiceMapOrder.rideCosts,
      total: eventServiceMapOrder.total,
      totalHours: eventServiceMapOrder.totalHours,
      kilometre: eventServiceMapOrder.kilometre,
      billed: eventServiceMapOrder.billed,
      seen: eventServiceMapOrder.seen,
      approved: eventServiceMapOrder.approved,
      event: eventServiceMapOrder.event,
      serviceMap: eventServiceMapOrder.serviceMap,
      cart: eventServiceMapOrder.cart,
    });

    this.eventsSharedCollection = this.eventService.addEventToCollectionIfMissing(this.eventsSharedCollection, eventServiceMapOrder.event);
    this.serviceMapsSharedCollection = this.serviceMapService.addServiceMapToCollectionIfMissing(
      this.serviceMapsSharedCollection,
      eventServiceMapOrder.serviceMap
    );
    this.cartsSharedCollection = this.cartService.addCartToCollectionIfMissing(this.cartsSharedCollection, eventServiceMapOrder.cart);
  }

  protected loadRelationshipsOptions(): void {
    this.eventService
      .query()
      .pipe(map((res: HttpResponse<IEvent[]>) => res.body ?? []))
      .pipe(map((events: IEvent[]) => this.eventService.addEventToCollectionIfMissing(events, this.editForm.get('event')!.value)))
      .subscribe((events: IEvent[]) => (this.eventsSharedCollection = events));

    this.serviceMapService
      .query()
      .pipe(map((res: HttpResponse<IServiceMap[]>) => res.body ?? []))
      .pipe(
        map((serviceMaps: IServiceMap[]) =>
          this.serviceMapService.addServiceMapToCollectionIfMissing(serviceMaps, this.editForm.get('serviceMap')!.value)
        )
      )
      .subscribe((serviceMaps: IServiceMap[]) => (this.serviceMapsSharedCollection = serviceMaps));

    this.cartService
      .query()
      .pipe(map((res: HttpResponse<ICart[]>) => res.body ?? []))
      .pipe(map((carts: ICart[]) => this.cartService.addCartToCollectionIfMissing(carts, this.editForm.get('cart')!.value)))
      .subscribe((carts: ICart[]) => (this.cartsSharedCollection = carts));
  }

  protected createFromForm(): IEventServiceMapOrder {
    return {
      ...new EventServiceMapOrder(),
      id: this.editForm.get(['id'])!.value,
      date: this.editForm.get(['date'])!.value ? dayjs(this.editForm.get(['date'])!.value, DATE_TIME_FORMAT) : undefined,
      dateFrom: this.editForm.get(['dateFrom'])!.value ? dayjs(this.editForm.get(['dateFrom'])!.value, DATE_TIME_FORMAT) : undefined,
      dateUntil: this.editForm.get(['dateUntil'])!.value ? dayjs(this.editForm.get(['dateUntil'])!.value, DATE_TIME_FORMAT) : undefined,
      costHour: this.editForm.get(['costHour'])!.value,
      rideCosts: this.editForm.get(['rideCosts'])!.value,
      total: this.editForm.get(['total'])!.value,
      totalHours: this.editForm.get(['totalHours'])!.value,
      kilometre: this.editForm.get(['kilometre'])!.value,
      billed: this.editForm.get(['billed'])!.value,
      seen: this.editForm.get(['seen'])!.value,
      approved: this.editForm.get(['approved'])!.value,
      event: this.editForm.get(['event'])!.value,
      serviceMap: this.editForm.get(['serviceMap'])!.value,
      cart: this.editForm.get(['cart'])!.value,
    };
  }
}
<div class="row justify-content-center">
  <div class="col-8">
    <form name="editForm" role="form" novalidate (ngSubmit)="save()" [formGroup]="editForm">
      <h2
        id="jhi-event-service-map-order-heading"
        data-cy="EventServiceMapOrderCreateUpdateHeading"
        jhiTranslate="createyoureventApp.eventServiceMapOrder.home.createOrEditLabel"
      >
        Create or edit a Event Service Map Order
      </h2>

      <div>
        <jhi-alert-error></jhi-alert-error>

        <div class="form-group" [hidden]="editForm.get('id')!.value == null">
          <label class="form-control-label" jhiTranslate="global.field.id" for="field_id">ID</label>
          <input type="number" class="form-control" name="id" id="field_id" data-cy="id" formControlName="id" [readonly]="true" />
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.eventServiceMapOrder.date" for="field_date">Date</label>
          <div class="d-flex">
            <input
              id="field_date"
              data-cy="date"
              type="datetime-local"
              class="form-control"
              name="date"
              formControlName="date"
              placeholder="YYYY-MM-DD HH:mm"
            />
          </div>
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.eventServiceMapOrder.dateFrom" for="field_dateFrom"
            >Date From</label
          >
          <div class="d-flex">
            <input
              id="field_dateFrom"
              data-cy="dateFrom"
              type="datetime-local"
              class="form-control"
              name="dateFrom"
              formControlName="dateFrom"
              placeholder="YYYY-MM-DD HH:mm"
            />
          </div>
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.eventServiceMapOrder.dateUntil" for="field_dateUntil"
            >Date Until</label
          >
          <div class="d-flex">
            <input
              id="field_dateUntil"
              data-cy="dateUntil"
              type="datetime-local"
              class="form-control"
              name="dateUntil"
              formControlName="dateUntil"
              placeholder="YYYY-MM-DD HH:mm"
            />
          </div>
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.eventServiceMapOrder.costHour" for="field_costHour"
            >Cost Hour</label
          >
          <input type="number" class="form-control" name="costHour" id="field_costHour" data-cy="costHour" formControlName="costHour" />
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.eventServiceMapOrder.rideCosts" for="field_rideCosts"
            >Ride Costs</label
          >
          <input type="number" class="form-control" name="rideCosts" id="field_rideCosts" data-cy="rideCosts" formControlName="rideCosts" />
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.eventServiceMapOrder.total" for="field_total">Total</label>
          <input type="number" class="form-control" name="total" id="field_total" data-cy="total" formControlName="total" />
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.eventServiceMapOrder.totalHours" for="field_totalHours"
            >Total Hours</label
          >
          <input
            type="text"
            class="form-control"
            name="totalHours"
            id="field_totalHours"
            data-cy="totalHours"
            formControlName="totalHours"
          />
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.eventServiceMapOrder.kilometre" for="field_kilometre"
            >Kilometre</label
          >
          <input type="number" class="form-control" name="kilometre" id="field_kilometre" data-cy="kilometre" formControlName="kilometre" />
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.eventServiceMapOrder.billed" for="field_billed">Billed</label>
          <input type="checkbox" class="form-check" name="billed" id="field_billed" data-cy="billed" formControlName="billed" />
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.eventServiceMapOrder.seen" for="field_seen">Seen</label>
          <input type="checkbox" class="form-check" name="seen" id="field_seen" data-cy="seen" formControlName="seen" />
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.eventServiceMapOrder.approved" for="field_approved"
            >Approved</label
          >
          <input type="checkbox" class="form-check" name="approved" id="field_approved" data-cy="approved" formControlName="approved" />
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.eventServiceMapOrder.event" for="field_event">Event</label>
          <select class="form-control" id="field_event" data-cy="event" name="event" formControlName="event">
            <option [ngValue]="null"></option>
            <option
              [ngValue]="eventOption.id === editForm.get('event')!.value?.id ? editForm.get('event')!.value : eventOption"
              *ngFor="let eventOption of eventsSharedCollection; trackBy: trackEventById"
            >
              {{ eventOption.id }}
            </option>
          </select>
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.eventServiceMapOrder.serviceMap" for="field_serviceMap"
            >Service Map</label
          >
          <select class="form-control" id="field_serviceMap" data-cy="serviceMap" name="serviceMap" formControlName="serviceMap">
            <option [ngValue]="null"></option>
            <option
              [ngValue]="
                serviceMapOption.id === editForm.get('serviceMap')!.value?.id ? editForm.get('serviceMap')!.value : serviceMapOption
              "
              *ngFor="let serviceMapOption of serviceMapsSharedCollection; trackBy: trackServiceMapById"
            >
              {{ serviceMapOption.id }}
            </option>
          </select>
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.eventServiceMapOrder.cart" for="field_cart">Cart</label>
          <select class="form-control" id="field_cart" data-cy="cart" name="cart" formControlName="cart">
            <option [ngValue]="null"></option>
            <option
              [ngValue]="cartOption.id === editForm.get('cart')!.value?.id ? editForm.get('cart')!.value : cartOption"
              *ngFor="let cartOption of cartsSharedCollection; trackBy: trackCartById"
            >
              {{ cartOption.id }}
            </option>
          </select>
        </div>
      </div>

      <div>
        <button type="button" id="cancel-save" data-cy="entityCreateCancelButton" class="btn btn-secondary" (click)="previousState()">
          <fa-icon icon="ban"></fa-icon>&nbsp;<span jhiTranslate="entity.action.cancel">Cancel</span>
        </button>

        <button
          type="submit"
          id="save-entity"
          data-cy="entityCreateSaveButton"
          [disabled]="editForm.invalid || isSaving"
          class="btn btn-primary"
        >
          <fa-icon icon="save"></fa-icon>&nbsp;<span jhiTranslate="entity.action.save">Save</span>
        </button>
      </div>
    </form>
  </div>
</div>
