import { Component, OnInit } from "@angular/core";
import { ActivatedRoute } from "@angular/router";
import { IEvent } from "app/entities/event/event.model";
import { EventService } from "app/entities/event/service/event.service";
import { IUser } from "app/entities/user/user.model";
import { GeneralService } from "app/general.service";


@Component({
  selector: 'jhi-calculation',
  templateUrl: './calculation.component.html',
  styleUrls: ['./calculation.component.scss']
})
export class CalculationComponent implements OnInit {
  eventId!: number;
  jhiEvent!: IEvent;
  user!: IUser;
  sub: any;

  constructor(
    private eventService: EventService,
    private route: ActivatedRoute,
    private generalService: GeneralService,
  ) {}

  ngOnInit(): void {
    this.route.parent!.params.subscribe(params => {
      this.eventId = params['eventId'];

      this.eventService.find(this.eventId).subscribe(ev => {
        this.jhiEvent = ev.body!;

        this.generalService.findWidthAuthorities().subscribe(user => {
          this.user = user.body!;
        });
      });
    });
  }

  previousState(): void {
    window.history.back();
  }
}
<div class="alert alert-info" role="alert">
    <h2 jhiTranslate="dashboard-event.calculation-title">Calculation and balance sheet figures</h2>
    <p jhiTranslate="dashboard-event.calculation-info">
        Below you can see the most important key figures for this event in one view. On the basis of this, you can quickly see whether this event is worthwhile for you if it is carried out.
    </p>
</div>
<hr/>
<p><jhi-calculation-event [jhiEvent]="jhiEvent"></jhi-calculation-event></p>

<div class="row">
    <div class="col-md-12">
        <hr/>
        <div class="btn-cancel">
            <button type="button" id="cancel-save" class="btn btn-secondary btn-cancel" (click)="previousState()">
              <fa-icon icon="ban"></fa-icon>&nbsp;<span jhiTranslate="entity.action.cancel">Cancel</span>
          </button>
        </div>
    </div>
</div>
