import { IBond } from './../../../entities/bond/bond.model';
import { BondService } from './../../../entities/bond/service/bond.service';
import { Component, OnInit } from '@angular/core';
import jspdf from 'jspdf';
import * as html2canvas from 'html2canvas';
import { ActivatedRoute } from "@angular/router";

@Component({
  selector: 'jhi-print-bond',
  templateUrl: './print-bond.component.html',
  styleUrls: ['./print-bond.component.scss']
})
export class PrintBondComponent implements OnInit {

  bondId: number;
  bond: IBond;

  constructor(private route: ActivatedRoute, private bondService: BondService) { }

  ngOnInit(): void {
    this.route.params.subscribe(params => {
      this.bondId = params['bondId'];
      this.bondService.find(this.bondId).subscribe(b => {
        this.bond = b.body;
      });
    });
  }

  generatePDF(): void {
    const data = document.getElementById('bond');
    html2canvas(data!).then((canvas: any) => {
      const imgWidth = 297;
      const imgHeight = 210;
      const contentDataURL = canvas.toDataURL('image/png');
      const pdf = new jspdf('l', 'mm', 'a4');
      pdf.addImage(contentDataURL, 'PNG', 0, 0, imgWidth, imgHeight);
      pdf.save('point-bond.pdf');
    });
  }

  previousState(): void {
    window.history.back();
  }

}
<div id="bond" class="bond">
  <table class="table_bond">
    <tr>
      <td class="logo"><img src="../../../../content/images/createyourevent_250px.png" /></td>
      <td class="company">
        <h1>Create Your Event</h1>
        <h1>Eventcompany</h1>
        <h1>Schaffhausen</h1>
        <div class="points-count">
          {{ bond.points }} {{ 'admin-bonds.points' | translate }}
        </div>
      </td>
      <td class="logo"><img src="../../../../content/images/createyourevent_250px.png" /></td>
    </tr>
    <tr>
      <td colspan="3">
        <div class="box_desc">
          <p class="writing_title">{{ bond.name }}</p>
          <p class="writing">{{ bond.description }}</p>
        </div>
      </td>
    </tr>
    <tr>
      <td colspan="3" class="center">
        <h3 jhiTranslate="admin-bonds.voucher-code"></h3>
        <span class="code">{{ bond.code }}</span>
      </td>
    </tr>
  </table>
</div>

<button type="button" id="cancel-save" class="btn btn-secondary" (click)="previousState()">
  <fa-icon icon="ban"></fa-icon>&nbsp;<span jhiTranslate="entity.action.cancel">Cancel</span>
</button>
<button type="button" id="cancel-save" class="btn btn-secondary" (click)="generatePDF()">
  <fa-icon icon=""></fa-icon>&nbsp;<span>PDF</span>
</button>
