import { Component, OnInit, ChangeDetectorRef } from "@angular/core";
import { ActivatedRoute } from "@angular/router";
import { NgbModal } from "@ng-bootstrap/ng-bootstrap";
import { IProduct } from "app/entities/product/product.model";
import { ProductService } from "app/entities/product/service/product.service";
import { ShopService } from "app/entities/shop/service/shop.service";
import { IShop, Shop } from "app/entities/shop/shop.model";
import { GeneralService } from "app/general.service";
import { ProductDeleteDialogComponent } from "app/views/product/product-delete-dialog.component";
import { JhiEventManager } from "ng-jhipster";
import { Subscription } from "rxjs";
import { SupplierService } from "../supplier.service";


@Component({
  selector: 'jhi-overview-product',
  templateUrl: './overview-product.component.html',
  styleUrls: ['./overview-product.component.scss']
})
export class OverviewProductComponent implements OnInit {
  products: IProduct[] = [];
  shop: IShop = new Shop();
  eventSubscriber?: Subscription;
  searchText = '';

  constructor(
    protected shopService: ShopService,
    protected productService: ProductService,
    protected supplierService: SupplierService,
    private route: ActivatedRoute,
    protected modalService: NgbModal,
    protected eventManager: JhiEventManager,
    private cdr: ChangeDetectorRef,
    private generalService: GeneralService
  ) {}

  ngOnInit(): void {
    const shopId = this.route.snapshot.paramMap.get('id')!;
    this.shopService.find(Number(shopId)).subscribe(res => {
      this.shop = res.body!;
      this.loadPage();
      this.registerChangeInProducts();
    });
  }

  loadPage(): void {
    this.generalService.findAllProductsByShopId(this.shop.id!).subscribe(resp => {
      this.products = resp.body!;
    });
  }

  delete(product: IProduct): void {
    const modalRef = this.modalService.open(ProductDeleteDialogComponent, { size: 'lg', backdrop: 'static' });
    modalRef.componentInstance.product = product;
    this.cdr.detectChanges();
  }

  registerChangeInProducts(): void {
    this.eventSubscriber = this.eventManager.subscribe('productListModification', () => this.loadPage());
  }

  previousState(): void {
    window.history.back();
  }
}
<h2 id="jhi-product-heading" jhiTranslate="overview-product.title">Overview of all product's</h2>
    <div *ngIf="products.length === 0">
        <span jhiTranslate="overview-product.no-product-found">No product found.</span>
    </div>

<div *ngIf="products.length !== 0">
    <span jhiTranslate="overview-product.search-info">Please insert a searchterm to filter after this one.</span><input [(ngModel)]="searchText" placeholder="search text goes here">
</div>


<ng-container *ngIf="products.length > 0">
<p-scrollPanel [style]="{width: '100%', height: '750px'}">
    <div class="content" fxLayout="row wrap" fxLayoutGap="16px">
    <ng-container *ngFor="let product of products | filter : searchText">
        <mat-card fxFlex="0 1 calc(20% - 32px)">
            <mat-card-header>
            <mat-card-title>{{ product.title }}</mat-card-title>
            <mat-card-subtitle>{{ product.motto }}</mat-card-subtitle>
            </mat-card-header>
            <img class="card-image" [src]="'data:' + product.photoContentType + ';base64,' + product.photo" style="max-width: 100%;"  alt="product image"/>
            <mat-card-content>
                <jhi-price-type [product]="product"></jhi-price-type>
                <p>{{ product.price | currency }} / {{ product.amount }} {{ product.unit }}</p>
            </mat-card-content>
            <mat-card-actions>
                <button type="submit"
                [routerLink]="['/products', product.id, 'view']"
                class="btn btn-primary btn-sm">
                <fa-icon icon="eye"></fa-icon>&nbsp;<span jhiTranslate="entity.action.view">View</span>
            </button>

                <button type="submit"
                [routerLink]="['/supplier/register-product', product.id, 'updateProduct']"
                class="btn btn-primary btn-sm">
                    <fa-icon icon="pencil-alt"></fa-icon>
                    <span class="d-none d-md-inline" jhiTranslate="entity.action.edit">Edit</span>
                </button>

                <button type="submit" (click)="delete(product)"
                class="btn btn-danger btn-sm">
                    <fa-icon icon="times"></fa-icon>
                    <span class="d-none d-md-inline" jhiTranslate="entity.action.delete">Delete</span>
                </button>

                <button type="submit"
                [routerLink]="['/products', product.id, 'dashboard-product']"
                class="btn btn-primary btn-sm">
                    <fa-icon icon="list"></fa-icon>
                    <span class="d-none d-md-inline" jhiTranslate="overview-product.dashboard-product">Dashboard product</span>
                </button>

            </mat-card-actions>
        </mat-card>
    </ng-container>
    </div>
</p-scrollPanel>
</ng-container>

<button type="button" id="cancel-save" class="btn btn-secondary" (click)="previousState()">
    <fa-icon icon="ban"></fa-icon>&nbsp;<span jhiTranslate="entity.action.cancel">Cancel</span>
</button>
