import { Component } from '@angular/core';
import { FieldType } from '@ngx-formly/core';

@Component({
 selector: 'jhi-formly-field-time',
 templateUrl: './formly-field-time.component.html',
})
export class FormlyFieldTimeComponent extends FieldType {}

<label for="{{id}}">
    <span>{{to.label}}</span>
        {{to.required ? '*' : ''}}
    </label>
    <input id="{{id}}" type="time" [placeholder]="to.placeholder" [formControl]="formControl" class="form-control" [formlyAttributes]="field" [class.is-invalid]="showError">
    <small class="form-text text-muted">{{ to.description }}</small>
