<div class="row justify-content-center">
  <div class="col-8">
    <form name="editForm" role="form" novalidate (ngSubmit)="save()" [formGroup]="editForm">
      <h2
        id="jhi-organization-heading"
        data-cy="OrganizationCreateUpdateHeading"
        jhiTranslate="createyoureventApp.organization.home.createOrEditLabel"
      >
        Create a Organization
      </h2>

      <div>
        <div class="form-group">
          <label class="form-control-label" jhiTranslate="create-organization.organization-type" for="field_organizationType"
            >Organization Type</label
          >
          <select
            class="form-control"
            name="organizationType"
            formControlName="organizationType"
            id="field_organizationType"
            data-cy="organizationType"
            (change)="changeType($event)"
          >
            <option *ngFor="let organizationType of organizationTypeValues" [value]="organizationType">
              {{ 'createyoureventApp.OrganizationType.' + organizationType | translate }}
            </option>
          </select>
          <div
            *ngIf="
              editForm.get('organizationType')!.invalid &&
              (editForm.get('organizationType')!.dirty || editForm.get('organizationType')!.touched)
            "
          >
            <small
              class="form-text text-danger"
              *ngIf="editForm.get('organizationType')?.errors?.required"
              jhiTranslate="entity.validation.required"
            >
              This field is required.
            </small>
          </div>
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="create-organization.name" for="field_name">Name</label>
          <input type="text" class="form-control" name="name" id="field_name" data-cy="name" formControlName="name" />
          <div *ngIf="editForm.get('name')!.invalid && (editForm.get('name')!.dirty || editForm.get('name')!.touched)">
            <small class="form-text text-danger" *ngIf="editForm.get('name')?.errors?.required" jhiTranslate="entity.validation.required">
              This field is required.
            </small>
          </div>
        </div>

        <ng-container *ngIf="type === 'RESTAURANT'">
          <div class="form-group">
            <label class="form-control-label" jhiTranslate="create-organization.menu" for="field_menu">Menu</label>
            <quill-editor
              [styles]="{ height: '350px' }"
              formControlName="menu"
              [modules]="modules"
              (onEditorCreated)="getEditorInstance($event)"
            >
              <quill-view format="text" theme="snow"> </quill-view>
            </quill-editor>
            <div *ngIf="editForm.get('menu')!.invalid && (editForm.get('menu')!.dirty || editForm.get('menu')!.touched)">
              <small class="form-text text-danger" *ngIf="editForm.get('menu')?.errors?.required" jhiTranslate="entity.validation.required">
                This field is required.
              </small>
            </div>
          </div>
        </ng-container>
        <ng-container *ngIf="type === 'HOTEL'">
          <div class="form-group">
            <label class="form-control-label" jhiTranslate="create-organization.menu" for="field_menu">Menu</label>
            <quill-editor
              [styles]="{ height: '350px' }"
              formControlName="menu"
              [modules]="modules"
              (onEditorCreated)="getEditorInstance($event)"
            >
              <quill-view format="text" theme="snow"> </quill-view>
            </quill-editor>
            <div *ngIf="editForm.get('menu')!.invalid && (editForm.get('menu')!.dirty || editForm.get('menu')!.touched)">
              <small class="form-text text-danger" *ngIf="editForm.get('menu')?.errors?.required" jhiTranslate="entity.validation.required">
                This field is required.
              </small>
            </div>
          </div>

