import { Component } from '@angular/core';
import { FieldType } from '@ngx-formly/core';

@Component({
  selector: 'jhi-formly-field-keywords',
  templateUrl: './formly-field-keywords.component.html'
})
export class FormlyFieldKeywordsComponent extends FieldType {}
<label for="{{id}}">
    <span>{{to.label}}</span>
        {{to.required ? '*' : ''}}
</label>
<tag-input id="{{id}}" [placeholder]="to.placeholder" [formControl]="formControl" [formlyAttributes]="field" [class.is-invalid]="showError" [maxItems]='4'></tag-input>
<small class="form-text text-muted">{{ to.description }}</small>
