import { Component, OnInit, ElementRef } from '@angular/core';
import { HttpResponse } from '@angular/common/http';
// eslint-disable-next-line @typescript-eslint/no-unused-vars
import { UntypedFormBuilder, Validators } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { Observable } from 'rxjs';
import { JhiDataUtils, JhiFileLoadError, JhiEventManager, JhiEventWithContent } from 'ng-jhipster';

import { AlertError } from 'app/shared/alert/alert-error.model';
import { IChips, Chips } from 'app/entities/chips/chips.model';
import { ChipsService } from 'app/entities/chips/service/chips.service';

@Component({
  selector: 'jhi-chips-update',
  templateUrl: './chips-update.component.html',
})
export class ChipsUpdateComponent implements OnInit {
  isSaving = false;

  editForm = this.fb.group({
    id: [],
    points: [],
    website: [],
    x: [],
    y: [],
    image: [],
    imageContentType: [],
    color: [],
  });

  constructor(
    protected dataUtils: JhiDataUtils,
    protected eventManager: JhiEventManager,
    protected chipsService: ChipsService,
    protected elementRef: ElementRef,
    protected activatedRoute: ActivatedRoute,
    private fb: UntypedFormBuilder
  ) {}

  ngOnInit(): void {
    this.activatedRoute.data.subscribe(({ chips }) => {
      this.updateForm(chips);
    });
  }

  updateForm(chips: IChips): void {
    this.editForm.patchValue({
      id: chips.id,
      points: chips.points,
      website: chips.website,
      x: chips.x,
      y: chips.y,
      image: chips.image,
      imageContentType: chips.imageContentType,
      color: chips.color,
    });
  }

  byteSize(base64String: string): string {
    return this.dataUtils.byteSize(base64String);
  }

  openFile(contentType: string, base64String: string): void {
    this.dataUtils.openFile(contentType, base64String);
  }

  setFileData(event: Event, field: string, isImage: boolean): void {
    this.dataUtils.loadFileToForm(event, this.editForm, field, isImage).subscribe(null, (err: JhiFileLoadError) => {
      this.eventManager.broadcast(
        new JhiEventWithContent<AlertError>('createyoureventApp.error', { ...err, key: 'error.file.' + err.key })
      );
    });
  }

  clearInputImage(field: string, fieldContentType: string, idInput: string): void {
    this.editForm.patchValue({
      [field]: null,
      [fieldContentType]: null,
    });
    if (this.elementRef && idInput && this.elementRef.nativeElement.querySelector('#' + idInput)) {
      this.elementRef.nativeElement.querySelector('#' + idInput).value = null;
    }
  }

  previousState(): void {
    window.history.back();
  }

  save(): void {
    this.isSaving = true;
    const chips = this.createFromForm();
    if (chips.id !== undefined) {
      this.subscribeToSaveResponse(this.chipsService.update(chips));
    } else {
      this.subscribeToSaveResponse(this.chipsService.create(chips));
    }
  }

  private createFromForm(): IChips {
    return {
      ...new Chips(),
      id: this.editForm.get(['id'])!.value,
      points: this.editForm.get(['points'])!.value,
      website: this.editForm.get(['website'])!.value,
      x: this.editForm.get(['x'])!.value,
      y: this.editForm.get(['y'])!.value,
      imageContentType: this.editForm.get(['imageContentType'])!.value,
      image: this.editForm.get(['image'])!.value,
      color: this.editForm.get(['color'])!.value,
    };
  }

  protected subscribeToSaveResponse(result: Observable<HttpResponse<IChips>>): void {
    result.subscribe(
      () => this.onSaveSuccess(),
      () => this.onSaveError()
    );
  }

  protected onSaveSuccess(): void {
    this.isSaving = false;
    this.previousState();
  }

  protected onSaveError(): void {
    this.isSaving = false;
  }
}
<div class="row justify-content-center">
    <div class="col-8">
        <form name="editForm" role="form" novalidate (ngSubmit)="save()" [formGroup]="editForm">
            <h2 id="jhi-chips-heading" jhiTranslate="createyoureventApp.chips.home.createOrEditLabel">Create or edit a Chips</h2>

            <div>
                <jhi-alert-error></jhi-alert-error>

                <div class="form-group" [hidden]="!editForm.get('id')!.value">
                    <label for="id" jhiTranslate="global.field.id">ID</label>
                    <input type="text" class="form-control" id="id" name="id" formControlName="id" readonly />
                </div>

                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.chips.points" for="field_points">Points</label>
                    <input type="number" class="form-control" name="points" id="field_points"
                           formControlName="points"/>
                </div>

                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.chips.website" for="field_website">Website</label>
                    <input type="text" class="form-control" name="website" id="field_website"
                           formControlName="website"/>
                </div>

                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.chips.x" for="field_x">X</label>
                    <input type="number" class="form-control" name="x" id="field_x"
                           formControlName="x"/>
                </div>

                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.chips.y" for="field_y">Y</label>
                    <input type="number" class="form-control" name="y" id="field_y"
                           formControlName="y"/>
                </div>

                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.chips.color" for="field_color">Color</label>
                    <!--
                    <input type="text" class="form-control" name="color" id="field_color"
                           formControlName="color"/>
                           -->
                           <p-colorPicker formControlName="color"></p-colorPicker>
                </div>

                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.chips.image" for="field_image">Image</label>
                    <div>
                        <img [src]="'data:' + editForm.get('imageContentType')!.value + ';base64,' + editForm.get('image')!.value" style="max-height: 100px;" *ngIf="editForm.get('image')!.value" alt="chips image"/>
                        <div *ngIf="editForm.get('image')!.value" class="form-text text-danger clearfix">
                            <span class="pull-left">{{ editForm.get('imageContentType')!.value }}, {{ byteSize(editForm.get('image')!.value) }}</span>
                            <button type="button" (click)="clearInputImage('image', 'imageContentType', 'file_image')" class="btn btn-secondary btn-xs pull-right">
                                <fa-icon icon="times"></fa-icon>
                            </button>
                        </div>
                        <input type="file" id="file_image" (change)="setFileData($event, 'image', true)" accept="image/*" jhiTranslate="entity.action.addimage"/>
                    </div>
                    <input type="hidden" class="form-control" name="image" id="field_image"
