import { Component, OnInit } from "@angular/core";
import { Router } from "@angular/router";
import { EventStatus } from "app/entities/enumerations/event-status.model";
import { EventService } from "app/entities/event/service/event.service";
import { IReservation } from "app/entities/reservation/reservation.model";
import { IUser } from "app/entities/user/user.model";
import { GeneralService } from "app/general.service";




@Component({
  selector: 'jhi-tickets',
  templateUrl: './tickets.component.html',
  styleUrls: ['./tickets.component.scss']
})
export class TicketsComponent implements OnInit {

  user: IUser;
  reservationsBilled: IReservation[] = [];
  reservationsNotBilled: IReservation[] = [];

  constructor(private generalService: GeneralService,
              private router: Router,
              private eventService: EventService) {}

  ngOnInit(): void {
    this.generalService.findWidthAuthorities().subscribe(u => {
      this.user = u.body!;

      this.generalService.getReservationsByUserAndNotBilled(this.user.id).subscribe(r => {
        const reservations = r.body;
        reservations.forEach(reservation => {
          if(reservation.event.definitelyConfirmed === true && reservation.event.status === EventStatus.DEFINITELY) {
            this.eventService.find(reservation.event.id).subscribe(e => {
              reservation.event = e.body;
              this.reservationsNotBilled.push(reservation);
            });
          }
        });
      });
      this.generalService.getReservationsByUserAndBilled(this.user.id).subscribe(rb => {
        const reservationsBilled = rb.body;
        this.reservationsBilled = reservationsBilled;
        /*
        reservationsBilled.forEach(reservation => {
          if(reservation.transactionId.transactionDepositId === null && reservation.transactionId.transactionId !== null) {
            this.eventService.find(reservation.event.id).subscribe(e => {
              reservation.event = e.body;
              this.reservationsBilled.push(reservation);
            });
          } else if(reservation.event.definitelyConfirmed === true && reservation.event.status === EventStatus.DEFINITELY) {
            this.eventService.find(reservation.event.id).subscribe(e => {
              reservation.event = e.body;
              this.reservationsBilled.push(reservation);
            });
          }
        });
        */
      });
    });
  }

  pay(reservationId: number): void {
    this.router.navigate(['/account/tickets/' + reservationId + '/payTicket']);
  }
}
<p-tabView>
  <p-tabPanel header="{{ 'tickets.unpaid-tickets' | translate }}">
      <ng-container *ngIf="reservationsNotBilled === null || reservationsNotBilled.length === 0">
        <div class="alert alert-info" role="alert">
          <span jhiTranslate="tickets.no-unpaid-tickets">No upaid tickets</span>
        </div>
      </ng-container>
      <ng-container *ngIf="reservationsNotBilled !== null && reservationsNotBilled.length > 0">
      <div class="container_ticket">
       <div class="ticket-container" *ngFor="let reservation of reservationsNotBilled">
        <widget type="ticket">
          <div class="top">
             <div class="eventname">{{ reservation.event.name }}</div>
             <img src="../../../content/images/dance_ticket.png" alt="" />
             <div class="deetz">
              <div class="row">
                <div class="col-md-3">
                  <span jhiTranslate="tickets.date" class="bold">Date</span>
                </div>
                <div class="col-md-9">
                  {{ reservation.event.dateStart | date: 'medium' }} - {{ reservation.event.dateEnd | date: 'medium' }}
                </div>
              </div>
              <div class="row">
                <div class="col-md-3">
                  <span jhiTranslate="tickets.price" class="bold">Price</span>
                </div>
                <div class="col-md-9">
                  {{ reservation.event.price | currency }}
                </div>
              </div>
              <div class="row">
                <div class="col-md-3">
                  <span jhiTranslate="tickets.location" class="bold">Location</span>
                </div>
                <div class="col-md-9">
                  {{ reservation.event.location.name }}
                </div>
              </div>
              <hr/>
              <div class="row">
                <div class="col-md-3">
                  <span jhiTranslate="tickets.name" class="bold">Name</span>
                </div>
                <div class="col-md-9">
                  {{ reservation.user.firstName }} {{reservation.user.lastName}}
                </div>
              </div>
             </div>
          </div>
          <div class="rip"></div>
          <div class="bottom">
            <button pButton pRipple type="button" label="{{ 'tickets.pay-ticket' | translate }}" icon="pi pi-star" style="font-size: 2rem"  class="p-button-rounded p-button-info" (click)="pay(reservation.id)"></button>
        </div>
       </widget>
       </div>
       <div class="clear"></div>
      </div>
    </ng-container>
  </p-tabPanel>
  <p-tabPanel header="{{ 'tickets.paid-tickets' | translate }}">

    <ng-container *ngIf="reservationsBilled === null || reservationsBilled.length === 0">
      <div class="alert alert-info" role="alert">
        <span jhiTranslate="tickets.no-paid-tickets">No upaid tickets</span>
      </div>
    </ng-container>
    <ng-container *ngIf="reservationsBilled  !== null && reservationsBilled.length > 0">
      <div class="container_ticket">
      <div class="ticket-container" *ngFor="let reservation of reservationsBilled">
      <widget type="ticket">
        <div class="number-of-tickets">
          <span jhiTranslate="tickets.count">Number of tickets:</span> {{ reservation.ticket.amount }}
        </div>
        <div class="top">
           <div class="eventname">{{ reservation.event.name }}</div>
           <img src="../../../content/images/dance_ticket.png" alt="" />
           <div class="deetz">
            <div class="row">
              <div class="col-md-3">
                <span jhiTranslate="tickets.date" class="bold">Date</span>
              </div>
              <div class="col-md-9">
                {{ reservation.event.dateStart | date: 'medium' }} - {{ reservation.event.dateEnd | date: 'medium' }}
              </div>
            </div>
            <div class="row">
              <div class="col-md-3">
                <span jhiTranslate="tickets.price" class="bold">Price</span>
              </div>
              <div class="col-md-9">
                {{ reservation.event.price | currency }}
              </div>
            </div>
            <div class="row">
              <div class="col-md-3">
                <span jhiTranslate="tickets.location" class="bold">Location</span>
              </div>
              <div class="col-md-9">
                {{ reservation.event.location.name }}
              </div>
            </div>
            <hr/>
            <div class="row">
              <div class="col-md-3">
                <span jhiTranslate="tickets.name" class="bold">Name</span>
              </div>
              <div class="col-md-9">
                {{ reservation.user.firstName }} {{reservation.user.lastName}}
              </div>
            </div>
           </div>
        </div>
        <div class="rip"></div>
        <div class="bottom">
        <div class="row">
          <div class="col-md-12">
            <button id="view-reservation" class="btn btn-primary" [routerLink]="['/organisator/guest-reservation', reservation.id, 'view']">
              <fa-icon icon="plus"></fa-icon>
              <span  jhiTranslate="tickets.view-ticket">
              View ticket
              </span>
          </button>
          </div>
        </div>
      </div>
     </widget>
     </div>
     <div class="clear"></div>
    </div>
    </ng-container>
  </p-tabPanel>
</p-tabView>
