import { HttpResponse } from '@angular/common/http';
import { Component, OnInit, ElementRef } from '@angular/core';
import { Validators, UntypedFormBuilder } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { TranslateService } from '@ngx-translate/core';
import { DATE_TIME_FORMAT } from 'app/config/input.constants';
import { IProduct, Product } from 'app/entities/product/product.model';
import { ProductService } from 'app/entities/product/service/product.service';
import { ShopService } from 'app/entities/shop/service/shop.service';
import { IShop } from 'app/entities/shop/shop.model';
import { TagsService } from 'app/entities/tags/service/tags.service';
import { Tags } from 'app/entities/tags/tags.model';
import { GeneralService } from 'app/general.service';
import { AlertError } from 'app/shared/alert/alert-error.model';
import { JhiDataUtils, JhiEventManager, JhiFileLoadError, JhiEventWithContent } from 'ng-jhipster';
import { MessageService } from 'primeng/api';
import { Observable } from 'rxjs';
import * as dayjs from 'dayjs';

@Component({
  selector: 'jhi-product-update',
  templateUrl: './product-update.component.html',
  providers: [MessageService],
})
export class ProductUpdateComponent implements OnInit {
  isSaving = false;
  shops: IShop[] = [];

  quillEditorRef: any;
  maxUploadFileSize = 500000;

  editForm = this.fb.group({
    id: [],
    title: [null, [Validators.required]],
    keywords: [],
    description: [null, [Validators.required]],
    dateAdded: [],
    dateModified: [],
    priceType: [],
    rentType: [],
    price: [null, [Validators.required]],
    photo: [null, [Validators.required]],
    photoContentType: [],
    photo2: [],
    photo2ContentType: [],
    photo3: [],
    photo3ContentType: [],
    youtube: [],
    active: [],
    stock: [null, [Validators.required]],
    productType: [],
    itemNumber: [],
    status: [],
    motto: [null, [Validators.required]],
    shop: [],
  });

  items: any[] = [];

  modules = {};

  constructor(
    protected dataUtils: JhiDataUtils,
    protected eventManager: JhiEventManager,
    protected productService: ProductService,
    protected shopService: ShopService,
    protected elementRef: ElementRef,
    protected activatedRoute: ActivatedRoute,
    private fb: UntypedFormBuilder,
    private tagsService: TagsService,
    private generalService: GeneralService,
    private messageService: MessageService,
    private translate: TranslateService
  ) {
    this.modules = {
      toolbar: [
        ['bold', 'italic', 'underline', 'strike'], // toggled buttons
        ['blockquote', 'code-block'],

        [{ header: 1 }, { header: 2 }], // custom button values
        [{ list: 'ordered' }, { list: 'bullet' }],
        [{ script: 'sub' }, { script: 'super' }], // superscript/subscript
        [{ indent: '-1' }, { indent: '+1' }], // outdent/indent
        [{ direction: 'rtl' }], // text direction

        [{ size: ['small', false, 'large', 'huge'] }], // custom dropdown
        [{ header: [2, 3, 4, 5, 6, false] }],

        [{ color: [] }, { background: [] }], // dropdown with defaults from theme
        [{ font: [] }],
        [{ align: [] }],

        ['clean'], // remove formatting button

        ['link', 'image', 'video'], // link and image, video
      ],
    };
  }

  ngOnInit(): void {
    this.activatedRoute.data.subscribe(({ product }) => {
      if (!product.id) {
        const today = dayjs().startOf('day');
        product.dateAdded = today;
        product.dateModified = today;
      }

      this.updateForm(product);

      this.shopService.query().subscribe((res: HttpResponse<IShop[]>) => (this.shops = res.body || []));

      this.generalService.findProductTags(product.id).subscribe(res => {
        res.body!.forEach(tag => {
          this.items.push({ display: tag.tag, value: tag.tag });
        });
      });
    });
  }

  updateForm(product: IProduct): void {
    this.editForm.patchValue({
      id: product.id,
      title: product.title,
      keywords: product.keywords,
      description: product.description,
      dateAdded: product.dateAdded ? product.dateAdded.format(DATE_TIME_FORMAT) : null,
      dateModified: product.dateModified ? product.dateModified.format(DATE_TIME_FORMAT) : null,
      priceType: product.priceType,
      rentType: product.rentType,
      price: product.price,
      photo: product.photo,
      photoContentType: product.photoContentType,
      photo2: product.photo2,
      photo2ContentType: product.photo2ContentType,
      photo3: product.photo3,
      photo3ContentType: product.photo3ContentType,
      youtube: product.youtube,
      active: product.active,
      stock: product.stock,
      productType: product.productType,
      itemNumber: product.itemNumber,
      status: product.status,
      motto: product.motto,
      shop: product.shop,
    });
  }

  byteSize(base64String: string): string {
    return this.dataUtils.byteSize(base64String);
  }

  openFile(contentType: string, base64String: string): void {
    this.dataUtils.openFile(contentType, base64String);
  }

  setFileData(event: Event, field: string, isImage: boolean): void {
    this.dataUtils.loadFileToForm(event, this.editForm, field, isImage).subscribe(null, (err: JhiFileLoadError) => {
      this.eventManager.broadcast(
        new JhiEventWithContent<AlertError>('createyoureventApp.error', { ...err, key: 'error.file.' + err.key })
      );
    });
  }

  getEditorInstance(editorInstance: any): void {
    this.quillEditorRef = editorInstance;
    const toolbar = editorInstance.getModule('toolbar');
    toolbar.addHandler('image', this.imageHandler);
  }

  imageHandler = (image: any, callback: any) => {
    const input = <HTMLInputElement>document.getElementById('fileInputField');
    input.onchange = () => {
      const file: File = input!.files![0];
      // file type is only image.
      if (/^image\//.test(file.type)) {
        if (file.size > this.maxUploadFileSize) {
          this.messageService.add({
            severity: 'error',
            summary: this.translate.instant('register-shop.filesize.error'),
            detail: this.translate.instant('register-shop.filesize.error.info'),
          });
        } else {
          const reader = new FileReader();
          reader.onload = () => {
            const range = this.quillEditorRef.getSelection();
            const img = '<img src="' + reader.result + '" />';
            this.quillEditorRef.clipboard.dangerouslyPasteHTML(range.index, img);
          };
          reader.readAsDataURL(file);
        }
      } else {
        this.messageService.add({
          severity: 'error',
          summary: this.translate.instant('register-shop.filetype.error'),
          detail: this.translate.instant('register-shop.filetype.error.info'),
        });
      }
    };
    input.click();
  };

  clearInputImage(field: string, fieldContentType: string, idInput: string): void {
    this.editForm.patchValue({
      [field]: null,
      [fieldContentType]: null,
    });
    if (this.elementRef && idInput && this.elementRef.nativeElement.querySelector('#' + idInput)) {
      this.elementRef.nativeElement.querySelector('#' + idInput).value = null;
    }
  }

  previousState(): void {
    window.history.back();
  }

  save(): void {
    this.isSaving = true;
    const product = this.createFromForm();
    if (product.id !== undefined) {
      this.subscribeToSaveResponse(this.productService.update(product));
    } else {
      this.subscribeToSaveResponse(this.productService.create(product));
    }
  }

  private createFromForm(): IProduct {
    return {
      ...new Product(),
      id: this.editForm.get(['id'])!.value,
      title: this.editForm.get(['title'])!.value,
      keywords: this.editForm.get(['keywords'])!.value,
      description: this.editForm.get(['description'])!.value,
      dateAdded: this.editForm.get(['dateAdded'])!.value ? dayjs(this.editForm.get(['dateAdded'])!.value, DATE_TIME_FORMAT) : undefined,
      dateModified: this.editForm.get(['dateModified'])!.value
        ? dayjs(this.editForm.get(['dateModified'])!.value, DATE_TIME_FORMAT)
        : undefined,
      priceType: this.editForm.get(['priceType'])!.value,
      rentType: this.editForm.get(['rentType'])!.value,
      price: this.editForm.get(['price'])!.value,
      photoContentType: this.editForm.get(['photoContentType'])!.value,
      photo: this.editForm.get(['photo'])!.value,
      photo2ContentType: this.editForm.get(['photo2ContentType'])!.value,
      photo2: this.editForm.get(['photo2'])!.value,
      photo3ContentType: this.editForm.get(['photo3ContentType'])!.value,
      photo3: this.editForm.get(['photo3'])!.value,
      youtube: this.editForm.get(['youtube'])!.value,
      active: this.editForm.get(['active'])!.value,
      stock: this.editForm.get(['stock'])!.value,
      productType: this.editForm.get(['productType'])!.value,
      itemNumber: this.editForm.get(['itemNumber'])!.value,
      status: this.editForm.get(['status'])!.value,
      motto: this.editForm.get(['motto'])!.value,
      shop: this.editForm.get(['shop'])!.value,
    };
  }

  protected subscribeToSaveResponse(result: Observable<HttpResponse<IProduct>>): void {
    result.subscribe(
      p => this.onSaveSuccess(p.body!),
      () => this.onSaveError()
    );
  }

  protected onSaveSuccess(p: IProduct): void {
    this.generalService.deleteProductTag(p.id!).subscribe(() => {
      this.items.forEach(item => {
        const tag = new Tags();
        tag.product = p;
        tag.tag = item.value;
        this.tagsService.create(tag).subscribe(() => {
          this.isSaving = false;
        });
      });
      this.previousState();
    });
  }

  protected onSaveError(): void {
    this.isSaving = false;
  }

  trackById(index: number, item: IShop): any {
    return item.id;
  }
}
<p-toast></p-toast>
<div class="row justify-content-center">
    <div class="col-8">
        <form name="editForm" role="form" novalidate (ngSubmit)="save()" [formGroup]="editForm">
            <h2 id="jhi-product-heading" jhiTranslate="createyoureventApp.product.home.createOrEditLabel">Create or edit a Product</h2>

            <div>
                <jhi-alert-error></jhi-alert-error>

                <div class="form-group" [hidden]="!editForm.get('id')!.value">
                    <label for="id" jhiTranslate="global.field.id">ID</label>
                    <input type="text" class="form-control" id="id" name="id" formControlName="id" readonly />
                </div>

                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.product.title" for="field_title">Title</label>
                    <input type="text" class="form-control" name="title" id="field_title"
                           formControlName="title"/>
                    <div *ngIf="editForm.get('title')!.invalid && (editForm.get('title')!.dirty || editForm.get('title')!.touched)">
                        <small class="form-text text-danger"
                               *ngIf="editForm.get('title')?.errors?.required" jhiTranslate="entity.validation.required">
                        This field is required.
                        </small>
                    </div>
                </div>

                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.product.motto" for="field_motto">Motto</label>
                    <input type="text" class="form-control" name="motto" id="field_motto"
                           formControlName="motto"/>
                    <div *ngIf="editForm.get('motto')!.invalid && (editForm.get('motto')!.dirty || editForm.get('motto')!.touched)">
                        <small class="form-text text-danger"
                               *ngIf="editForm.get('motto')?.errors?.required" jhiTranslate="entity.validation.required">
                        This field is required.
                        </small>
                    </div>
                </div>

                <!--
                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.product.keywords" for="field_keywords">Keywords</label>
                    <input type="text" class="form-control" name="keywords" id="field_keywords"
                           formControlName="keywords"/>
                </div>
                -->

                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.product.keywords" for="field_keywords">Keywords</label>
                    <tag-input [(ngModel)]='items'></tag-input>
                </div>



                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.product.description" for="field_description">Description</label>
                    <quill-editor name="description" id="field_description" formControlName="description" [modules]="modules" (onEditorCreated)="getEditorInstance($event)"></quill-editor>
                    <input type="file" style="display: none" id="fileInputField"/>
                    <div *ngIf="editForm.get('description')!.invalid && (editForm.get('description')!.dirty || editForm.get('description')!.touched)">
                        <small class="form-text text-danger"
                               *ngIf="editForm.get('description')?.errors?.required" jhiTranslate="entity.validation.required">
                        This field is required.
                        </small>
                    </div>
                </div>

                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.product.dateAdded" for="field_dateAdded">Date Added</label>
                    <div class="d-flex">
                        <input id="field_dateAdded" type="datetime-local" class="form-control" name="dateAdded" formControlName="dateAdded" placeholder="YYYY-MM-DD HH:mm"/>
                    </div>
                </div>

                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.product.dateModified" for="field_dateModified">Date Modified</label>
                    <div class="d-flex">
                        <input id="field_dateModified" type="datetime-local" class="form-control" name="dateModified" formControlName="dateModified" placeholder="YYYY-MM-DD HH:mm"/>
                    </div>
                </div>

                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.product.priceType" for="field_priceType">Price Type</label>
                    <select class="form-control" name="priceType" formControlName="priceType" id="field_priceType">
                        <option value="ITEM_PRICE">{{ 'createyoureventApp.PriceType.ITEM_PRICE' | translate }}</option>
                        <option value="HOURLY_PRICE">{{ 'createyoureventApp.PriceType.HOURLY_PRICE' | translate }}</option>
                    </select>
                </div>

                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.product.price" for="field_price">Price</label>
                    <input type="number" class="form-control" name="price" id="field_price"
                           formControlName="price"/>
                    <div *ngIf="editForm.get('price')!.invalid && (editForm.get('price')!.dirty || editForm.get('price')!.touched)">
                        <small class="form-text text-danger"
                               *ngIf="editForm.get('price')?.errors?.required" jhiTranslate="entity.validation.required">
                        This field is required.
                        </small>
                        <small class="form-text text-danger"
                            [hidden]="!editForm.get('price')?.errors?.number" jhiTranslate="entity.validation.number">
                            This field should be a number.
                        </small>
                    </div>
                </div>

                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.product.number" for="field_number">Number</label>
                    <input type="number" class="form-control" name="number" id="field_number"
                           formControlName="number"/>
                    <div *ngIf="editForm.get('number')!.invalid && (editForm.get('number')!.dirty || editForm.get('number')!.touched)">
                        <small class="form-text text-danger"
                               *ngIf="editForm.get('number')?.errors?.required" jhiTranslate="entity.validation.required">
                        This field is required.
                        </small>
                        <small class="form-text text-danger"
                            [hidden]="!editForm.get('number')?.errors?.number" jhiTranslate="entity.validation.number">
                            This field should be a number.
                        </small>
                    </div>
                </div>

                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.product.photo" for="field_photo">Photo</label>
                    <div>
                        <img [src]="'data:' + editForm.get('photoContentType')!.value + ';base64,' + editForm.get('photo')!.value" style="max-height: 100px;" *ngIf="editForm.get('photo')!.value" alt="product image"/>
                        <div *ngIf="editForm.get('photo')!.value" class="form-text text-danger clearfix">
                            <span class="pull-left">{{ editForm.get('photoContentType')!.value }}, {{ byteSize(editForm.get('photo')!.value) }}</span>
                            <button type="button" (click)="clearInputImage('photo', 'photoContentType', 'file_photo')" class="btn btn-secondary btn-xs pull-right">
                                <fa-icon icon="times"></fa-icon>
                            </button>
                        </div>
                        <input type="file" id="file_photo" (change)="setFileData($event, 'photo', true)" accept="image/*" jhiTranslate="entity.action.addimage"/>
                    </div>
                    <input type="hidden" class="form-control" name="photo" id="field_photo"
                           formControlName="photo"/>
                    <input type="hidden" class="form-control" name="photoContentType" id="field_photoContentType"
                           formControlName="photoContentType" />
                    <div *ngIf="editForm.get('photo')!.invalid && (editForm.get('photo')!.dirty || editForm.get('photo')!.touched)">
                        <small class="form-text text-danger"
                               *ngIf="editForm.get('photo')?.errors?.required" jhiTranslate="entity.validation.required">
                        This field is required.
                        </small>
                    </div>
                </div>

                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.product.photo2" for="field_photo2">Photo 2</label>
                    <div>
                        <img [src]="'data:' + editForm.get('photo2ContentType')!.value + ';base64,' + editForm.get('photo2')!.value" style="max-height: 100px;" *ngIf="editForm.get('photo2')!.value" alt="product image"/>
                        <div *ngIf="editForm.get('photo2')!.value" class="form-text text-danger clearfix">
                            <span class="pull-left">{{ editForm.get('photo2ContentType')!.value }}, {{ byteSize(editForm.get('photo2')!.value) }}</span>
                            <button type="button" (click)="clearInputImage('photo2', 'photo2ContentType', 'file_photo2')" class="btn btn-secondary btn-xs pull-right">
                                <fa-icon icon="times"></fa-icon>
                            </button>
                        </div>
                        <input type="file" id="file_photo2" (change)="setFileData($event, 'photo2', true)" accept="image/*" jhiTranslate="entity.action.addimage"/>
                    </div>
                    <input type="hidden" class="form-control" name="photo2" id="field_photo2"
                           formControlName="photo2"/>
                    <input type="hidden" class="form-control" name="photo2ContentType" id="field_photo2ContentType"
                           formControlName="photo2ContentType" />
                </div>

                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.product.photo3" for="field_photo3">Photo 3</label>
                    <div>
                        <img [src]="'data:' + editForm.get('photo3ContentType')!.value + ';base64,' + editForm.get('photo3')!.value" style="max-height: 100px;" *ngIf="editForm.get('photo3')!.value" alt="product image"/>
                        <div *ngIf="editForm.get('photo3')!.value" class="form-text text-danger clearfix">
                            <span class="pull-left">{{ editForm.get('photo3ContentType')!.value }}, {{ byteSize(editForm.get('photo3')!.value) }}</span>
                            <button type="button" (click)="clearInputImage('photo3', 'photo3ContentType', 'file_photo3')" class="btn btn-secondary btn-xs pull-right">
                                <fa-icon icon="times"></fa-icon>
                            </button>
                        </div>
                        <input type="file" id="file_photo3" (change)="setFileData($event, 'photo3', true)" accept="image/*" jhiTranslate="entity.action.addimage"/>
                    </div>
                    <input type="hidden" class="form-control" name="photo3" id="field_photo3"
                           formControlName="photo3"/>
                    <input type="hidden" class="form-control" name="photo3ContentType" id="field_photo3ContentType"
                           formControlName="photo3ContentType" />
                </div>

                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.product.youtube" for="field_youtube">Youtube</label>
                    <input type="text" class="form-control" name="youtube" id="field_youtube"
                           formControlName="youtube"/>
                </div>

                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.product.active" for="field_active">Active</label>
                    <input type="checkbox" class="form-control" name="active" id="field_active"
                           formControlName="active"/>
                </div>

                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.product.rating" for="field_rating">Rating</label>
                    <input type="number" class="form-control" name="rating" id="field_rating"
                           formControlName="rating"/>
                </div>

                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.product.shop" for="field_shop">Shop</label>
                    <select class="form-control" id="field_shop" name="shop" formControlName="shop">
                        <option [ngValue]="null"></option>
                        <option [ngValue]="shopOption.id === editForm.get('shop')!.value?.id ? editForm.get('shop')!.value : shopOption" *ngFor="let shopOption of shops; trackBy: trackById">{{ shopOption.id }}</option>
                    </select>
                </div>
            </div>

            <div>
                <button type="button" id="cancel-save" class="btn btn-secondary" (click)="previousState()">
                    <fa-icon icon="ban"></fa-icon>&nbsp;<span jhiTranslate="entity.action.cancel">Cancel</span>
                </button>

                <button type="submit" id="save-entity" [disabled]="editForm.invalid || isSaving" class="btn btn-primary">
                    <fa-icon icon="save"></fa-icon>&nbsp;<span jhiTranslate="entity.action.save">Save</span>
                </button>
            </div>
        </form>
    </div>
</div>
