@Component({
  selector: 'app-flyer-generator',
  templateUrl: './flyer-generator.component.html',
  styleUrls: ['./flyer-generator.component.css']
})
export class FlyerGeneratorComponent implements OnInit {

  constructor() { }

  ngOnInit() {
    // Initialisieren des Marq-Widgets
    const marqWidget = new MarqWidget({
      apiKey: 'Ihr API-Schlüssel hier',
      container: 'marq-container',
      design: 'default',
      onComplete: function (data) {
        // Callback-Funktion, die aufgerufen wird, wenn das Design abgeschlossen ist
        console.log('Design abgeschlossen', data);
      }
    });

    // Laden des Marq-Widgets
    marqWidget.load();
  }

}
