import { Component, OnInit } from '@angular/core';
import { HttpResponse } from '@angular/common/http';
import { UntypedFormBuilder } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { Observable } from 'rxjs';
import { finalize, map } from 'rxjs/operators';

import * as dayjs from 'dayjs';
import { DATE_TIME_FORMAT } from 'app/config/input.constants';

import { IBond, Bond } from '../bond.model';
import { BondService } from '../service/bond.service';
import { AlertError } from 'app/shared/alert/alert-error.model';
import { EventManager, EventWithContent } from 'app/core/util/event-manager.service';
import { DataUtils, FileLoadError } from 'app/core/util/data-util.service';
import { IUser } from 'app/entities/user/user.model';
import { UserService } from 'app/entities/user/user.service';
import { IPointsExchange } from 'app/entities/points-exchange/points-exchange.model';
import { PointsExchangeService } from 'app/entities/points-exchange/service/points-exchange.service';

@Component({
  selector: 'jhi-bond-update',
  templateUrl: './bond-update.component.html',
})
export class BondUpdateComponent implements OnInit {
  isSaving = false;

  usersSharedCollection: IUser[] = [];
  pointsExchangesSharedCollection: IPointsExchange[] = [];

  editForm = this.fb.group({
    id: [],
    name: [],
    description: [],
    code: [],
    points: [],
    creationDate: [],
    redemptionDate: [],
    user: [],
    pointsExchange: [],
  });

  constructor(
    protected dataUtils: DataUtils,
    protected eventManager: EventManager,
    protected bondService: BondService,
    protected userService: UserService,
    protected pointsExchangeService: PointsExchangeService,
    protected activatedRoute: ActivatedRoute,
    protected fb: UntypedFormBuilder
  ) {}

  ngOnInit(): void {
    this.activatedRoute.data.subscribe(({ bond }) => {
      if (bond.id === undefined) {
        const today = dayjs().startOf('day');
        bond.creationDate = today;
        bond.redemptionDate = today;
      }

      this.updateForm(bond);

      this.loadRelationshipsOptions();
    });
  }

  byteSize(base64String: string): string {
    return this.dataUtils.byteSize(base64String);
  }

  openFile(base64String: string, contentType: string | null | undefined): void {
    this.dataUtils.openFile(base64String, contentType);
  }

  setFileData(event: Event, field: string, isImage: boolean): void {
    this.dataUtils.loadFileToForm(event, this.editForm, field, isImage).subscribe({
      error: (err: FileLoadError) =>
        this.eventManager.broadcast(new EventWithContent<AlertError>('createyoureventApp.error', { ...err, key: 'error.file.' + err.key })),
    });
  }

  previousState(): void {
    window.history.back();
  }

  save(): void {
    this.isSaving = true;
    const bond = this.createFromForm();
    if (bond.id !== undefined) {
      this.subscribeToSaveResponse(this.bondService.update(bond));
    } else {
      this.subscribeToSaveResponse(this.bondService.create(bond));
    }
  }

  trackUserById(index: number, item: IUser): string {
    return item.id!;
  }

  trackPointsExchangeById(index: number, item: IPointsExchange): number {
    return item.id!;
  }

  protected subscribeToSaveResponse(result: Observable<HttpResponse<IBond>>): void {
    result.pipe(finalize(() => this.onSaveFinalize())).subscribe(
      () => this.onSaveSuccess(),
      () => this.onSaveError()
    );
  }

  protected onSaveSuccess(): void {
    this.previousState();
  }

  protected onSaveError(): void {
    // Api for inheritance.
  }

  protected onSaveFinalize(): void {
    this.isSaving = false;
  }

  protected updateForm(bond: IBond): void {
    this.editForm.patchValue({
      id: bond.id,
      name: bond.name,
      description: bond.description,
      code: bond.code,
      points: bond.points,
      creationDate: bond.creationDate ? bond.creationDate.format(DATE_TIME_FORMAT) : null,
      redemptionDate: bond.redemptionDate ? bond.redemptionDate.format(DATE_TIME_FORMAT) : null,
      user: bond.user,
      pointsExchange: bond.pointsExchange,
    });

    this.usersSharedCollection = this.userService.addUserToCollectionIfMissing(this.usersSharedCollection, bond.user);
    this.pointsExchangesSharedCollection = this.pointsExchangeService.addPointsExchangeToCollectionIfMissing(
      this.pointsExchangesSharedCollection,
      bond.pointsExchange
    );
  }

  protected loadRelationshipsOptions(): void {
    this.userService
      .query()
      .pipe(map((res: HttpResponse<IUser[]>) => res.body ?? []))
      .pipe(map((users: IUser[]) => this.userService.addUserToCollectionIfMissing(users, this.editForm.get('user')!.value)))
      .subscribe((users: IUser[]) => (this.usersSharedCollection = users));

    this.pointsExchangeService
      .query()
      .pipe(map((res: HttpResponse<IPointsExchange[]>) => res.body ?? []))
      .pipe(
        map((pointsExchanges: IPointsExchange[]) =>
          this.pointsExchangeService.addPointsExchangeToCollectionIfMissing(pointsExchanges, this.editForm.get('pointsExchange')!.value)
        )
      )
      .subscribe((pointsExchanges: IPointsExchange[]) => (this.pointsExchangesSharedCollection = pointsExchanges));
  }

  protected createFromForm(): IBond {
    return {
      ...new Bond(),
      id: this.editForm.get(['id'])!.value,
      name: this.editForm.get(['name'])!.value,
      description: this.editForm.get(['description'])!.value,
      code: this.editForm.get(['code'])!.value,
      points: this.editForm.get(['points'])!.value,
      creationDate: this.editForm.get(['creationDate'])!.value
        ? dayjs(this.editForm.get(['creationDate'])!.value, DATE_TIME_FORMAT)
        : undefined,
      redemptionDate: this.editForm.get(['redemptionDate'])!.value
        ? dayjs(this.editForm.get(['redemptionDate'])!.value, DATE_TIME_FORMAT)
        : undefined,
      user: this.editForm.get(['user'])!.value,
      pointsExchange: this.editForm.get(['pointsExchange'])!.value,
    };
  }
}
<div class="row justify-content-center">
  <div class="col-8">
    <form name="editForm" role="form" novalidate (ngSubmit)="save()" [formGroup]="editForm">
      <h2 id="jhi-bond-heading" data-cy="BondCreateUpdateHeading" jhiTranslate="createyoureventApp.bond.home.createOrEditLabel">
        Create or edit a Bond
      </h2>

      <div>
        <jhi-alert-error></jhi-alert-error>

        <div class="form-group" [hidden]="editForm.get('id')!.value == null">
          <label class="form-control-label" jhiTranslate="global.field.id" for="field_id">ID</label>
          <input type="number" class="form-control" name="id" id="field_id" data-cy="id" formControlName="id" [readonly]="true" />
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.bond.name" for="field_name">Name</label>
          <input type="text" class="form-control" name="name" id="field_name" data-cy="name" formControlName="name" />
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.bond.description" for="field_description">Description</label>
          <textarea
            class="form-control"
            name="description"
            id="field_description"
            data-cy="description"
            formControlName="description"
          ></textarea>
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.bond.code" for="field_code">Code</label>
          <input type="text" class="form-control" name="code" id="field_code" data-cy="code" formControlName="code" />
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.bond.points" for="field_points">Points</label>
          <input type="number" class="form-control" name="points" id="field_points" data-cy="points" formControlName="points" />
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.bond.creationDate" for="field_creationDate"
            >Creation Date</label
          >
          <div class="d-flex">
            <input
              id="field_creationDate"
              data-cy="creationDate"
              type="datetime-local"
              class="form-control"
              name="creationDate"
              formControlName="creationDate"
              placeholder="YYYY-MM-DD HH:mm"
            />
          </div>
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.bond.redemptionDate" for="field_redemptionDate"
            >Redemption Date</label
          >
          <div class="d-flex">
            <input
              id="field_redemptionDate"
              data-cy="redemptionDate"
              type="datetime-local"
              class="form-control"
              name="redemptionDate"
              formControlName="redemptionDate"
              placeholder="YYYY-MM-DD HH:mm"
            />
          </div>
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.bond.user" for="field_user">User</label>
          <select class="form-control" id="field_user" data-cy="user" name="user" formControlName="user">
            <option [ngValue]="null"></option>
            <option
              [ngValue]="userOption.id === editForm.get('user')!.value?.id ? editForm.get('user')!.value : userOption"
              *ngFor="let userOption of usersSharedCollection; trackBy: trackUserById"
            >
              {{ userOption.id }}
            </option>
          </select>
        </div>

        <div class="form-group">
          <label class="form-control-label" jhiTranslate="createyoureventApp.bond.pointsExchange" for="field_pointsExchange"
            >Points Exchange</label
          >
          <select
            class="form-control"
            id="field_pointsExchange"
            data-cy="pointsExchange"
            name="pointsExchange"
            formControlName="pointsExchange"
          >
            <option [ngValue]="null"></option>
            <option
              [ngValue]="
                pointsExchangeOption.id === editForm.get('pointsExchange')!.value?.id
                  ? editForm.get('pointsExchange')!.value
                  : pointsExchangeOption
              "
              *ngFor="let pointsExchangeOption of pointsExchangesSharedCollection; trackBy: trackPointsExchangeById"
            >
              {{ pointsExchangeOption.id }}
            </option>
          </select>
        </div>
      </div>

      <div>
        <button type="button" id="cancel-save" data-cy="entityCreateCancelButton" class="btn btn-secondary" (click)="previousState()">
          <fa-icon icon="ban"></fa-icon>&nbsp;<span jhiTranslate="entity.action.cancel">Cancel</span>
        </button>

        <button
          type="submit"
          id="save-entity"
          data-cy="entityCreateSaveButton"
          [disabled]="editForm.invalid || isSaving"
          class="btn btn-primary"
        >
          <fa-icon icon="save"></fa-icon>&nbsp;<span jhiTranslate="entity.action.save">Save</span>
        </button>
      </div>
    </form>
  </div>
</div>
