import { ChangeDetectorRef, Component, OnInit } from "@angular/core";
import { ActivatedRoute } from "@angular/router";
import { SharedChatService } from "app/chat.service";
import { ICreateYourEventService } from "app/entities/create-your-event-service/create-your-event-service.model";
import { CreateYourEventServiceService } from "app/entities/create-your-event-service/service/create-your-event-service.service";
import { EventStatus } from "app/entities/enumerations/event-status.model";
import { IEventServiceMapOrder } from "app/entities/event-service-map-order/event-service-map-order.model";
import { EventServiceMapOrderService } from "app/entities/event-service-map-order/service/event-service-map-order.service";
import { EventService } from "app/entities/event/service/event.service";
import { IServiceMap } from "app/entities/service-map/service-map.model";
import { ServiceMapService } from "app/entities/service-map/service/service-map.service";
import { IUser } from "app/entities/user/user.model";
import { GeneralService } from "app/general.service";
import * as dayjs from "dayjs";

@Component({
  selector: 'jhi-customer-booking',
  templateUrl: './customer-booking.component.html',
  styleUrls: ['customer-booking.component.scss']
})
export class CustomerBookingComponent implements OnInit {
  serviceMapOrders!: IEventServiceMapOrder[];
  user!: IUser;

  services!: ICreateYourEventService[];
  service!: ICreateYourEventService;
  serviceId!: number;

  serviceMaps!: IServiceMap[];
  serviceMap!: IServiceMap;
  serviceMapId: number;

  serviceMapsOrdersProvisionally: IEventServiceMapOrder[] = [];
  serviceMapsOrdersDefinitely: IEventServiceMapOrder[] = [];

  clickSeen = false;

  constructor(
    private generalService: GeneralService,
    private sharedChatService: SharedChatService,
    private route: ActivatedRoute,
    private createYourEventServiceService: CreateYourEventServiceService,
    private eventServiceMapOrderService: EventServiceMapOrderService,
    private eventService: EventService,
    private ref: ChangeDetectorRef
  ) {}

  ngOnInit(): void {
    this.serviceId = Number(this.route.snapshot.paramMap.get('serviceId'));
    this.serviceMapId = Number(this.route.snapshot.paramMap.get('serviceMapId'));
    this.createYourEventServiceService.find(this.serviceId).subscribe(ser => {
      this.service = ser.body!;

      this.generalService.findByCreateYourEventServiceId(this.serviceId).subscribe(smaps => {
        this.serviceMaps = smaps.body!;
        if(this.serviceMapId) {
          const id = this.serviceMapId;
          this.serviceMapsOrdersProvisionally = [];
          this.serviceMapsOrdersDefinitely = [];
          this.generalService.getAllEventServiceMapsOrdersByServiceMapId(id).subscribe(smos => {
            smos.body!.forEach(smo => {
              this.eventService.find(smo.event.id).subscribe(e => {
                smo.event = e.body;
                if (dayjs(smo.event.dateStart).isAfter(dayjs()) && smo.event!.status === EventStatus.PROCESSING) {
                  this.generalService.findEventById(smo.event.id).subscribe(r => {
                    smo.event = r.body;
                    this.serviceMapsOrdersProvisionally.push(smo);
                  });
                } else if (
                  dayjs(smo.event.dateStart).isAfter(dayjs()) &&
                  smo.event.status === EventStatus.DEFINITELY &&
                  smo.event.definitelyConfirmed === true
                ) {
                  this.eventService.find(smo.event.id).subscribe(r => {
                    smo.event = r.body;
                    this.serviceMapsOrdersDefinitely.push(smo);
                  });
                }
              });
            });
          });
        }
      });
    });
  }

  previousState(): void {
    window.history.back();
  }

  selectServiceMap(event: any): void {
    const id = event.value;
    this.serviceMapsOrdersProvisionally = [];
    this.serviceMapsOrdersDefinitely = [];
    this.generalService.getAllEventServiceMapsOrdersByServiceMapId(id).subscribe(smos => {
      smos.body!.forEach(smo => {
        this.eventService.find(smo.event.id).subscribe(e => {
          smo.event = e.body;
          if (dayjs(smo.event.dateStart).isAfter(dayjs()) && smo.event!.status === EventStatus.PROCESSING) {
            this.generalService.findEventById(smo.event.id).subscribe(r => {
              smo.event = r.body;
              this.serviceMapsOrdersProvisionally.push(smo);
            });
          } else if (
            dayjs(smo.event.dateStart).isAfter(dayjs()) &&
            smo.event.status === EventStatus.DEFINITELY &&
            smo.event.definitelyConfirmed === true
          ) {
            this.eventService.find(smo.event.id).subscribe(r => {
              smo.event = r.body;
              this.serviceMapsOrdersDefinitely.push(smo);
            });
          }
        });
      });
    });
  }

  hasService(): boolean {
    return this.service === null;
  }

  clickUserName(user: IUser): void {
    this.sharedChatService.callClickName(user);
  }

  seenChange(event: any, id: number): void {
    this.clickSeen = true;
    this.eventServiceMapOrderService.find(id).subscribe(smo => {
      const eventProductOrder = smo.body!;
      eventProductOrder.seen = true;

      this.eventServiceMapOrderService.update(eventProductOrder).subscribe(() => {
        this.clickSeen = false;
      });
    });
  }

  approvedChange(event: any, id: number): void {
    this.eventServiceMapOrderService.find(id).subscribe(smo => {
      const eventProductOrder = smo.body!;
      eventProductOrder.approved = true;

      this.eventServiceMapOrderService.update(eventProductOrder).subscribe();
    });
  }
}
<div class="container-fluid">
    <div id="row">
        <div class="col-md-12">
            <label for="servicemaps" jhiTranslate="customer-booking.select-servicemap"></label>
            <p-dropdown  id="servicemaps" name="serviceMap" [options]="serviceMaps" [(ngModel)]="serviceMapId" optionLabel="title" optionValue="id" (onChange)="selectServiceMap($event)" placeholder="{{ 'customer-booking.select-servicemap' | translate }}"></p-dropdown>
        </div>
    </div>
    <hr/>

    <p-tabView>
        <p-tabPanel header="{{ 'customer-booking.provisionally' | translate }}">
            <div class="row">
                <div class="col-md-12">
                    <div *ngFor="let order of serviceMapsOrdersProvisionally">
                        <p-card class="cards">
                            <p-header>
                                <h3>{{ order.serviceMap!.title }}</h3>
                            </p-header>
                            <table>
                                <tr class="tr-border">
                                    <td><span jhiTranslate="customer-booking.date-from" class="bold">Date from</span></td><td>{{ order.dateFrom!.toString() | date: 'medium'}}</td>
                                </tr>
                                <tr class="tr-border">
                                    <td><span jhiTranslate="customer-booking.date-until" class="bold">Date until</span></td><td>{{ order.dateUntil!.toString()  | date: 'medium' }}</td>
                                </tr>
                                <tr class="tr-border">
                                    <td><span jhiTranslate="customer-booking.cost-hour" class="bold">Cost hour</span></td><td>{{ order.costHour | currency }}</td>
                                </tr>
                                <tr class="tr-border">
                                    <td><span jhiTranslate="customer-booking.total-hours" class="bold">Total hours</span></td><td>{{ order.totalHours }}</td>
                                </tr>
                                <tr class="tr-border">
                                    <td><span jhiTranslate="customer-booking.ride-costs" class="bold">Ride costs</span></td><td>{{ order.rideCosts | currency }}</td>
                                </tr>
                                <tr class="tr-border">
                                    <td><span jhiTranslate="customer-booking.kilometre" class="bold">Kilometre</span></td><td>{{ order.kilometre }} km</td>
                                </tr>
                                <tr class="tr-border">
                                    <td><span jhiTranslate="customer-booking.total" class="bold">Total</span></td><td>{{ order.total | currency }}</td>
                                </tr>
                            </table>
                            <hr/>
                            <dl>
                                <dt><span jhiTranslate="customer-booking.event" class="bold">Event</span></dt>
                                <dd><a href="/events/{{ order.event.id }}/view">{{ order.event.name }}</a></dd>

                                <dt><span jhiTranslate="customer-booking.organisator" class="bold">Organisator</span></dt>
                                <dd><a (click)="clickUserName(order.event.user)">{{ order.event.user.email }}</a></dd>
                            </dl>
                            <p-footer>
                                <div class="border-box">
                                    <span jhiTranslate="orders.seen">Seen</span>
                                    <p-toggleButton (onChange)="seenChange($event, order.id!)" [(ngModel)]="order.seen" [disabled]="order.seen" onIcon="pi pi-check" offIcon="pi pi-times"></p-toggleButton>
                                </div>
                                <div class="border-box">
                                    <span jhiTranslate="orders.approved">Approved</span>
                                    <p-toggleButton (onChange)="approvedChange($event, order.id!)" [(ngModel)]="order.approved" [disabled]="order.approved || !order.seen || clickSeen" onIcon="pi pi-check" offIcon="pi pi-times"></p-toggleButton>
                                </div>
                            </p-footer>
                        </p-card>
                    </div>
                    <div class="clear"></div>
              </div>
            </div>
        </p-tabPanel>
        <p-tabPanel header="{{ 'customer-booking.definitely' | translate }}">
            <div class="row">
                <div class="col-md-12">
                    <div *ngFor="let order of serviceMapsOrdersDefinitely">
                        <p-card class="cards">
                            <p-header>
                                <h3>{{ order.serviceMap!.title }}</h3>
                            </p-header>
                            <table>
                                <tr class="tr-border">
                                    <td><span jhiTranslate="customer-booking.date-from" class="bold">Date from</span></td><td>{{ order.dateFrom!.toString() | date: 'medium'}}</td>
                                </tr>
                                <tr class="tr-border">
                                    <td><span jhiTranslate="customer-booking.date-until" class="bold">Date until</span></td><td>{{ order.dateUntil!.toString()  | date: 'medium' }}</td>
                                </tr>
                                <tr class="tr-border">
                                    <td><span jhiTranslate="customer-booking.cost-hour" class="bold">Cost hour</span></td><td>{{ order.costHour | currency }}</td>
                                </tr>
                                <tr class="tr-border">
                                    <td><span jhiTranslate="customer-booking.total-hours" class="bold">Total hours</span></td><td>{{ order.totalHours }}</td>
                                </tr>
                                <tr class="tr-border">
                                    <td><span jhiTranslate="customer-booking.ride-costs" class="bold">Ride costs</span></td><td>{{ order.rideCosts | currency }}</td>
                                </tr>
                                <tr class="tr-border">
                                    <td><span jhiTranslate="customer-booking.kilometre" class="bold">Kilometre</span></td><td>{{ order.kilometre }} km</td>
                                </tr>
                                <tr class="tr-border">
                                    <td><span jhiTranslate="customer-booking.total" class="bold">Total</span></td><td>{{ order.total | currency }}</td>
                                </tr>
                            </table>
                            <hr/>
                            <dl>
                                <dt><span jhiTranslate="customer-booking.event" class="bold">Event</span></dt>
                                <dd><a href="/events/{{ order.event.id }}/view">{{ order.event!.name }}</a></dd>

                                <dt><span jhiTranslate="customer-booking.organisator" class="bold">Organisator</span></dt>
                                <dd><a (click)="clickUserName(order.event!.user!)">{{ order.event!.user!.email }}</a></dd>
                            </dl>
                            <p-footer>
                            </p-footer>
                        </p-card>
                    </div>
                    <div class="clear"></div>
              </div>
            </div>
        </p-tabPanel>
    </p-tabView>

    <hr/>
    <div class="btn-cancel">
        <button type="button" id="cancel-save" class="btn btn-secondary btn-cancel" (click)="previousState()">
          <fa-icon icon="ban"></fa-icon>&nbsp;<span jhiTranslate="entity.action.cancel">Cancel</span>
      </button>
    </div>

</div>
