                severity: 'error',
                summary: this.translate.instant('select-products.error'),
                detail: this.translate.instant('select-products.error-date')
              });
              this.displayDialog = false;
              return;
            }
            let arr1 = [];
            let arr2 = [];
            let arr3 = [];
            let arr4 = [];
