}

calcRevenue(): number {
  this.revenue = Number(this.jhiEvent.price) * Number(this.jhiEvent.minPlacenumber);
  return this.revenue;
}

calcProducts(): number {
  let totalProducts = 0;
  this.eventProductOrders.forEach(element => {
    totalProducts += element.total!;
  });
  this.totalProducts = totalProducts;
  return this.totalProducts;
}

