import { Component, OnInit, ElementRef, OnDestroy } from '@angular/core';
// eslint-disable-next-line @typescript-eslint/no-unused-vars
import { UntypedFormBuilder } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { JhiDataUtils, JhiFileLoadError, JhiEventManager, JhiEventWithContent } from 'ng-jhipster';

import { AlertError } from 'app/shared/alert/alert-error.model';
import { SharedEventService } from './shared-event.service';
import { ILocation } from 'app/entities/location/location.model';
import { LocationService } from 'app/entities/location/service/location.service';
import { IProduct } from 'app/entities/product/product.model';
import { ProductService } from 'app/entities/product/service/product.service';
import { IUser } from 'app/entities/user/user.model';
import { UserService } from 'app/entities/user/user.service';
import { EventService } from 'app/views/event/event.service';
import { ValidateFileSizeService } from 'app/validators/ValidateFileSize.service';

@Component({
  selector: 'jhi-event-flyer',
  templateUrl: './flyer.component.html',
  styleUrls: ['./flyer.scss'],
})
export class FlyerComponent implements OnInit, OnDestroy {
  isSaving = false;
  locations: ILocation[] = [];
  users: IUser[] = [];
  products: IProduct[] = [];
  dateStartDp: any;
  dateEndDp: any;
  model: any;

  editForm = this.fb.group({
    flyer: [null, [this.validateFileSizeService.valFileSize(256000)]],
    flyerContentType: [],
    youtube: [],
  });

  constructor(
    protected dataUtils: JhiDataUtils,
    protected eventManager: JhiEventManager,
    protected eventService: EventService,
    protected locationService: LocationService,
    protected userService: UserService,
    protected productService: ProductService,
    protected elementRef: ElementRef,
    protected activatedRoute: ActivatedRoute,
    private fb: UntypedFormBuilder,
    private router: Router,
    protected sharedEventService: SharedEventService,
    private validateFileSizeService: ValidateFileSizeService
  ) {}

  ngOnInit(): void {
    this.model = this.sharedEventService.sharedEvent;
    this.updateForm();
  }

  ngOnDestroy(): void {}

  updateForm(): void {
    this.editForm.patchValue({
      flyer: this.sharedEventService.sharedFlyer,
      flyerContentType: this.sharedEventService.sharedFlyerContentType,
    });
  }

  byteSize(base64String: string): string {
    return this.dataUtils.byteSize(base64String);
  }

  openFile(contentType: string, base64String: string): void {
    this.dataUtils.openFile(contentType, base64String);
  }

  setFileData(event: Event, field: string, isImage: boolean): void {
    this.dataUtils.loadFileToForm(event, this.editForm, field, isImage).subscribe(null, (err: JhiFileLoadError) => {
      this.eventManager.broadcast(
        new JhiEventWithContent<AlertError>('createyoureventApp.error', { ...err, key: 'error.file.' + err.key })
      );
    });
  }

  clearInputImage(field: string, fieldContentType: string, idInput: string): void {
    this.editForm.patchValue({
      [field]: null,
      [fieldContentType]: null,
    });
    if (this.elementRef && idInput && this.elementRef.nativeElement.querySelector('#' + idInput)) {
      this.elementRef.nativeElement.querySelector('#' + idInput).value = null;
    }
  }

  previousState(): void {
    this.router.navigate(['/organisator/create-event']);
  }

  save(): void {
    const flyerContentType = this.editForm.get(['flyerContentType'])!.value;
    const flyer = this.editForm.get(['flyer'])!.value;
    const youtube = this.editForm.get(['youtube'])!.value;

    this.sharedEventService.sharedYoutube = youtube;
    this.sharedEventService.sharedFlyer = flyer;
    this.sharedEventService.sharedFlyerContentType = flyerContentType;

    if (this.model.price === 0 && this.model.investment === 0) {
      this.router.navigate(['/organisator/create-event/overview']);
    } else {
      this.router.navigate(['/organisator/create-event/products-services']);
    }
  }
}
<div class="row justify-content-center">
    <div class="col-8">

            <h2 id="jhi-event-heading" jhiTranslate="createyoureventApp.event.home.createOrEditLabel">Create or edit a Event</h2>

            <div>
                <jhi-alert-error></jhi-alert-error>
                <form name="editForm" role="form" novalidate  [formGroup]="editForm">
                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.event.flyer" for="field_flyer">Flyer</label>
                    <div>
                        <img [src]="'data:' + editForm.get('flyerContentType')!.value + ';base64,' + editForm.get('flyer')!.value" style="max-height: 500px;" *ngIf="editForm.get('flyer')!.value" alt="event image"/>
                        <div *ngIf="editForm.get('flyer')!.value" class="form-text text-danger clearfix">
                            <span class="pull-left">{{ editForm.get('flyerContentType')!.value }}, {{ byteSize(editForm.get('flyer')!.value) }}</span>
                            <button type="button" (click)="clearInputImage('flyer', 'flyerContentType', 'file_flyer')" class="btn btn-secondary btn-xs pull-right">
                                <fa-icon icon="times"></fa-icon>
                            </button>
                        </div>
                        <input type="file" id="file_flyer" (change)="setFileData($event, 'flyer', true)" accept="image/*" jhiTranslate="entity.action.addimage"/>
                    </div>
                    <input type="hidden" class="form-control" name="flyer" id="field_flyer"
                           formControlName="flyer"/>
                    <input type="hidden" class="form-control" name="flyerContentType" id="field_flyerContentType"
                           formControlName="flyerContentType" />
                    <div *ngIf="editForm.get('flyer')!.invalid">
                        <small class="form-text text-danger"
                            *ngIf="editForm.get('flyer')?.errors?.maxFileSize" jhiTranslate="messages.fileSize">
                            This file is too big.
                        </small>
                    </div>
                </div>

                <div>
                    <span jhiTranslate="flyer.youtube.info">Add VideoId from URL from Youtube:</span>
                    <div class="form-group">
                        <label class="form-control-label" jhiTranslate="createyoureventApp.event.youtube" for="field_youtube">Youtube</label>
                        <input type="text" class="form-control" name="youtube" id="field_youtube"
                               formControlName="youtube"/>
                    </div>
                </div>

            <div>
                <button type="button" id="cancel-save" class="btn btn-secondary" (click)="previousState()">
                    <fa-icon icon="ban"></fa-icon>&nbsp;<span jhiTranslate="entity.action.cancel">Cancel</span>
                </button>

                <button type="button" id="save-entity" (click)="save()" [disabled]="editForm.invalid || isSaving" class="btn btn-primary">
                    <fa-icon icon="save"></fa-icon>&nbsp;<span jhiTranslate="entity.action.save">Save</span>
                </button>
            </div>
        </form>
    </div>
</div>
