                <fa-icon icon="ban"></fa-icon>&nbsp;<span jhiTranslate="entity.action.cancel">Cancel</span>
            </button>

            <button type="submit" id="save-entity" [disabled]="contactForm.invalid || isSaving" class="btn btn-primary">
                <fa-icon icon="save"></fa-icon>&nbsp;<span jhiTranslate="entity.action.save">Save</span>
            </button>
         x</div>
        </div>
        </form>
    </mat-tab>
    <mat-tab label="Map">
        <p-gmap [options]="options" [style]="{'width':'100%','height':'320px'}" [overlays]="overlays"></p-gmap>
    </mat-tab>
  </mat-tab-group>
