import { HttpResponse } from '@angular/common/http';
import { Component, OnInit, ElementRef, ChangeDetectorRef } from '@angular/core';
import { Validators, UntypedFormBuilder } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { TranslateService } from '@ngx-translate/core';
import { ADDRESS_REGEX } from 'app/constants';
import { ICreateYourEventService, CreateYourEventService } from 'app/entities/create-your-event-service/create-your-event-service.model';
import { CreateYourEventServiceService } from 'app/entities/create-your-event-service/service/create-your-event-service.service';
import { TagsService } from 'app/entities/tags/service/tags.service';
import { Tags } from 'app/entities/tags/tags.model';
import { UserPointAssociationService } from 'app/entities/user-point-association/service/user-point-association.service';
import { UserPointAssociation } from 'app/entities/user-point-association/user-point-association.model';
import { IUser } from 'app/entities/user/user.model';
import { GeneralService } from 'app/general.service';
import { PointsDataService } from 'app/points/points-display/points-display.service';
import { AlertError } from 'app/shared/alert/alert-error.model';
import { ValidateFileSizeService } from 'app/validators/ValidateFileSize.service';
import { ValidatorHttp } from 'app/validators/ValidatorHttp.service';
import * as dayjs from 'dayjs';
import { JhiDataUtils, JhiEventManager, JhiFileLoadError, JhiEventWithContent } from 'ng-jhipster';
import { MessageService } from 'primeng/api';
import { Observable } from 'rxjs';

@Component({
  selector: 'jhi-create-service',
  templateUrl: './create-service.component.html',
  styleUrls: ['create-service.component.scss'],
  providers: [MessageService],
})
export class CreateServiceComponent implements OnInit {
  error = false;
  success = false;

  isSaving = false;
  formattedaddress = '';
  usr: IUser;

  quillEditorRef: any;
  maxUploadFileSize = 500000;

  editForm = this.fb.group({
    id: [],
    name: [null, [Validators.required]],
    logo: [null, [this.validateFileSizeService.valFileSize(256000)]],
    logoContentType: [],
    active: [],
    description: [null, [Validators.required]],
    address: [null, [Validators.required, Validators.pattern(ADDRESS_REGEX)]],
    motto: [null, [Validators.required]],
    phone: [null, [Validators.required]],
    webAddress: [null, [this.validatorHttp.checkHttp()]],
    category: [null, [Validators.required]],
    user: [],
  });

  items: any[] = [];

  modules = {};

  constructor(
    protected dataUtils: JhiDataUtils,
    protected eventManager: JhiEventManager,
    protected createYourEventServiceService: CreateYourEventServiceService,
    protected elementRef: ElementRef,
    protected activatedRoute: ActivatedRoute,
    private fb: UntypedFormBuilder,
    private generalService: GeneralService,
    private tagsService: TagsService,
    private ref: ChangeDetectorRef,
    private pointsDataService: PointsDataService,
    private userPointAssociationService: UserPointAssociationService,
    private validateFileSizeService: ValidateFileSizeService,
    private messageService: MessageService,
    private translate: TranslateService,
    private validatorHttp: ValidatorHttp
  ) {
    this.modules = {
      toolbar: [
        ['bold', 'italic', 'underline', 'strike'], // toggled buttons
        ['blockquote', 'code-block'],

        [{ header: 1 }, { header: 2 }], // custom button values
        [{ list: 'ordered' }, { list: 'bullet' }],
        [{ script: 'sub' }, { script: 'super' }], // superscript/subscript
        [{ indent: '-1' }, { indent: '+1' }], // outdent/indent
        [{ direction: 'rtl' }], // text direction

        [{ size: ['small', false, 'large', 'huge'] }], // custom dropdown
        [{ header: [2, 3, 4, 5, 6, false] }],

        [{ color: [] }, { background: [] }], // dropdown with defaults from theme
        [{ font: [] }],
        [{ align: [] }],

        ['clean'], // remove formatting button

        ['link', 'image', 'video'], // link and image, video
      ],
    };
  }

  ngOnInit(): void {
    this.generalService.findWidthAuthorities().subscribe(u => {
      this.usr = u.body;
    });
  }

  getEditorInstance(editorInstance: any): void {
    this.quillEditorRef = editorInstance;
    const toolbar = editorInstance.getModule('toolbar');
    toolbar.addHandler('image', this.imageHandler);
  }

  imageHandler = (image: any, callback: any) => {
    const input = <HTMLInputElement>document.getElementById('fileInputField');
    document.getElementById('fileInputField').onchange = () => {
      const file: File = input.files[0];
      // file type is only image.
      if (/^image\//.test(file.type)) {
        if (file.size > this.maxUploadFileSize) {
          this.messageService.add({
            severity: 'error',
            summary: this.translate.instant('register-shop.filesize.error'),
            detail: this.translate.instant('register-shop.filesize.error.info'),
          });
        } else {
          const reader = new FileReader();
          reader.onload = () => {
            const range = this.quillEditorRef.getSelection();
            const img = '<img src="' + reader.result + '" />';
            this.quillEditorRef.clipboard.dangerouslyPasteHTML(range.index, img);
          };
          reader.readAsDataURL(file);
        }
      } else {
        this.messageService.add({
          severity: 'error',
          summary: this.translate.instant('register-shop.filetype.error'),
          detail: this.translate.instant('register-shop.filetype.error.info'),
        });
      }
    };
    input.click();
  };

  byteSize(base64String: string): string {
    return this.dataUtils.byteSize(base64String);
  }

  openFile(contentType: string, base64String: string): void {
    this.dataUtils.openFile(contentType, base64String);
  }

  setFileData(event: Event, field: string, isImage: boolean): void {
    this.dataUtils.loadFileToForm(event, this.editForm, field, isImage).subscribe(null, (err: JhiFileLoadError) => {
      this.eventManager.broadcast(
        new JhiEventWithContent<AlertError>('createyoureventApp.error', { ...err, key: 'error.file.' + err.key })
      );
    });
  }

  clearInputImage(field: string, fieldContentType: string, idInput: string): void {
    this.editForm.patchValue({
      [field]: null,
      [fieldContentType]: null,
    });
    if (this.elementRef && idInput && this.elementRef.nativeElement.querySelector('#' + idInput)) {
      this.elementRef.nativeElement.querySelector('#' + idInput).value = null;
    }
  }

  previousState(): void {
    window.history.back();
  }

  /*

  save(): void {
    this.isSaving = true;
    const createYourEventService = this.createFromForm();
    if (createYourEventService.id !== undefined) {
      this.subscribeToSaveResponse(this.createYourEventServiceService.update(createYourEventService));
    } else {
      this.subscribeToSaveResponse(this.createYourEventServiceService.create(createYourEventService));
    }
  }
*/

  save(): void {
    this.isSaving = true;
    const createYourEventService = this.createFromForm();
    this.subscribeToSaveResponse(this.createYourEventServiceService.create(createYourEventService));
  }

  private createFromForm(): ICreateYourEventService {
    return {
      ...new CreateYourEventService(),
      id: this.editForm.get(['id'])!.value,
      name: this.editForm.get(['name'])!.value,
      logoContentType: this.editForm.get(['logoContentType'])!.value,
      logo: this.editForm.get(['logo'])!.value,
      active: this.editForm.get(['active'])!.value,
      description: this.editForm.get(['description'])!.value,
      address: this.editForm.get(['address'])!.value,
      motto: this.editForm.get(['motto'])!.value,
      phone: this.editForm.get(['phone'])!.value,
      webAddress: this.editForm.get(['webAddress'])!.value,
      category: this.editForm.get(['category'])!.value,
      user: this.usr,
    };
  }

  protected subscribeToSaveResponse(result: Observable<HttpResponse<ICreateYourEventService>>): void {
    result.subscribe(
      s => this.onSaveSuccess(s.body),
      () => this.onSaveError()
    );
  }

  protected onSaveSuccess(s: ICreateYourEventService): void {
    window.scrollTo(0, 0);
    this.generalService.findWidthAuthorities().subscribe(ub => {
      const user = ub.body;
      this.generalService.findPointsByKey('create_service').subscribe(p => {
        const points = p.body;
        this.generalService.findUserPointAssociationByUsersIdAndPointkey(user.id, points.key).subscribe(si => {
          const upa = si.body;
          const day = dayjs().date();
          let i = 0;
          upa.forEach(element => {
            if (dayjs(element.date).date() === day) {
              i++;
            }
          });
          if (i < points.countPerDay) {
            const iupa = new UserPointAssociation();
            iupa.users = user;
            iupa.points = points;
            iupa.date = dayjs();
            this.userPointAssociationService.create(iupa).subscribe();
            user.points += points.points;
            user.loggedIn = true;
            this.generalService.updateUserLoggedInAndPoints(user.id, user.loggedIn, user.points).subscribe(t => {
              this.generalService.findWidthAuthorities().subscribe(k => {
                this.pointsDataService.changePoint(k.body.points);
              });
            });
          }
        });
      });
    });

    this.items.forEach(item => {
      const tag = new Tags();
      tag.tag = item.value;
      tag.service = s;
      this.tagsService.create(tag).subscribe(() => {
        this.isSaving = false;
        this.success = true;
        window.scrollTo(0, 0);
      });
    });
  }

  protected onSaveError(): void {
    this.isSaving = false;
    this.error = true;
  }

  public addressChange(address: any): void {
    this.formattedaddress = address.formatted_address;
    this.editForm.patchValue({ address: address.formatted_address });
  }
}
<p-toast></p-toast>
<ng-container *ngIf="success || error">
    <div class="row justify-content-center">
        <div class="col-md-8">
            <h1 jhiTranslate="create-service.title">Registration service</h1>

            <div class="alert alert-success" *ngIf="success" jhiTranslate="create-service.messages.success">
                <strong>Registration service saved!</strong> After successfully checking your details, we will activate the service.
            </div>

            <div class="alert alert-danger" *ngIf="error" jhiTranslate="create-service.messages.error.fail">
                <strong>Registration failed!</strong> Please try again later.
            </div>
        </div>
    </div>
</ng-container>


<ng-container *ngIf="!success && !error">
<div class="row justify-content-center">
    <div class="col-8">
        <form name="editForm" role="form" novalidate (ngSubmit)="save()" [formGroup]="editForm">
            <h2 id="jhi-create-your-event-service-heading" jhiTranslate="createyoureventApp.createYourEventService.home.createOrEditLabel">Create or edit a Create Your Event Service</h2>

            <div>
                <jhi-alert-error></jhi-alert-error>

                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.createYourEventService.name" for="field_name">Name</label>
                    <input type="text" class="form-control" name="name" id="field_name"
                           formControlName="name"/>
                    <div *ngIf="editForm.get('name')!.invalid && (editForm.get('name')!.dirty || editForm.get('name')!.touched)">
                        <small class="form-text text-danger"
                               *ngIf="editForm.get('name')?.errors?.required" jhiTranslate="entity.validation.required">
                        This field is required.
                        </small>
                    </div>
                </div>

                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.createYourEventService.category" for="field_category">Category</label>
                    <select class="form-control" name="category" formControlName="category" id="field_category">
                        <option value="SECURITAS">{{ 'createyoureventApp.ServiceCategory.SECURITAS' | translate }}</option>
                        <option value="SHUTTLESERVICE">{{ 'createyoureventApp.ServiceCategory.SHUTTLESERVICE' | translate }}</option>
                        <option value="SANITARY">{{ 'createyoureventApp.ServiceCategory.SANITARY' | translate }}</option>
                        <option value="CLEANINGSERVICE">{{ 'createyoureventApp.ServiceCategory.CLEANINGSERVICE' | translate }}</option>
                        <option value="PLUMBER">{{ 'createyoureventApp.ServiceCategory.PLUMBER' | translate }}</option>
                        <option value="DISCJOKEY">{{ 'createyoureventApp.ServiceCategory.DISCJOKEY' | translate }}</option>
                        <option value="BAND">{{ 'createyoureventApp.ServiceCategory.BAND' | translate }}</option>
                        <option value="ELECTRONICSTECHNICIAN">{{ 'createyoureventApp.ServiceCategory.ELECTRONICSTECHNICIAN' | translate }}</option>
                        <option value="COMPUTERSCIENTIST">{{ 'createyoureventApp.ServiceCategory.COMPUTERSCIENTIST' | translate }}</option>
                        <option value="MISCELLANEOUS">{{ 'createyoureventApp.ServiceCategory.MISCELLANEOUS' | translate }}</option>
                    </select>
                    <div *ngIf="editForm.get('category')!.invalid && (editForm.get('category')!.dirty || editForm.get('category')!.touched)">
                        <small class="form-text text-danger"
                               *ngIf="editForm.get('category')?.errors?.required" jhiTranslate="entity.validation.required">
                        This field is required.
                        </small>
                    </div>
                </div>

                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.createYourEventService.logo" for="field_logo">Logo</label>
                    <div>
                        <img [src]="'data:' + editForm.get('logoContentType')!.value + ';base64,' + editForm.get('logo')!.value" style="max-height: 100px;" *ngIf="editForm.get('logo')!.value" alt="createYourEventService image"/>
                        <div *ngIf="editForm.get('logo')!.value" class="form-text text-danger clearfix">
                            <span class="pull-left">{{ editForm.get('logoContentType')!.value }}, {{ byteSize(editForm.get('logo')!.value) }}</span>
                            <button type="button" (click)="clearInputImage('logo', 'logoContentType', 'file_logo')" class="btn btn-secondary btn-xs pull-right">
                                <fa-icon icon="times"></fa-icon>
                            </button>
                        </div>
                        <input type="file" id="file_logo" (change)="setFileData($event, 'logo', true)" accept="image/*" jhiTranslate="entity.action.addimage"/>
                    </div>
                    <input type="hidden" class="form-control" name="logo" id="field_logo"
                           formControlName="logo"/>
                    <input type="hidden" class="form-control" name="logoContentType" id="field_logoContentType"
                           formControlName="logoContentType" />
                    <div *ngIf="editForm.get('logo')!.invalid">
                        <small class="form-text text-danger"
                            *ngIf="editForm.get('logo')?.errors?.maxFileSize" jhiTranslate="messages.fileSize">
                            This file is too big.
                        </small>
                    </div>
                </div>

                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.createYourEventService.description" for="field_description">Description</label>
                    <quill-editor [styles]="{height: '350px'}" formControlName="description" [modules]="modules"  (onEditorCreated)="getEditorInstance($event)">
                        <quill-view format="text" theme="snow">
                            <quill-view-html theme="snow"></quill-view-html>
                        </quill-view>
                    </quill-editor>
                    <input type="file" style="display: none" id="fileInputField"/>
                    <div *ngIf="editForm.get('description')!.invalid && (editForm.get('description')!.dirty || editForm.get('description')!.touched)">
                        <small class="form-text text-danger"
                               *ngIf="editForm.get('description')?.errors?.required" jhiTranslate="entity.validation.required">
                        This field is required.
                        </small>
                    </div>
                </div>

                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.createYourEventService.address" for="field_address">Address</label>
                    <input ngx-google-places-autocomplete  type="text" class="form-control" name="address" id="field_address"
                           formControlName="address" (onAddressChange)="addressChange($event)"/>
                    <div *ngIf="editForm.get('address')!.invalid && (editForm.get('address')!.dirty || editForm.get('address')!.touched)">
                        <small class="form-text text-danger"
                               *ngIf="editForm.get('address')?.errors?.required" jhiTranslate="entity.validation.required">
                        This field is required.
                        </small>
                        <small class="form-text text-danger"
                                *ngIf="editForm.get('address')?.errors?.pattern" jhiTranslate="settings.messages.validate.address.pattern">
                        This adress-form iw wrong.
                        </small>
                    </div>
                </div>

                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.createYourEventService.motto" for="field_motto">Motto</label>
                    <input type="text" class="form-control" name="motto" id="field_motto"
                           formControlName="motto"/>
                    <div *ngIf="editForm.get('motto')!.invalid && (editForm.get('motto')!.dirty || editForm.get('motto')!.touched)">
                        <small class="form-text text-danger"
                               *ngIf="editForm.get('motto')?.errors?.required" jhiTranslate="entity.validation.required">
                        This field is required.
                        </small>
                    </div>
                </div>

                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.product.keywords" for="field_keywords">Keywords</label>
                    <tag-input [(ngModel)]='items' [maxItems]='4' [ngModelOptions]="{standalone: true}"></tag-input>
                </div>

                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.createYourEventService.phone" for="field_phone">Phone</label>
                    <input type="text" class="form-control" name="phone" id="field_phone"
                           formControlName="phone"/>
                    <div *ngIf="editForm.get('phone')!.invalid && (editForm.get('phone')!.dirty || editForm.get('phone')!.touched)">
                        <small class="form-text text-danger"
                               *ngIf="editForm.get('phone')?.errors?.required" jhiTranslate="entity.validation.required">
                        This field is required.
                        </small>
                    </div>
                </div>

                <div class="form-group">
                    <label class="form-control-label" jhiTranslate="createyoureventApp.createYourEventService.webAddress" for="field_webAddress">Web Address</label>
                    <input type="text" class="form-control" name="webAddress" id="field_webAddress"
                           formControlName="webAddress"/>
                    <div *ngIf="editForm.get('webAddress')!.invalid && (editForm.get('webAddress')!.dirty || editForm.get('webAddress')!.touched)">
                      <small class="form-text text-danger"
                              *ngIf="editForm.get('webAddress')?.errors?.startsWithHttp" jhiTranslate="messages.startsWithHttp">
                      The webaddress must starts with either "http://" or "https://"
                      </small>
                </div>
                </div>
            </div>

            <div>
                <button type="button" id="cancel-save" class="btn btn-secondary" (click)="previousState()">
                    <fa-icon icon="ban"></fa-icon>&nbsp;<span jhiTranslate="entity.action.cancel">Cancel</span>
                </button>

                <button type="submit" id="save-entity" [disabled]="editForm.invalid || isSaving" class="btn btn-primary">
                    <fa-icon icon="save"></fa-icon>&nbsp;<span jhiTranslate="entity.action.save">Save</span>
                </button>
            </div>
        </form>
    </div>
</div>
</ng-container>
