        const tag = new Tags();
        tag.tag = item.value;
        tag.shop = s;
        this.tagsService.create(tag).subscribe(() => {
          this.isSaving = false;
        });
      });
      this.isSaving = false;
      this.success = true;
      window.scrollTo(0, 0);
    });
  }
